css:
@-moz-document url-prefix("http://www.tsdm.net"), url-prefix("http://www.tsdm.me"), url-prefix("http://www.tsdm.tw") {
/*本CSS规则基于   【论坛风格】五周年（简洁）  制作且仅此适用*/

/*背景图设置*/
#tsdmlt {
	background-attachment: fixed!important;
	background-image: url(http://5.66825.com/download/pic/000/325/ccdf85da2620f551cc9cd10a0f0f655d.jpg) !important;
	background-repeat: no-repeat!important;
    background-size:cover!important;
	background-position: 0px 0px!important;
}

/*第三方登录标签*/
[class*="vm qq_bind"]{display:none !important;} 
[class*="pipe"]{display:none !important;} 
[style*="vertical-align: middle"]{display:none !important;}


/*清理广告推广，网页尾部声明*/
#tsdmadv_img{display:none !important;}
#ft{display:none !important;}
#tsdmWbFrame{display:none !important;} 
#previewmc{display:none !important;}

/*天使论坛微博改为悬浮，鼠标经过时出现*/
#ts_sidebar_base { width: 510px !important;top:-120px !important;height:2000px !important; background:none !important; box-shadow:none !important; -webkit-box-shadow:none !important; }
#ts_slide_open{display:block !important;}
#ts_slide_close{display:none !important;} 
#inner_to_right{display:none !important;} 
#ts_weibo_frame{height:91% !important;}
#ts_sidebar_swt{position:relative !important;top:160px !important;left:0px !important;text-align:left !important;}
#ts_weibo_wrapper{left:30px !important;}
#ts_sidebar_base {right: -480px !important; min-height : 0 !important; overflow : hidden !important;}
#ts_sidebar_base:hover{right: -250px !important; }

/*全局透明，默认开启*/
#postlist{background:none!important;}
#f_pst{background:none!important;}
.pls{background:none!important;}
#threadlist{background:none!important;}
.bm{background:none!important;}
#Kahrpba_show{background:url(http://www.tsdmw.net/xz/static/image/Kahrpba/prefix.gif) no-repeat rgba(255, 255, 255, 0)!important;}
[class="t_f tspostm"]{background:none!important;}
.tsdm_norm_title{background:none!important;}
.quote{background:none!important;}
.pl .quote{background:rgba(255, 255, 255, 0.5)!important;}
.p_pop{background: rgba(255, 255, 255, 0.7)!important;}
.t_f{background: none!important;}
.sign_inner{background: none!important;}
.tedt .bar{background: none!important;}
.newthread tr th, .newthread tr td{background:rgba(255, 255, 255, 0.5)!important;}
.area{background-color: rgba(255,255,255,0.7)!important;}
#fastpostmessage{background: rgba(255,255,255,0)!important;}
#fwin_content_pay{background-color: rgba(255,255,255,0.6)!important;display: block!important;}
.m_c .o{background: rgba(255,255,255,0.6)!important;}
div#toptb>div.wp {position: fixed !important;background:rgba(0,0,0,0.5);}/*导航栏悬浮*/
}

@-moz-document url-prefix("http://www.tsdm.net/forum.php?mod=viewthread"), url-prefix("http://www.tsdm.me/forum.php?mod=viewthread"), url-prefix("http://www.tsdm.tw/forum.php?mod=viewthread") {
td{background: none!important;}
table{background: none!important;}
}
