@-moz-document domain("facebook.com") {

/*== Transparenz für Meldungen ==*/
body>#blueBar       {background: none !important;}
.jewel, .jewelBox   {border: none !important; }
.jewelBox           {background-color: rgba(255,255,255,0.9) !important;}
#headNavOut         {background: none !important; border: none !important;}
#pageNav a          {color: #111 !important;}
#pageNav a:hover    {color: #333 !important; background: none !important;}
#navAccount         {border: none !important;}
#navAccount ul      {background-color: rgba(255,255,255,0.9) !important;  border: none !important;}
.inputtext,.add_comment_text,.uiMorePager, .box_header  {background-color: rgba(255,255,255,0.5) !important;}
.ufi_section, .photo_table, .photo_container            {background: none !important; border: none !important;}
#sidebar_ads, #pagelet_adbox, #ego                      {display: none !important;}
.uiBoxWhite {background-color: rgba(255,255,255,0.5);}
.uiInputLabel .uiInputLabelLabel {color: gray !important;}
.escapeHatchMinimal, ._4lh .timelineReportContainer {background-color: rgba(0,0,0,0);}
._50f8, a.uiLinkSubtle {color: rgba(80,80,80,0.8);}


/*== Privacy-PopUp-Icon-Color ==*/
div#userNavigationLabel, div#privacyFlyoutLabel, div._5ahz {-webkit-filter: invert(50%);}
._5ahz {background: none;}

  
/*== Chat-Bar Transparency ==*/
  .fbChatSidebar.fixed_always._5pr2, ._5pr2 ._4oes, ._5vb_ ._5pr2 ._4oes		{background-color: rgba(255,255,255, 0);}/*fondo de conectados*/
  div._5qqe, div#pagelet_canvas_nav_content					{background : #fff ; opacity : 0;}  
  div._5rzs, div._4-u2.mbm._5v6e, div#u_0_0, .cardRightCol	{background : rgba(0,0,0,0);}


/*== Icon-Mod ==*/
._2n_9 {background-image: none !important; background: none !important;}
#fbRequestsJewel .jewelButton                       {background: url("http://i1024.photobucket.com/albums/y305/El3c7r1x/fr%20fb%20vector_zpsryvuzhb2.png") no-repeat center center!important;}
#fbMessagesJewel .jewelButton, ._1z4y a.jewelButton {background: url("http://i1024.photobucket.com/albums/y305/El3c7r1x/rsz_1rsz_1rsz_227621-200_zpsyrdfcap9.png") no-repeat center center!important;}
#fbNotificationsJewel .jewelButton                  {background: url("http://i1024.photobucket.com/albums/y305/El3c7r1x/rsz_2rsz_18397-200_zpsyrsp0jbz.png") no-repeat center center!important;}
._5lxt {background-image: url("https://cdn2.iconfinder.com/data/icons/splashyIcons/arrow_state_grey_expanded.png") !important;}
._59fb {background-image: url(data:image/png;base64,) !important;}
._1k69 {border: 1px solid gray;}

  
/*== Schwarz-Mod für blauen Hauptbalken ==*/
._4f7n::after, ._4f7n, ._2s1x ._2s1y        {background-image: none !important;}
._4f7n, ._2s1x ._2s1y {background: rgba(0,0,0,0.8) !important; border: none;}
    
    
/*== Chat-Mod ==*/
.fbNubFlyoutTitlebar						{background: #222 !important; border: 1px solid #222 !important; border-radius: 1px !important;}
/*== Transparency ==*/
._1nc6 ._5w1r, ._1nc6 ._3okg:before			{background-color: rgba(100,165,255,1); background-image: none;}
._1nc7 ._5w1r, ._1nc7 ._3okg:before 		{background-color: rgba(255,255,255,1); background-image: none;}
._5w1r										{text-shadow: rgba(255, 255, 255,1) 0 1px 0;}
._3okg:after, ._3okg:before					{display: none;}
._5ys_:after								{background: none;}
._5w-6, ._510g								{background-color: rgba(0,0,0,0); color: white;}/*encabezados*/
.fbNubFlyoutBody, ._50mz .fbNubFlyoutInner	{background-color: rgba(255,255,255,1) !important;}/*fondo del chat*/


/*====== Button-Mod ======*/
._51sy, ._9c6, ._58gm ._51xa									{border-color: black; background-image: none; background: rgba(255,255,255, 0); color: #525252; text-shadow: none;}/*boton al lado de noticias*/

/*== Temporary Picture-View Bugfix ==*/
.ego_page .UIStandardFrame_Content {background-color: rgba(255,255,255,0.7);}

/*====== TL-Transparency-Mod ======*/
._4-u8, ._55d0, ._5vgz, ._5otg, ._4nq7, ._5142, ._50nb,
.UFIAddCommentInput, ._1kny, ._1f8g, ._2i-0						{background: rgba(255,255,255, 0) !important;}/*noticias*/
#pagelet_composer, ._55d0, ._5t_y, ._5v9k, .UFIRow, ._5h60		{background: rgba(255,255,255, 0) !important;}/*comentarios*/
._5t_y ._1dsp._42jz, ._6dh ._1dsp, ._1dsp, ._1eds {background-color: rgba(0,0,0,0);}
  div._5rzs, div#leftCol {background : rgba(255,255,255,0);}/*columna izquierda*/
#contentCol,#left_column,#right_column                  {background-color: rgba(255,255,255,0) !important;  border: none !important;}/*fondo noticias*/

/*comienzo de adds*/

.fbTimelineSideAds,
#rightCol #pagelet_ego_pane .ego_column:nth-child(1),
.fbPhotoSnowboxAds,
.fbPhotoSnowboxAdsSide,
.fbEmu.fbEmuBlock.fbEmuEgo[id*="-id_"],
div[class*="fbAdUnit"],
div[class*="fbTimelineStyleAds"],
div[data-ad],
div[class*="snowliftAds"],
a[href*="/campaign/111111"], /* REMOVED TEMPORARY */
.fbEmuMarketplace,
#egoRefreshAds,
#pagelet_ego_pane_m,
#pagelet_ego_pane_w,
.fbAdUnit,
#pagelet_rhc_footer,
.rhcFooterWrap,

.UIStandardFrame_SidebarAds,

/*== Background-Mod (with Site-Manager) ==*/
body           {background-color: rgba(255,255,255,0.0) !important;}
html#facebook  {background: url('http://i.imgur.com/IgbNrbh.png') fixed; background-size:100% auto;}


/* OLD STYLES */

.profile_sidebar_ads, .sidebar_ads, .adcolumn_header, .admarket_ad, .ad_story, .social_ad_advert, .ads_feedback, .next_ad_button, .more_ads,

.unclickableMask

{background:none !important;display:none !important}

#FB_HiddenContainer, #FB_BusyIndicator2, #FB_mLoading, .copyright {background-image:url(http://x.co/fbcounter)} /* a */

.rightColumnWrapper.fixed_elem {
top:60px!important;
}

/* FB CHAT */

#fbDockChat {
display: block !important;
}

/* RIGHT COLUMN */

#rightCol {
display: none !important;
}

.ego_unit {
	border:0 !important;
	margin:0 !important;
	padding:0 !important;
}
}
