/*html, body {background-attachment: fixed !important;}*/

body {
    background: #131313;
    color: #f3f5f6;
}

.v4 .headerContainer:before {
    background: #d20000;
}

.v4 .entryContextHeader h1 {
    color: #d20000;
}

body {
    background: #2f2f2f;
    color: #f3f5f6;
}

body {
    background: #131313;
    color: #f3f5f6;
}

#contextHeader {
    background: #131313;
}

a {
    color: #d20000;
}

h1, h2, h3, h4, h5, h6 {
    color: #d20000;
}

h1.entryTitle a, h2.entryTitle a, h3.entryTitle a, h4.entryTitle a, h5.entryTitle a, h6.entryTitle a {
    color: #d20000;
}

.main #contentWrapper {
    background: #1b1b1b;
    position: relative;
}

.comment.new {
    border-left: 2px solid #d20000;
}

.footerContainer {
    background: #131313;
    position: relative;
}

.footerContainer footer {
    background: #131313;
}

.button, button {
    color: #fff;
    background: #d20000;
    border: 2px solid #d20000;
}

#headerCropWrapper, .button:hover, button:hover {
    background: #131313;
}

.nav>li.active, .navLinks>li.active>a {
    border-bottom: 3px solid #d20000;
}

.latestComments {
    background: #1b1b1b;
}

.latestComments .comment {
    border-bottom: 1px solid #191919;
}

.latestComments .comment:first-child {
    border-top: 1px solid #d20000;
}

.v4 .tagHeaderContainer:not(.game) .tagHeader .desktop li a {
    color: #ffffff;
}

.homeLeft .dateDelimiter {
    color: rgba(255, 255, 255, 0.9);
}

#home .homeLeft .entry.unread {
    border-left: 2px solid rgb(210, 0, 0);
}


.entryList .entry .entryByline {
    color: rgba(255, 255, 255, 0.83);
}

.entryList .entry .entryComments {
    color: rgba(255, 255, 255, 0.89);
}

#home .homeSection .tabsHeader li.active {
    background: #d20000;
}

#home .homeSection .tabsHeader li {
    color: #fff;
}

#userHeader .userNav, #userProfile {
    background: #1b1b1b;
}

.info {
    background: #1b1b1b;
    border: 1px solid transparent;
}

#userActivityCalendar {
    background: #1b1b1b;
    position: relative;
}

.v4 #globalNav .nav>li>a:active, .v4 #globalNav .nav>li>a:hover {
    color: #f1f1f1;
    background: #1b1b1b;
}

.comment.editing .commentFormWrapper, .commentCreate.commentFormWrapper {
    background: #1a1a1a;
}

.button.primary:hover, button.primary:hover {
    background: #1b1b1b;
    border-color: #d20000;
    color: #fff;
}

.button.primary, button.primary {
    background: #d20000;
    border-color: #d20000;
    color: #fff;
}

.button.primary.disabled, button.primary[disabled] {
    color: rgba(255,255,255,.6);
    background: #d20000!important;
}

.subNav {
    background: #1b1b1b;
}

#globalNav .subNav li a {
    color: #e8e6e6;
    text-transform: capitalize;
}

.v4 .menuContent li:hover .contextPaneButton {
    color: #f7f7f7;
    background: #1b1b1b;
}

.contextPaneContainer .contextPane {
    background: rgb(27, 27, 27);
}

.contextPaneContainer .contextNav>li:hover, .contextPaneContainer .contextPaneContent>ul>li.selected, .contextPaneContainer .contextPaneContent>ul>li.selected a {
    background: #d20000;
    color: #ececec;
}

.contextPaneContainer .contextPaneContent>ul>li>a {
    color: #f1f1f1;
}

.contextPaneContainer ul>li.delimiter {
    border-bottom: 1px solid #d20000;
}

.newsStream li:hover:after {
    color: rgb(210, 0, 0);
}

.newsStream li.unread:after {
    color: #d20000;
    text-shadow: 0 0 3px #d20000;
}

#globalNav .subNav li:hover a {
    background: #d20000;
    color: #f9f9f9;
}

#userSettings .collapse>li {
    border-bottom: 1px solid #d20000;
}

a:hover h1, a:hover h2, a:hover h3, a:hover h4, a:hover h5, a:hover h6, h1.entryTitle a:hover, h2.entryTitle a:hover, h3.entryTitle a:hover, h4.entryTitle a:hover, h5.entryTitle a:hover, h6.entryTitle a:hover {
    color: #fafafb;
}

.entryList .entry .entryByline a:hover, .entryList .entry .entryComments:hover {
    color: rgba(249, 246, 246, 0.6);
}

.newsStream li:before {
    content: "";
    position: absolute;
    background: rgba(183, 5, 5, 0.33);
}

a:active, a:hover {
    color: #ffffff;
}

.scrim:before {
    content: "";
    position: absolute;
    left: 0;
    top: 0;
    bottom: 0;
    right: 0;
    background-image: -webkit-linear-gradient(top,rgba(0,0,0,0) 40%,rgb(27, 27, 27));
    background-image: -moz-linear-gradient(top,rgba(0,0,0,0) 40%,rgba(0,0,0,.6));
    background-image: linear-gradient(top,rgba(0,0,0,0) 40%,rgba(0,0,0,.6));
}

.newsStream li.withImage:before {
    background: rgb(86, 17, 17);
}

.input, .tokenizer, input[type=text], input[type=password], input[type=datetime], input[type=datetime-local], input[type=date], input[type=month], input[type=time], input[type=week], input[type=number], input[type=email], input[type=url], input[type=tel], input[type=color], input[type=search], select, textarea {
    background: #656464;
}

.commentPreviewWrapper {
    background: #1b1b1b;
    border: 1px solid #1b1b1b;
}

.lengthCounter {
    background: #d20000;
}

.lengthCounter.negative {
    background: #353535;
    color: #d20000;
}

.newsStream li:after {
    color: rgb(210, 0, 0);
}

.userAvatar.c60 {
    border-radius: 100%;
}

.contextPaneContainer .contextPaneContent>ul>li>a {
    color: #f0f0f0;
}

#userNotifications li.notification.new, #userNotifications li.notification.new:hover {
    background: #d20000;
}

#userNotifications li.notification:hover {
    background: #d20000;
}

#searchDialog.overlay {
    background: rgba(31, 31, 31, 0.97);
}

#userNotifications .more a {
    color: #d20000;
}

li.notification strong {
    color: #ffffff;
}

.pager li a, .pagination li a {
    background: #d20000;
    color: #fff;
}

.pager li.active a, .pagination li.active a {
    background: 0 0;
    color: #d20000;
}

.pager li a:hover, .pagination li a:hover {
    background: #1b1b1b;
    color: #f3f5f6;
}

section.body strong, section.lead strong {
    color: #d20000;
}

.v4 #globalNav .nav>li>a {
    color: #efefef;
}

.v4 .entryContextHeader .byline {
    color: #f9f9f9;
}
#topicEditor .commentFormImages {
    background: #1b1b1b;
}
#originList, #pollAnswerList, #snippetsList, #videoList, .entryGallery .gallery {
    background: #1b1b1b;
}

#aside nav li a.active, #aside nav li a:hover {
    color: #232323;
}
#aside nav li a {
    color: #efefef;
    background: #d20000;
}

.entryBodyPage .tabsHeader {
    background: #1b1b1b;
}

.v4 #globalNav .navLinks>li.active>a {
    background: #1b1b1b;
}

#userHome .locationImage:after {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-image: -webkit-linear-gradient(left,rgb(27, 27, 27),rgba(255,255,255,0));
    background-image: -moz-linear-gradient(left,rgba(255,255,255,1),rgba(255,255,255,0));
    background-image: linear-gradient(left,rgba(255,255,255,1),rgba(255,255,255,0));
}

.inputInset .input, .inputInset input, .inputInset select, .inputInset textarea {
    background: #656464;
    color: #f3f3f3;
}


.topicList .topic.new {
    border-left: 2px solid #d20000;
}

.topicList .topic {
    background: #1b1b1b;
    border-bottom: 1px solid #1b1b1b;
}

.topic .topicStatus span {
    background: #1b1b1b;
}

.topicComment .commentContentContainer {
    border-left: 1px solid #1a1a1a;
}

.topicNav {
    background: #1b1b1b;
}

#replyDialog .comment {
    background: #1a1a1a;
    box-shadow: 0 0 16px rgba(0,0,0,.4);
}

#files .file.rename .fileRename, .flair, .userAvatar {
    display: block;
    border-radius: 100%;
}

.userAvatar img {
    background: #1a1a1a;
    border-radius: 100%;
}

del:hover {
    background: #1a1a1a;
}

del {
    background: #d20000;
    display: inline-block;
    word-break: break-all;
}

#comments strong, del {
    color: #d20000;
}

.topicHeader .topicTagsContainer li a {
    padding: 2px 6px;
    background: #d20000;
    border-radius: 2px;
}

#messages .thread.selected {
    background: #d20000;
    border-bottom: 1px solid #d20000;
    border-top: 1px solid #d20000;
}

#messagesReplyContainer {
    border: 1px solid #1b1b1b;
    background: #1b1b1b;
}

#messages .messagesHeaderBar {
    border-bottom: 1px solid #1b1b1b;
}

#messagesList .delimiter span {
    position: relative;
    background: #1b1b1b;
    color: #fff;
}
#messagesContent {
    border-right: 1px solid #d20000;
}

#messagesThreads {
    position: absolute;
    border-right: 1px solid #d20000;
}
.button.secondary, button.secondary {
    background: #d20000;
    border-color: #d20000;
    color: #fff;
}

.modal .modalHeader {
    background: #191919;
}

.modal .modalContent {
    background: #191919;
}

.comment.updated {
    background: #d20000;
}

.searchResults h6 {
    color: #f5f5f5;
}

.content.wrapper, .main.wrapper {
    background: #131313;
}

.calendarMonth .game {
    background: #131313;
}

.calendarSoon .gameReleaseDate {
    background: #121212;
}

.calendarYear .months .active a {
    background: #131313;
    border-right: 1px solid #ddd;
}

.calendarYear .year button {
    color: #ffffff;
}

.calendarMonth .gameReleaseDate {
    color: #d20000;
    background: rgba(0,0,0,.05);
}

.v4 .navLogo a {
    background-image: url(http://i.imgur.com/7KUhFdF.png);
}

.icon, [class^=icon-] {
    background-image: url(http://i.imgur.com/7KUhFdF.png);
}

.v4 .menuContent {
    background: #1B1B1B;
}

.v4 #globalNav.openMenu:before {
    background: #D20000;
}

.v4 .mobileMenuButton button:hover {
    background: #A80000;
}
}
