css:
@-moz-document url-prefix("https://ia.szu.edu.cn/cas/login") {
html {
    background: url('http://szdx.sinaapp.com/wechat/asset/ia_bg.jpg') no-repeat center center fixed; 
  	-webkit-background-size: cover;
  	-moz-background-size: cover;
  	-o-background-size: cover;
  	background-size: cover;
    height: 100%;
    width: 100%;
    overflow: hidden;
}

body {
    font: 14px/18px tahoma,arial,'Hiragino Sans GB','\5b8b\4f53',sans-serif !important;
}

#cas, .bg, .contant {
	background: none !important;
}

.top {
	background: none;
}

.top .container {
	display: none;
}

.top:after {
    margin: 60px auto;
    margin-bottom: 0;
	content: '';
    display: block;
    height: 60px;
    width: 435px;
    background-image: url("http://cdn.iszu.cn/auththeme/szuialogo@2x.png");
    background-size: 435px 60px;
}

.contant .container .left {
	display: none;
}

.contant .container .right {
    width: 360px;
	margin: 0 auto !important;
}

.contant .container .right .former {
    padding: 20px 10px 20px 10px;
	width: 100%;
    background: #FFF;
    border-radius: 6px;
    box-shadow: 0 0 5px rgba(0,0,0,.3);
    margin: 0;
}

.contant .container .right .former:after {
    position: absolute;
    left: 0;
    margin-top: 20px;
    width: 100%;
    content: 'Design By Jason.';
    font-size: 12px;
    line-height: 30px;
    text-align: center;
    font-weight: normal;
    color: rgba(255,255,255,.8);
    text-shadow: 0 0 5px rgba(0,0,0,.6);
    display: block;
}

.contant .container .right .former .form {
	background: none;
}

.contant .container .right .former .title {
	font-size: 20px;
    line-height: 40px;
    -webkit-user-select: none;
    cursor: normal;
}

.contant .container .right .former .lable {
	margin: 15px 30px;
    height: 36px;
}

.contant .container .right .former .lable .lable_left {
    border-radius: 3px 0 0 3px;
    height: 36px;
}

.contant .container .right .former .lable .lable_right .form-control {
    border-radius: 0 3px 3px 0;
    height: 36px;
    border-left: none;
}

.contant .container .right .former .lable .lable_right .form-control:focus {
    border-color: #3464AE;
    transition: all 300ms;
}

.contant .container .right .former .lable span {
    color: #666;
	font-size: 12px;
}

.contant .container .right .former .lable input[type="image"] {
	opacity: 0;
}

.contant .container .right .former .lable div[class=""]:after {
    position: relative;
    top: -35px;
    width: 100%;
    height: 40px;
	content: '登 录';
    color: #FFF;
    text-align: center;
    line-height: 40px;
    font-size: 16px;
    display: block;
    background: #3464AE;
    border-radius: 3px;
    pointer-events: none;
}

.contant .container .right .former .lable div[class=""]:hover:after {
    background: #2e5797;
}

.contant .container .right .former .lable .span_left {
    margin: 0;
    line-height: 36px;
}

.contant .container .right .former .i1,
.contant .container .right .former .i2 {
	display: none;
}

.col-xs-12, .col-md-12, .col-sm-12, .col-lg-6 {
	float: none;
}
}
