css:
@-moz-document domain("seasonvar.ru")
{

/* html body */
  html body
  {
    padding-top: 1px !important;
    background-color: #edeef0;
  }

/* body */
  body
  {
    padding-top: 1px !important;
  }

/* noindex */
  noindex,
.lside-player div div
  {
    display: none !important;
    position: absolute;
    left: -9999px;
  }

  noindex,
.td-for-left-block,
#___plusone_1td,
#player_msoc,
div [align="center"]
  {
    display: none !important;
    position: absolute;
    left: -9999px;
  }

  .pgs-player-inside,
.pgs-player-inside object
  {
    height: 754px;
  }

  .middle.left
  {
    border-left-width: 20px !important;
    padding-left: 20px !important;
  }

  .middle
  {
    background-color: #edeef0;
    box-shadow: 0 0 0 0;
  }

/* border */
  .news,
.rside-title,
.tabs-title,
.tabs-result
  {
    border-bottom: 0;
    border: 0;
  }

  .btn-abs,
.pgs-mark_line-set span,
.pgs-mark_line-switch.act .html,
.pgs-mark_line-switch .flash,
.rside-title li.act,
.rside-title li:hover,
.tabs-title li.act,
.tabs-title li:hover
  {
    background-image: linear-gradient(#f6bc23, #f6bc23);
    border-right: 0;
  }

  .head-btn .btn-abs.today
  {
    background-color: #f6bc23;
/* border-radius */;
  }

  .wrapper,
.tabs-title-fulwidth li:last-child,
.tabs-title.one li:last-child,
.tabs-title li.act,
.tabs-title li:hover,
.tabs-title,
.tabs-title-fulwidth li:last-child,
.tabs-title.one li:last-child,
.tabs-result,
.pgs-seaslist .tabs-result,
.content-wrap,
.pgs-mark_line-switch .flash,
.pgs-mark_line-switch .html,
.pgs-mark_line-set span,
.pgs-sinfo-img,
.pgs-sinfo-img img,
.headblock-search-txt,
.headblock,
.head-btn,
.btn,
.head-btn[data-menu]:after,
.head-btn[data-menu] img,
.rside-title li:first-child,
.pst,
.pst img,
.poster:after,
.poster:before,
.news-head,
.rside-title li:last-child,
.middle,
.news-head:before
  {
    border-radius: 0;
/* display: none  */;
  }

  .lside.sidebar,
.header_icon,
.header_txt,
.pgs-back,
.pgs-stags-wrap,
.pgs-sinfo-menu,
.one.tabs-title,
.uppage,
.pgs-sinfo-actor-title,
.tabs-close:nth-of-type(6) > .tabs-title,
.tabs-close:nth-of-type(7) > .tabs-close-btn,
.tabs-close:nth-of-type(7) > .one.tabs-title,
.pgs-afterplay.tabs-result,
.pgs-review.tabs-result,
.footer,
.act.tabs-close-btn,
.pgs-head,
.news-head:before,
.infoBox.svnotify
  {
    display: none !important;
/* box-shadow: 0 0 0 0  */;
  }

  .header,
.uppage,
.btn-abs,
.pgs-mark_line-set span,
.pst,
.content-wrap,
.pgs-seaslist .tabs-result,
.news-head,
.news-head:after
  {
    box-shadow: 0 0 0 0;
/* background: url  */;
  }

  .header_line:before,
.header_line,
.header_line:after
  {
    background: url(https://www.s.comm/);
  }

  .header_line
  {
    background-color: #edeef0;
  }
}
