css:
@-moz-document domain("enbdev.com") {
body {
    background: #151515;
}
div.content {
    background: #151515;
    border-color: #404040;
}
div.pcenter,
div.ppage {
    background: #151515;
}
p,
h4,
h2,
a.black {
    color: #777777;
}
a.black:visited {
    color: #634c77;
}
.pscreenshot img {
    border-color: #404040;
}
li {
    background-color: #232323;
    padding: 10px;
}
.menu_vertical a {
    background: #151515;
    border-left: 1px solid #404040;
    border-right: 1px solid #404040;
    color: #777777;
}
.menu_vertical a:visited {
    background: #151515;
    border-left: 1px solid #404040;
    border-right: 1px solid #404040;
    color: #5f5f5f;
}
.tftextinput2 {
    color: #777777;
    border-color: #404040;
    background: #202020;
    text-align: center;
}
#footer {
    background-color: #151515;
}
a.black {
    color: #5f5f5f;
}
a.black:hover {
    color: #777777;
}
li#taw0 {
    background: #333333;
}
input[type="image"] {
    margin-left: 16px;
    margin-top: 16px;
}
.menu_horizontal {
    display: none;
}
#preview-img {
    border-color: #404040;
}
.tfbutton2 {
    color: #000000;
    border: solid 1px #404040;
    border-left: 0px;
    background: #202020;
    height: 26px;
}
hr {
    border-color: #404040;
}
a:link {
    color: #96abce!important;
}
}

@-moz-document domain("enbdev.com") {
img {
    max-width: 100%!important;
}
body {
    color: #777777;
    margin: auto;
    max-width: 80%;
}
div#logodesc {
    display: none;
}
.row1 {
    background-color: #151515;
    border-color: #404040;
}
a:link,
.nav,
.gensmall,
.postdetails,
.postauthor {
    color: #777777;
}
a:hover,
a:visited {
    color: #7a5d90;
}
a.topictitle:visited {
    color: #444444;
}
.tablebg,
.spacer {
    background-color: #404040;
}
.cat {
    background-color: #122a40;
    background-image: none;
    border-bottom: 0px;
}
th {
    color: #777777;
    background-color: #122a40;
    background-image: none;
    border-top: 0px;
}
.row2 {
    background-color: #151515;
}
tr {
    background-color: #151515;
    color: #777777;
}
table {
    border-color: #404040!important;
}
.quotetitle,
.attachtitle {
    border-color: #404040!important;
    color: #fafafa!important;
    background-color: #202020!important;
}
.quotecontent,
.attachcontent {
    border-color: #404040!important;
    color: #fafafa!important;
    background-color: #000000!important;
}
.codetitle {
    border-color: #404040!important;
    color: #fafafa!important;
    background-color: #202020!important;
}
.codecontent {
    border-color: #404040!important;
    color: #006600!important;
    background-color: #000000!important;
}
div {
    border-color: #404040!important;
    background: #151514!important;
}
}
