css:
@-moz-document domain("vk.com") {
	/* Page Width */
	#page_header, #page_layout{
		width: /*[[width]]*/ !important;   
	}

	#footer_wrap{
		width: 100% !important;
	}

	#page_body{
		width: calc(100% - 170px) !important;
	}

	/* Chat Section */
	.im-chat-input .im-chat-input--textarea{
		width: calc(100% - 120px) !important;
	}
	
	/* User Profile Section */
	.page_module_upload{
		padding: 28px 13px 28px 40% !important;
	}

	/* Apps Section */
	.apps_recent_block{
		width: calc(100% - 365px) !important;
	}
	
	.apps_featured_slider{
		width: 100%;	
	}
	
	/* News Section */
	.wall_text{
		overflow: hidden;   
	}
	
	/* Video Section */
	.videocat_row_ugc_popular, .videocat_featured_playlists, .video_items_list {
		width: 100%;
	}
	
	.videocat_row{
		width: auto;										
	}
	
	.videocat_featured_playlists {
		overflow: hidden;						
	}
	
	.videocat_featured_playlist{
		width: calc(25% - 10px);
		margin: 5px !important;
	}
	
	.videocat_featured_video {
		margin: 11px 11px 0 0 !important;	
	}
	
	.videocat_channel_subscribe .videocat_subscribe_btn{
		margin: 0;								
	}
}
