@-moz-document regexp("https?://(www|encrypted).google(.\\w+)(.\\w+)?/((\\?|#|search|webhp).*)?") {
  
/*
// ==UserScript==
// @name Google - Поиск
// @namespace https://userstyles.org/users/273791
// @description Переносит настройки поиска в боковую панель.
// @author NewsIT
// @version 0.2
// @match google.com.ua
// @grant none
// ==/UserScript==
*/

#hdtbMenus {
background:transparent!important;
display:inline-block!important;
float:left!important;
height:0!important;
overflow:visible!important;
position:static!important;
}

#hdtbMenus .hdtb-mn-cont {
height:0!important;
}

#hdtbMenus .hdtb-mn-hd,#hdtbMenus .hdtbU {
display:block!important;
max-width:200px!important;
min-width:200px!important;
position:relative!important;
top:0!important;
white-space:normal!important;
width:200px!important;
}

#hdtbMenus .hdtbU {
background:transparent!important;
border:0!important;
box-shadow:none!important;
margin-bottom:10px!important;
}

#hdtbMenus span.tnv-lt-sm {
font-weight:normal!important;
height:auto!important;
overflow:visible!important;
white-space:nowrap!important;
}

#hdtbMenus .hdtb-mn-hd .mn-hd-txt {
color:#DD4B39!important;
white-space:normal!important;
}

#hdtbMenus .hdtb-mn-hd.hdtb-msel[aria-label*=" – "],
#hdtbMenus .hdtb-mn-hd.hdtb-msel[aria-label*=×] {
display:block!important;
padding-left:30px!important;
pointer-events:none!important;
}

#cdrlnk,.exylnk {
background:transparent!important;
color:inherit!important;
}

#lc-input {
margin-bottom:2px!important;
margin-right:2px!important;
max-width:150px!important;
width:100%!important;
}

#hdtb-rst.hdtb-mn-hd {
display:block!important;
padding:0 30px!important;
}

#resultStats {
opacity:1!important;
top:0!important;
}

#cnt,#footcnt,#footcnt .fbar {
background:transparent!important;
}

#irc_bg {
-moz-box-sizing:border-box!important;
-webkit-box-sizing:border-box!important;
background:transparent!important;
box-sizing:border-box!important;
left:0!important;
}

#irc_cl,#irc_cc {
background-color:#222!important;
}

#irc_cc {
margin-left:-200px!important;
}

#rhs_block {
margin-left:-100px!important;
}

#gsr:not(.hp) #tsf .tsf-p {
padding-left:0!important;
}

#gsr:not(.hp) #tsf .tsf-p > div > table:first-child,#gsr:not(.hp) #tsf .tsf-p > .sfbibbbc,#gsr:not(.hp) #tsf .tsf-p > .sfibbbc {
margin-left:200px!important;
padding-left:10px!important;
}

#gsr:not(.hp) #gbq1 {
margin-right:15px!important;
max-width:200px!important;
min-width:200px!important;
padding-right:0!important;
}

#hdtbMenus .hdtbU .hdtbItm.hdtbSel {
background:transparent!important;
color:#DD4B39!important;
font-weight:bold!important;
}

.action-menu .action-menu-panel {
background:transparent!important;
border:0!important;
box-shadow:none!important;
display:inline-block!important;
position:relative!important;
top:0!important;
vertical-align:top!important;
visibility:inherit!important;
z-index:0!important;
}

.action-menu .action-menu-panel .action-menu-item {
background:#EEE!important;
display:inline-block!important;
font-size:11px!important;
margin:0 2px!important;
padding:0 5px!important;
}

.action-menu .action-menu-panel .action-menu-item a.fl {
font-size:11px!important;
padding:0!important;
}

.action-menu .action-menu-panel .action-menu-button {
padding:0!important;
}

#rcnt h3.r a,#rcnt span.tl a {
text-decoration:underline!important;
}

#rcnt h3.r em,#rcnt span.tl em,#rcnt span.st em {
color:#DD4B39!important;
font-weight:bold!important;
}

#hdtb-tls,#hdtbMenus .hdtb-mn-hd,#hdtbMenus .hdtb-mn-hd .mn-dwn-arw,.action-menu .ab_button {
display:none!important;
}

#hdtb-s,#botabar {
margin-left:15px!important;
}

#hdtb-msb > .hdtb-mitem:first-child,#center_col,#footcnt,.ab_tnav_wrp,#rhscol,#kappbar {
margin-left:200px!important;
}

#footcnt ._hd,#footcnt ._iq,#kappbar .klcar {
margin-left:0!important;
}

}
