css:
@-moz-document domain("fs.to"), domain("fs.ua")
{

/*
// ==UserScript==
// @name FS TO
// @namespace https://userstyles.org/users/273791
// @description Убирает баннеры на внутренних страницах сайта.
// @author NewsIT
// @version 0.5
// @match fs.to
// @grant none
// ==/UserScript==
*/

  div#ir-ext-ui
  {
    background-color: rgba(255,255,255,0);
    border: 0;
    border-radius: 0;
    color: rgba(255,255,255,0);
    font-size: 0;
    line-height: 1px;
  }

  .b-section-controls
  {
    margin-top: 0!important;
  }

  .b-item-material-comments__item-review-positive
  {
    width: 99%!important;
    background: #DDF9D1!important;
  }

  .b-item-material-comments__item-review-negative
  {
    width: 99%!important;
    background: #FDD!important;
  }

  .b-item-material-comments__item-right
  {
    float: none;
    width: 92%!important;
    margin: 5px 0 3px 70px;
  }

  .b-new-feedback-text
  {
    padding: 12px 10px 0 15px;
  }

  .new-feedback-text-wrap .field
  {
    width: 920px;
  }

  .l-left
  {
    margin: 10px 0 0 15px;
  }

  .l-center
  {
    margin: 10px 0 0 30px;
  }

  .b-tab-item__description
  {
    font-size: 14px!important;
    line-height: 20px!important;
    text-indent: 1em;
    padding: 10px 10px 0!important;
  }

  .b-similar
  {
    margin: 18px 10px 0 15px !important;
    padding: 8px 0 0 8px !important;
  }

  .b-similar__title
  {
    margin-left: 25px;
  }

  .b-item-material-comments__best-title
  {
    margin: 22px 0 -10px 11px;
  }

  .b-item-material-comments__count
  {
    margin: 20px 0 10px 11px !important;
  }

  .b-item-material-comments__item-right-head
  {
    font-weight: 700;
  }

  .b-item-material-comments__item-question-title
  {
    font-weight: 400;
  }

  .b-item-material-comments__item-text
  {
    font-size: 14px!important;
    line-height: 20px!important;
    text-indent: 1em;
  }

  .b-item-material-comments__item
  {
    margin: 20px 0 0 9px !important;
  }

  .b-item-material-comments__item-question
  {
    margin: 3px 0;
  }

  .b-item-material-comments__footer
  {
    padding: 0 0 60px!important;
  }

  div [class*=-universal]
  {
    border: none;
  }

  .l-content.m-content_type_position_view
  {
    overflow: visible!important;
  }

  .l-content
  {
    min-height: 100%;
  }

  .l-content.m-content_type_player
  {
    padding-bottom: 0;
  }

  .l-content-wrap
  {
    margin-bottom: 10px;
  }

  .b-spoiler.m-expanded
  {
    background-position: 915px 8px!important;
  }

  .b-spoiler
  {
    background-position: 915px 8px!important;
    height: 20px;
  }

  .b-spoiler .right
  {
    text-indent: 0;
    width: 620px!important;
  }

  .b-poster-detail
  {
    border-right: 0 solid;
  }

  .b-poster-detail__link:hover
  {
    border-radius: 0;
    width: 315px;
  }

  .b-poster-detail__main
  {
    max-width: 165px!important;
  }

  .b-poster-tile__link:hover
  {
    border-radius: 0;
  }

  .b-scroll-top
  {
    background-position: 50% 95%!important;
    border-radius: 0!important;
    bottom: 0!important;
    box-shadow: none!important;
    height: 100%!important;
    right: 0!important;
    transition: all .2s!important;
    width: 80px!important;
  }

  .b-scroll-top:hover
  {
    background-color: rgba(4,14,21,0.65)!important;
    background-position: 50% 95%!important;
    border-radius: 0!important;
    bottom: 0!important;
    height: 100%!important;
    right: 0!important;
    transition: all .2s!important;
    width: 80px!important;
  }

  .b-aplayer__html5-desktop-skip
  {
    top: 505px!important;
  }

  a:visited.b-poster-tile__link *,a:visited.b-poster-tile__link
  {
    color: gray!important;
    border: none!important;
    background: #F8F8E6!important;
    text-shadow: 0 1px 1px #FFF;
  }

  body,.b-body-branding-hr,.l-body-branding,.l-body-inner
  {
    background: #151A21 no-repeat center fixed!important;
    background-size: cover!important;
  }

  .m-main
  {
    background: none!important;
  }

  div [class*=universal],div [class*=fsverticalsmall],.b-section-banner-wrap + div
  {
    height: 1px!important;
    max-height: 1px!important;
    overflow: hidden!important;
    opacity: 0;
  }

  .b-section-controls.m-section-controls_position_list
  {
    display: block!important;
  }

  .current-file-fullname
  {
    font-family: calibri;
  }

  .ir-ext-dimensions
  {
    background: #192436;
    color: #1a25;
  }

  .b-nowviewed,.b-popular-series
  {
    margin-top: -250px!important;
  }

  .l-content-center,.b-item-material-comments__content
  {
    width: 99%!important;
  }

  .b-actions-panel,.b-files-folders
  {
    margin: 0 10px 0 15px;
  }

  .b-contest,.b-contest__dropdown,.b-contest__top,.m-player-movie + div,.b-tab-item__edit-material + div,.l-content-right,.b-section-list > div:nth-last-of-type(2),.b-nowviewed ~ div:nth-last-of-type(3),.b-tab-item__edit-material ~ div:nth-last-of-type(3),.b-iframe-player-wrap ~ div,.b-section-banner-wrap + div div table tbody,div [class*=-universal__item],div [class*=-glowwidesmall__item],div [class*=-universalsmall__item],div [class*=-fsverticalsmall-item__link]
  {
    position: fixed;
    top: -1000px;
  }

  .b-aplayer-teasers__item,.b-online-watch-place
  {
    display: none;
  }

  .b-popular-series,.b-nowviewed,.b-section-controls
  {
    margin-top: -260px!important;
  }

  .b-player-popup__close,iframe[src^="http://ad.admixer"],a[href^="http://ad.admixer"],.b-share,iframe[src^="http://ad.admixer"],a[href^="http://ad.admixer"]
  {
    display: none!important;
  }
}

@-moz-document regexp("http://fs.to/video/*/.*?play&file.*"), regexp("http://fs.to/video/*/.*?play&file.*")
{

  [class*=l-header]
  {
    background: #000!important;
  }

  .l-body-inner
  {
    min-height: 100vh;
    background: #000!important;
    transition: background-color .4s ease;
  }

  .l-content-center
  {
    height: 0!important;
    opacity: .01;
    transform: translateY(100px)!important;
    transition: all .4s ease;
  }

  .l-content
  {
    background: #000!important;
    color: #fff!important;
    text-shadow: none!important;
    border: none!important;
  }

  .l-content a
  {
    color: #fff;
  }

  .b-tab-item__vote
  {
    border: none!important;
  }

  .b-files-folders-link
  {
    background-image: none!important;
    padding-left: 0!important;
  }

  .l-content:hover a
  {
    color: #000!important;
  }

  .b-spoiler .right,.l-content-center,.l-content-center,.b-item-material-comments__item
  {
    width: 100%!important;
  }

  .b-item-material-comments__item-right
  {
    width: 90%;
  }

  .b-tab-item__add-to,.b-tab-item__vote
  {
    display: none;
  }

  .l-content:hover .b-tab-item__add-to,.l-content:hover .b-tab-item__vote
  {
    display: table-cell;
  }

  .search .field-wrap
  {
    background: #000;
  }

  .search .field-wrap input
  {
    transition: all .4s ease;
    width: 98%;
  }

  .search .field-wrap input:focus
  {
    background: #232A33;
  }

  .b-tabs-player-page__edit,.b-share,.b-feedback__touch
  {
    display: none!important;
  }
}
