css:
@-moz-document domain("youtube.com") {
/* MASTER HEAD */
#yt-masthead-container {
	background-color: #1684F2;
    border-bottom: 0px solid;
}
  
#yt-masthead #search-btn {background: #FF5E5E !important; border: none !important;}

.appbar-hidden #masthead-positioner-height-offset {
    height: 50px;
    background-color: #0288E1;
}
  
/* ------ Guide ----------- */

#guide-container, #appbar-guide-menu, .guide-flyout {background: #2D79C5 !important; border: 0px !important;}
#appbar-guide-menu {top: -1px !important;}
.guide-item, .guide-item-subtitle, .guide-view-more , .guide-count {color: white !important;}
#guide-container li.guide-section h3 a {color: white !important;}
.guide-item.guide-item-selected {background: #2D79C5 !important; text-shadow: none !important;}
.guide-item:hover, .guide-item:hover .guide-item-subtitle , .guide-item:hover .guide-count{background: #FF7777 !important; color: #000000 !important;}
.guide-view-more:hover {background: #000000 !important; color: #2D79C5 !important;}

/* optional white shaddow: .guide-section:not(:last-of-type) {border-bottom: 0px !important; background: linear-gradient(#2D79C5 95%, rgba(196,52,46,0)), radial-gradient(farthest-side at 50% 100%, rgba(255,255,255,0.8), rgba(255,255,255,0)) 0 100% !important;}
.guide-section-separator {border-bottom: 0px !important;}*/

.guide-section.signup-promo {color: white !important;}

/* ------ Guide icons ----- */

.guide-item .guide-what-to-watch-icon        {background: url('https://s.ytimg.com/yts/imgbin/www-guide-topguide-vfl46PkXw.png') no-repeat scroll 0px -352px  !important;}
.guide-item .guide-my-channel-icon           {background: url('https://s.ytimg.com/yts/imgbin/www-guide-topguide-vfl46PkXw.png') no-repeat scroll 0px -631px  !important;}
.guide-item .guide-my-subscriptions-icon     {background: url('https://s.ytimg.com/yts/imgbin/www-guide-topguide-vfl46PkXw.png') no-repeat scroll 0px -304px  !important;}
.guide-item .guide-social-icon               {background: url('https://s.ytimg.com/yts/imgbin/www-guide-topguide-vfl46PkXw.png') no-repeat scroll 0px -559px  !important;}
.guide-item .guide-history-icon              {background: url('https://s.ytimg.com/yts/imgbin/www-guide-topguide-vfl46PkXw.png') no-repeat scroll 0px -583px  !important;}
.guide-item .guide-watch-later-icon          {background: url('https://s.ytimg.com/yts/imgbin/www-guide-topguide-vfl46PkXw.png') no-repeat scroll 0px -144px  !important;}
.guide-item .guide-playlists-icon            {background: url('https://s.ytimg.com/yts/imgbin/www-guide-topguide-vfl46PkXw.png') no-repeat scroll 0px -328px  !important;}
.guide-item .guide-likes-playlist-icon       {background: url('https://s.ytimg.com/yts/imgbin/www-guide-topguide-vfl46PkXw.png') no-repeat scroll 0px -24px  !important;}
.guide-item .guide-collection-icon           {background: url('https://s.ytimg.com/yts/imgbin/www-guide-topguide-vfl46PkXw.png') no-repeat scroll 0px -376px  !important;}
.guide-item .guide-builder-icon              {background: url('https://s.ytimg.com/yts/imgbin/www-guide-topguide-vfl46PkXw.png') no-repeat scroll 0px -839px  !important;}
.guide-item .guide-subscription-manager-icon {background: url('https://s.ytimg.com/yts/imgbin/www-guide-topguide-vfl46PkXw.png') no-repeat scroll 0px -815px  !important;}
.guide-item .guide-purchases-icon            {background: url('https://s.ytimg.com/yts/imgbin/www-guide-topguide-vfl46PkXw.png') no-repeat scroll 0px -770px !important;}
.guide-flyout-view-more-icon {background: url('https://s.ytimg.com/yts/imgbin/www-guide-topguide-vfl46PkXw.png') no-repeat scroll 0px -186px  !important;}
  
/* MASTER HEAD - New Upload Button */
.yt-uix-button-icon-material-upload{
	background: url(
data:image/png;base64,);
}
.yt-uix-button-opacity, .yt-uix-button-opacity:hover{
	opacity:100   
}
/* MASTER HEAD - Notification Active Bell */
#yt-masthead-user .yt-uix-button-icon-bell,#yt-masthead-user .yt-uix-button-icon-bell:hover {
	background:url(
data:image/png;base64,);
    opacity: 100
}

/* MASTER HEAD - Notification Active Bell */
#yt-masthead-user .sb-notif-on .yt-uix-button-icon-bell{
  background: no-repeat url(data:image/png;base64,);
  background-size: auto;
  opacity: 100;
}
#yt-masthead-user .sb-notif-on .yt-uix-button-icon-bell:hover {
  opacity: 50;
  }
/* MASTER HEAD - Sidebar Button */
.show-guide .yt-uix-button-icon-appbar-guide, .show-guide #masthead-positioner:hover .yt-uix-button-icon-appbar-guide {
    background: url(
data:image/png;base64,) !important;
}
.yt-uix-button-icon-appbar-guide {
background: url(
data:image/png;base64,) !important;
}
/* MASTER HEAD - Sidebar Button Box */
button#appbar-guide-button {
    box-shadow: 0 0 0 0;
    background-color: transparent;
    border: 0px solid;
}
button#appbar-guide-button:hover {
    box-shadow: 0 0 0 0;
    background-color: transparent;
    border: 0px solid;
}
button#appbar-guide-button:active {
    box-shadow: 0 0 0 0;
    background-color: transparent;
    border: 0px solid;
}
/* MASTER HEAD - Sidebar Button Arrow */
.not-yt-legacy-css #masthead-positioner #appbar-guide-button:hover .yt-uix-button-arrow, .yt-legacy-css #masthead-positioner:hover #appbar-guide-button .yt-uix-button-arrow {
  border-top-color: #FFF;
}
.not-yt-legacy-css #masthead-positioner #appbar-guide-button .yt-uix-button-arrow, .yt-legacy-css #masthead-positioner:hover #appbar-guide-button .yt-uix-button-arrow {
  border-top-color: #FFF;
}
/* MASTER HEAD - Region Abreviation Text Color */
#yt-masthead #logo-container span.content-region {
    color: #FFFFFF;
}
/* Full Page Alert (Added 12-8-15) */
.yt-alert-default.yt-alert-info, .yt-alert-actionable.yt-alert-info, .yt-alert-naked.yt-alert-info .yt-alert-icon, .yt-alert-small.yt-alert-info {
    background: #0288E1;
    border-bottom: 1px solid #e8e8e8;
}
/* MASTER SIDEBAR - Active Page */
.guide-item.guide-item-selected, .guide-item.guide-item-selected:hover{
  background: #0288E1;
}

/* MASTER SIDEBAR - Section Headings */
li.guide-section h3 a {
  color: #0288E1;
}
/* Page Navagation Tabs */
a.yt-uix-button-epic-nav-item.partially-selected:hover, button.yt-uix-button-epic-nav-item:hover, button.yt-uix-button-epic-nav-item.selected, button.yt-uix-button-epic-nav-item.yt-uix-button-toggled, .epic-nav-item:hover, .epic-nav-item.selected, .epic-nav-item.yt-uix-button-toggled, .epic-nav-item-heading{
  border-color: #0288E1;
}
.feed-header-feed-filter .yt-uix-button-epic-nav-item.selected, .feed-header-feed-filter .yt-uix-button-epic-nav-item:hover{
    border-color: #0288E1;
}
a.yt-uix-button-epic-nav-item:hover, a.yt-uix-button-epic-nav-item.selected, a.yt-uix-button-epic-nav-item.yt-uix-button-toggled{
    border-color: #0288E1;
}
/* PLAYER - Progress Bar */
.ytp-play-progress {
  background: #0288E1;
}
.ytp-swatch-background-color, .ytp-swatch-background-color-secondary {
    background-color: #0288E1;
}
/* TRANSPERENT PLAYER - Anotations & Auto Play Slider Buttons */
.ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox {
  background: #0288E1;
}
/* TRANSPERENT PLAYER - Volume Slider */
.ytp-volume-slider-handle:before {
    background: #0288E1;
}
/* TRANSPERENT PLAYER - Other Stuff */
.ytp-settings-button.ytp-hd-quality-badge:after, .ytp-settings-button.ytp-4k-quality-badge:after, .ytp-settings-button.ytp-5k-quality-badge:after, .ytp-settings-button.ytp-8k-quality-badge:after{
  background-color: #0288E1;
}
.ytp-chrome-controls .ytp-button-with-box:after{
  background-color: #0288E1;
}
.ytp-menuitem sup {
  color: #0288E1;
}
/* WATCH PAGE - AutoPlay Slider Button */
.yt-uix-checkbox-on-off input[type=checkbox]:checked+label {
  background-color: #0288E1;
}
/* WATCH PAGE - Like Scale */
.video-extras-sparkbar-likes {
  background: #0288E1;
}
/* CREATOR PAGES - Tabs */
.creator-editor-nav-tabs li.selected, .creator-editor-nav-tabs li:hover,.epic-nav-item.selected,.epic-nav-item:hover,.menu-tab:hover, .menu-tab.selected {
  border-bottom: 4px solid #0288E1;
}
/* CREATOR PAGES - Sidebar Sub Menu (Also used in user settings) */
#creator-sidebar .creator-sidebar-item.selected, #creator-sidebar .creator-sidebar-submenu .creator-sidebar-item.selected a:hover{
background-color: #0288E1;
}
/* SITEWIDE - Blue Buttons */
.yt-uix-button-primary, .yt-uix-button-primary[disabled], .yt-uix-button-primary[disabled]:hover, .yt-uix-button-primary[disabled]:active, .yt-uix-button-primary[disabled]:focus {
  border-color: #0288E1;
  background: #0288E1;
}
/* WATCHPAGE Loading Progress Bar (Thanks to nalljya) */
#progress {
    background-color:white!important;
    box-shadow: 0 0 0 0;
}

  
/* LAST VERSION */
       
.yt-uix-clickcard-card.yt-masthead-multilogin .yt-masthead-picker-header, .yt-uix-clickcard-card.yt-masthead-multilogin .yt-masthead-multilogin-users-header {
       background-color: #5366F2;
       border-bottom: none;
       color: #fff;
       font-size: 11px;
       font-weight: 500;
       padding: 5px 15px;
       text-transform: uppercase;
}
       
.compact-shelf .yt-uix-button-shelf-slider-pager {
       position: absolute;
       bottom: 10px;
       background: #59c4ff;
       border: none;
       width: 15px;
       height: 60px;
       top: 50%;
       margin-top: -60px;
}
       
.yt-uix-button-default {
       border-color: #d3d3d3;
       background: #59c4ff;
       color: #333;
}
       
.yt-uix-clickcard-card.yt-masthead-multilogin .yt-uix-card-body-arrow {
       border-bottom-color: #5366F2
}

.webkit .yt-ui-ellipsis-2, .yt-ui-ellipsis-3 { /* Titlecolor */
       background: transparent;
       color: #000000 !important;
       text-decoration: none;
       padding-bottom: 2px;
}
       
.yt-lockup-title a, .yt-lockup:hover a, .yt-lockup:hover .yt-lockup-meta a, .yt-lockup:hover .yt-lockup-description a {
       color: #004DFF      
}
       
#action-panel-details a {
       color: #0062FF;
}

.shelf-item .branded-page-module-title .branded-page-module-title-text { /* Headline color */
       color: rgba(0, 0, 0, 0.7) !important;
       font-size: 15px;
       font-weight: 500;
}

.yt-lockup .yt-lockup-meta a, .yt-lockup .yt-lockup-description a, .yt-lockup .yt-lockup-byline a {
       color: rgba(0, 0, 0, 0.9) !important;
       text-decoration: none;
}

.yt-default h1, .yt-default h2, .yt-default h3, .yt-default h4, .yt-default h5, .yt-default h6, h1.yt, h2.yt, h3.yt, h4.yt, h5.yt, h6.yt { /* Videotitle */
       color: #000000;
}

#watch-description {
       /* Description */
       color: #000000;
}

#watch-header .yt-user-info a {
       /* Channelname */
       color: #000000;
}

.comment-renderer-header .comment-author-text { /* namecolor */
	color: rgba(0, 9, 255, 0.7);
}

.comment-renderer-text-content { /* Comment textcolor */
