}

.yt-uix-button-opacity {
       opacity: 1;
}

#watch8-action-buttons .yt-uix-button, #watch8-action-buttons .yt-uix-button:hover, #action-panel-dismiss, #action-panel-dismiss:hover {
       color: #000000;
}

.channel-header .branded-page-header-title .branded-page-header-title-link {
       color: #0015FF;
       text-decoration: none;
}

a.yt-uix-button-epic-nav-item.selected, a.yt-uix-button-epic-nav-item.yt-uix-button-toggled, button.yt-uix-button-epic-nav-item.selected, button.yt-uix-button-epic-nav-item.yt-uix-button-toggled, .epic-nav-item.selected, .epic-nav-item.yt-uix-button-toggled, .epic-nav-item-heading, .branded-page-module-title a:visited, .branded-page-module-title a, .branded-page-related-channels h3 a, .branded-page-related-channels h3, .branded-page-module-title, .branded-page-v2-secondary-col .branded-page-related-channels-see-more a, .yt-card .yt-card-title, .caption-line-text {
