css:
@-moz-document domain("wikipedia"){}
@-moz-document regexp("https?://([a-z]{2,13}).wiki(p|m)edia.org/w(iki|)/(?!Main_Page|Wikipedia:Portada|Wikip%C3%A9dia:Accueil_principal|Wikipedia:Hauptseite|%D0%97%D0%B0%D0%B3%D0%BB%D0%B0%D0%B2%D0%BD%D0%B0%D1%8F_%D1%81%D1%82%D1%80%D0%B0%D0%BD%D0%B8%D1%86%D0%B0|Pagina_principale|%E3%83%A1%E3%82%A4%E3%83%B3%E3%83%9A%E3%83%BC%E3%82%B8|Hoofdpagina|Wikipedia:Strona_g%C5%82%C3%B3wna|Portal:Huvudsida|Trang_Ch%C3%ADnh|Unang_Panid|Syahan_nga_Pakli|Wikipedia:%E9%A6%96%E9%A1%B5|Wikip%C3%A9dia:P%C3%A1gina_principal|Ana_Sayfa|Bahasa_Indonesia|%EC%9C%84%ED%82%A4%EB%B0%B1%EA%B3%BC:%EB%8C%80%EB%AC%B8).*") {
#left-navigation,#p-views,#p-personal,#footer,.mw-indicators,#mw-page-base,.mw-editsection,#mw-head-base,.ui-widget-overlay,#siteSub,#mw-panel>.portal,.toctoggle,#firstHeading>*,#contentSub,#siteNotice{display:none!important}

@font-face{font-family:'Custom';src:url(https://fonts.gstatic.com/s/raleway/v11/0dTEPzkLWceF7z0koJaX1A.woff2)}
:not(pre){font-family:'Custom'!important}

/*Font size*/
h1,h2,h3{font-weight: 400 !important}
a,blockquote,p,dl,ul,li{font-weight:300!important}

body{font-size:16px}
h1,h1 *{font-size:55px!important}
h2,h2 *{font-size:43px !important}
h3,h3 *{font-size:31px !important}
h4,h4 *{font-size:22px!important}
blockquote{font-size:20px!important}
p,tt,dt{font-size:16px}
dd {font-size: 15px !important}
ul,li,li span {font-size: 14px !important}
#toctitle>h2{font-size:30px!important}

/*Line height*/
ul,li{line-height:1.8 !important}

/*Font style*/
blockquote,dt{font-style: italic !important}
tt {font-family: 'Source Code Pro','Lucida Sans Unicode','Courier New' !important}

/*Link color*/
a{color:#0088cc!important;text-decoration:none}
a:visited{color:#9a15c3!important}
a:hover{color:#005580!important;text-decoration:underline}
.new,.new:link,.new:visited{color:#cd5b45!important}
.suggestions-result-current{background-color:#005580!important}
code{color: #39892f !important}

/*Border*/
*{border:0!important;box-shadow:none!important}
[role="menu"],[type="search"],.referencetooltip,.suggestions,.thumbinner,.ui-widget,table td,table th,table tr{border:1px solid #eee!important}

/*Background*/
*{background-color:transparent!important}
body,body>*,[role="menu"],[type="search"],.suggestions,.credits_box{background-color:white!important}

/*spacing*/
dt{margin-top: 10px !important;}
h1{margin-bottom:80px!important}
table,td,th{padding:5px!important}
code {padding:3px!important}
blockquote{margin:35px 75px!important}
select{padding:0 10px}
#toc ul{margin:0 0 0 1em!important} 
.suggestions-result{padding:10px!important}

/*extra*/
table,td,th{border-collapse:collapse!important}
.suggestions{width:500px!important;margin:0px!important;box-sizing:border-box;top:29px!important;position:fixed!important;right:0!important}
#toc{overflow:hidden!important}
#toc:hover{overflow-y:scroll!important}
#coll-download-as-rdf2latex{display:block!important;position:fixed;top:0!important}
#p-search,#searchButton{position:fixed!important;height:30px!important;top:0;right:0}
#p-search {width:500px!important;overflow:hidden;margin:0!important;z-index:100}
#p-search form {margin:0!important}
#simpleSearch{margin:0!important;padding:0!important;width:500px!important;height:30px!important;background:transparent!important}
#simpleSearch>#searchInput{opacity:0;height:30px!important;width:500px!important}
#simpleSearch:hover>#searchInput,#simpleSearch>#searchInput:focus{opacity:1}
#toc{width:300px!important;position:fixed!important;top:250px!important;padding:0 0 0 10px!important;bottom:0!important;background:transparent!important;left:0!important;overflow-y:hidden!important;height:calc(100% - 250px)!important}
#toctitle {padding-bottom:1em}

@media all and (max-width: 1199px){
#toc{display:none}
#content{padding-top:0}
body{padding:80px 0 0 0}
.mw-body{margin:0!important}
#p-logo {position:fixed!important;height:30px!important;width:30px!important;left:0!important;top:0!important}
.mw-wiki-logo {background-image:url(https://www.wikipedia.org/portal/wikipedia.org/assets/img/Wikipedia_wordmark.png)!important;height:30px!important;width:100px!important;background-size:100px auto!important;padding:0!important;background-position:center;padding-left:6px!important}
#mw-navigation{background-color:white!important;position:fixed;height:30px!important;top:0!important;width:100%}}

@media all and (min-width: 1200px){
#toc{display:inline}
#content{padding-top:100px}
body{padding:0 0 0 140px}
.mw-body{margin-left:200px!important}
#p-logo {position:fixed!important;height:250px!important;width:250px!important;left:30px!important;top:0!important}
.mw-wiki-logo {background-image:url(https://upload.wikimedia.org/wikipedia/commons/thumb/b/bb/Wikipedia_wordmark.svg/2000px-Wikipedia_wordmark.svg.png)!important;height:250px!important;width:250px!important;background-size:250px auto!important;padding:0!important;background-position:center}}
}

@-moz-document regexp("https?://([a-z]{2,13}).wiki(p|m)edia.org/w(iki|)/(Main_Page|Wikipedia:Portada|Wikip%C3%A9dia:Accueil_principal|Wikipedia:Hauptseite|%D0%97%D0%B0%D0%B3%D0%BB%D0%B0%D0%B2%D0%BD%D0%B0%D1%8F_%D1%81%D1%82%D1%80%D0%B0%D0%BD%D0%B8%D1%86%D0%B0|Pagina_principale|%E3%83%A1%E3%82%A4%E3%83%B3%E3%83%9A%E3%83%BC%E3%82%B8|Hoofdpagina|Wikipedia:Strona_g%C5%82%C3%B3wna|Portal:Huvudsida|Trang_Ch%C3%ADnh|Unang_Panid|Syahan_nga_Pakli|Wikipedia:%E9%A6%96%E9%A1%B5|Wikip%C3%A9dia:P%C3%A1gina_principal|Ana_Sayfa|Bahasa_Indonesia|%EC%9C%84%ED%82%A4%EB%B0%B1%EA%B3%BC:%EB%8C%80%EB%AC%B8)") {
*{border-color:#eee!important}
body{background-color:white!important}
#p-logo>a,body>:not(#mw-navigation):not(.suggestions),#mw-head>:not(#right-navigation),#mw-panel>.portal,#p-views{display:none!important}
#p-logo,#mw-panel {position:fixed!important;left:calc(50% - 175px);top:120px}
#p-logo{background-image:url(https://upload.wikimedia.org/wikipedia/commons/thumb/b/bb/Wikipedia_wordmark.svg/2000px-Wikipedia_wordmark.svg.png)!important;height:250px!important;width:350px!important;background-size:350px auto!important;padding:0!important;background-position:center;background-repeat:no-repeat}
#searchform{position:fixed!important;left:calc(50% - 292px);top:300px;z-index:1;width:554px}
#simpleSearch{width:554px!important;background-image:none!important;min-width:584px!important;height:40px!important;padding:0!important}
#searchInput{width:554px!important;height:40px!important;padding:0 0 0 16px!important}
#searchButton{margin-right:8px!important}}
