css:
@-moz-document domain("tsdm.net"), domain("tsdm.me"), domain("tsdm.tw")  {

/* tsdm 水区极简 - v1.6 -  2016/05/30 D */

#p_btn,
.a_pb,
.sign {
    display: none;
}
.po {
    border-top: none;
}
.fastre,
.add_callme,
.editp,
.showmenu,
.showmenu+a,
.showmenu+a+a {
    background: none;
    color: rgba(76, 158, 177, 0.71);
}
.qdsmile,
.tsdm_norm_title,
.tsdm_statbar,
.authicn,
.md_ctrl {
    display: none;
}
.pls .avatar img {
    padding: 0px;
    border-radius: 922px;
    background: rgba(146, 128, 128, 0) ;
    max-height: 64px;
    max-width: 64px;
}
.post_username_1,
.post_username_3 {
    filter: glow(color=#00BFFF, strength=3);
    height: 18px;
    color: #34B3A8;
    padding: 1px;
    margin-top: 8px;
    font-size: 16px;
    text-align: center;
    text-shadow: 0 3px 2px rgba(0, 191, 255, 0.52);
}
.post_username_2 {
    filter: glow(color=#00BFFF, strength=3);
    height: 18px;
    color: #F7247A;
    padding: 1px;
    margin-top: 8px;
    font-size: 16px;
    text-align: center;
    text-shadow: 0 3px 2px rgba(255, 20, 147, 0.6);
}
.post_nickname {
    text-align: center;
    color: rgba(0, 0, 0, 0.28);
    /* position: relative; */
    
    margin-top: 6px;
}
.pls .avatar {
    margin-top: 22px;
}
.pi {
    opacity: 0.4;
}
.ad .pls {
    height: 1px;
}
.t_f,
.t_f td {
    font-size: 15px;
    color: rgba(0, 0, 0, 0.74);
    padding: 10px;
    padding-top: 20px;
    padding-bottom: 32px;
    background: rgba(0, 0, 0, 0);
}
.plc {
    position: relative;
}
.po {
    display: none;
}
.tsdm_post_t:hover .po {
    display: block
}
.pi {
    opacity: 0.6;
    position: absolute;
    width: 500px;
    text-align: right;
    bottom: 4px;
    right: 2px;
    margin-bottom: 10px;
    padding: 7px;
    border-bottom: none;
}
.ad .plc {
    background: #D8D6D6;
    padding: 0;
    overflow: hidden;
}
.tsdm_post_t {
    background-color: #F1F1F1;
}
.pl .quote {
    padding-bottom: 5px;
    border: 1px solid rgba(93, 93, 93, 0.09);
}
.rate {
    margin: 20px 0;
    font-size: 12px;
    margin-bottom: 33px;
}
.a_pt a img {
    height: 30px;
    opacity: 0.6;
}
.bm {
    border: 1px solid rgba(93, 93, 93, 0.29);
}

.tsdm_post_t tbody tr:nth-of-type(1) {
    background: linear-gradient(to bottom, #F1F1F1, rgba(242, 242, 242, 0));
}

.by_nullice {
    opacity: 1.020;
}
/* 帖子列表： */

.tl th,
.tl td {
    padding: 9px 0;
    border-bottom: 1px dashed #D6D6D6;

}
.bmw .bm_c th,
.bmw .bm_c td {
    background: none
}
.bmw tr cite {
    opacity: 0.4;
}
.bmw tr .by:nth-of-type(2) cite {
    opacity: 0.6;
}
.bmw tr .by:nth-of-type(2) cite+em {
    opacity: 0.5;
}
.bmw tr cite+em {
    opacity: 0.3;
}
.bmw td.num a {
    opacity: 0.4;
}
.bmw td.num em {
    opacity: 0.3;
}
.bmw span.tps {
    opacity: 0.2;
}
.bmw tbody th img {
    opacity: 0.3;
}
.bmw tbody:hover {
    background-color: rgba(0, 176, 255, 0.03);
}
.bmw tbody:hover tr cite {
    opacity: 0.8;
}
.bmw tbody:hover tr cite+em {
    opacity: 0.7;
}
.bmw tbody:hover td.num a {
    opacity: 0.8;
}
.bmw tbody:hover td.num em {
    opacity: 0.7;
}
.bmw tbody:hover span.tps {
    opacity: .7;
}
.bmw tbody:hover th img {
    opacity: 0.45;
}
.bmw .by_nullice {
    opacity: 1.020;
}
}
