@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.caf.fr"), domain("wwwd.caf.fr") {
  body, #theme-header, #theme-page, #footer-outside, .conteneur-titre, .section-caf, #content, #header, .bloc-bottom, .bottom-image, #bas_caf, #nav-bar, #main, #page, #nav-inner {
    background: #444 !important;
  }
  
  #theme-page-principal, .home-bottom, .bloc-content, .bloc-title, #page-content ul li, .bx-wrapper .bx-viewport, .vdf_accueil_gauche .vdf_accueil_gauche_contenu, .vdf_accueil_container .vdf_accueil_container_bloc_centre, .vdf_accueil_droit_contenu, #page-wrapper, #affiche, .bloc-bottom, .ma-caf, .bloc-custom.mon-compte {
    background: #666 !important;
  }
  
  #theme-page-sidebar, #theme-footer-principal, .conteneur-contenu, .theme-bloc-bar-moncompte-contenu, #sidebar, .bx-wrapper .bx-caption, .bloc-vdf-dernieres-actus-content, .bloc-vdf-dernieres-actus, .bloc-vdf-articles-recommandes, .bloc-vdf-articles-les-plus-lus, .vdf-rubriques-filter-by .filter-by-content, .masituation_container.active, .bloc-content {
    background: #888 !important;
  }
  
  #menu li.on, .active-trail {
    background: #aaa !important;
  }
  
  .div-col-left .img-triangle {
    opacity: 0;
  }
  
  body, .section-caf #page-content p, #page-content p, div h3 span, #page-content table td {
    color: #999 !important;
  }
  
  h2, a, #footer-2 .block1 ul ul a, #primary .view-content .item-list ul li a, #secondary .view-content .item-list ul li a, #trois .view-content .item-list ul li a, #breadcrump a {
    color: #ccc !important;
  }
  
  #breadcrump p, .vdf_slider_titre, .vdf_accueil_gauche_contenu .vdf_accueil_titre, .vdf_accueil_centre_contenu .vdf_accueil_titre, .vdf_accueil_droit_contenu .vdf_accueil_titre, .vdf_btn_important_title {
    color: #39c !important;
  }
  
  #menu li.on a, .bx-wrapper .bx-caption, .filter-by-content-input, .active-link, .active {
    color: #444 !important;
  }
  
  .vdf_btn_important_title {
    border: 1px solid #39c;
  }
}
