css:
@-moz-document domain("play.pokemonshowdown.com"), domain("psim.us") {
.userbar:before {
    display: block;
    content: "";
    width: 32px;
    height: 32px;
    float: left;
    position: relative;
    top: -5px;
    background: url(http://www.gifs.net/Animation11/Creatures_and_Cartoons/Pokemon/Clefairy.gif);
}
body {
    background: url("http://i.imgur.com/b5xVgUi.jpg?2") !important;
    -webkit-background-size: cover;
    -o-background-size: cover;
    background-size: cover;
}
.ps-room.ps-room-light {
    background: rgba(0, 0, 0, 0);
    border-left: 1px solid #fff;
}
.ps-room.ps-room-light.scrollable {
    background: rgba(0, 0, 0, 0.5);
    color: #FFF;
    text-shadow: 0px -1px 0px #0F1923;
}
.header {
    background: rgba(0, 0, 0, 0.5);
}
.userbar .username {
    text-shadow: 1px 1px 0px #000, 1px -1px 0px #000, -1px 1px 0px #000, -1px -1px 0px #000;
}
.menugroup {
    background: transparent;
}
.mainmenu .menugroup:nth-child(1) .button,
.mainmenu .menugroup:nth-child(2) p:nth-child(1) .button,
.mainmenu .menugroup:nth-child(2) p:nth-child(2) .button,
.mainmenu .menugroup:nth-child(2) p:nth-child(3) .button,
.mainmenu .menugroup:nth-child(3) p:nth-child(1) .button,
.mainmenu .menugroup:nth-child(3) p:nth-child(2) .button {
    font-size: 15px;
    font-family: "helvetica neue", Helvetica, Verdana, sans-serif;
    box-shadow: 0px 0px 3px rgba(0, 0, 0, 0.5), 0px 1px 1px rgba(255, 255, 255, 0.5) inset;
    background-image: linear-gradient(#eab7f3, #496491);
    background: -moz-linear-gradient(center top, #7392A7, #496491) repeat scroll 0% 0% transparent;
    border: 1px solid #444ac9;
}
.mainmenu .menugroup:nth-child(1) .button:hover,
.mainmenu .menugroup:nth-child(2) p:nth-child(1) .button:hover,
.mainmenu .menugroup:nth-child(2) p:nth-child(2) .button:hover,
.mainmenu .menugroup:nth-child(2) p:nth-child(3) .button:hover,
.mainmenu .menugroup:nth-child(3) p:nth-child(1) .button:hover,
.mainmenu .menugroup:nth-child(3) p:nth-child(2) .button:hover,
.mainmenu .menugroup:nth-child(1) .button:focus,
.mainmenu .menugroup:nth-child(2) p:nth-child(1) .button:focus,
.mainmenu .menugroup:nth-child(2) p:nth-child(2) .button:focus,
.mainmenu .menugroup:nth-child(2) p:nth-child(3) .button:focus,
.mainmenu .menugroup:nth-child(3) p:nth-child(1) .button:focus,
.mainmenu .menugroup:nth-child(3) p:nth-child(2) .button:focus {
    background: #444ac9;
    border: 1px solid #304C7E;
    box-shadow: 0px 1px 5px rgba(0, 0, 0, 0.2), 0px 0px 1px rgba(0, 0, 0, 0.2) inset;
}
.menugroup .button.disabled,
.menugroup .button.disabled:hover {
    background: #f3f3f3;
    border: 1px solid #304C7E;
    box-shadow: 0px 1px 5px rgba(0, 0, 0, 0.2), 0px 0px 1px rgba(0, 0, 0, 0.2) inset;
    color: #DDD;
}
.select {
    font-family: "helvetica neue", Helvetica, Verdana, sans-serif;
    box-shadow: 0px 0px 3px rgba(0, 0, 0, 0.5), 0px 1px 1px rgba(255, 255, 255, 0.5) inset;
    background-image: linear-gradient(#7392A7, #496491);
    background: -moz-linear-gradient(center top, #7392A7, #496491) repeat scroll 0% 0% transparent;
    border: 1px solid #304C7E;
    color: #FFF;
    text-shadow: 0px -1px 0px #0F1923;
}
.select:hover,
.select:focus {
    background: #496491;
    border: 1px solid #304C7E;
    box-shadow: 0px 1px 5px rgba(0, 0, 0, 0.2), 0px 0px 1px rgba(0, 0, 0, 0.2) inset;
}
.select:disabled {
    color: #FFF !important;
    border: 1px solid #304C7E;
    background: #496491;
}
.select::after {
    color: #FFF;
}
.battleform .label {
    opacity: 0;
}
.roomlist {
    width: 100%;
    max-width: 100% !important;
}
.roomlist h2 {
    color: #FFF;
    text-shadow: 1px 1px #0F1923;
}
.roomlist h2:hover {
    color: #FFF;
    text-shadow: 0px -1px 0px #0F1923;
}
.roomlist a.ilink {
    color: #fff;
    font-family: "helvetica neue", Helvetica, Verdana, sans-serif;
    text-shadow: 0px 1px 0px #0F1923;
    box-shadow: 0px 1px 5px rgba(0, 0, 0, 0.2), 0px 1px 1px rgba(255, 255, 255, 0.5) inset;
    background-image: linear-gradient(#496491, #6575bc);
    background: -moz-linear-gradient(center top, #7392A7, #496491) repeat scroll 0% 0% transparent;
    border: 1px solid #304C7E;
    width: calc(60% + 100px);
}
.roomlist a.ilink:hover {
    background: #496491 url("http://cdn.bulbagarden.net/upload/1/16/Spr_3e_173.png") center no-repeat;
    background-position: 400px;
    border: 1px solid #304C7E;
    box-shadow: 0px 1px 5px rgba(0, 0, 0, 0.2), 0px 0px 1px rgba(0, 0, 0, 0.2) inset;
    color: #FFF;
}
.mainmenufooter {
    position: fixed;
    bottom: 0px;
    left: 0px;
    padding: 8px;
    font-family: "helvetica neue", Helvetica, Verdana, sans-serif;
    background: #496491;
    border: 1px solid #304C7E;
    color: #FFF;
    text-shadow: 1px 1px 0px #304C7E, 1px -1px 0px #304C7E, -1px 1px 0px #304C7E, -1px -1px 0px #304C7E;
    border-top-right-radius: 5px;
    box-shadow: 0px 0px 3px rgba(0, 0, 0, 0.5), 0px 1px 1px rgba(255, 255, 255, 0.5) inset;
}
.mainmenufooter a {
    color: #FFF;
    text-shadow: 1px 1px 0px #304C7E, 1px -1px 0px #304C7E, -1px 1px 0px #304C7E, -1px -1px 0px #304C7E;
}
.maintabbarbottom {
    border-color: #000;
    box-shadow: 1px 1px 5px #000;
    z-index: 4;
}
.tabbar a.button.cur,
.tabbar a.button.cur:hover {
    border-color: #000;
    z-index: 4;
}
.tabbar a.button {
    border-color: #000;
    font-family: "helvetica neue", Helvetica, Verdana, sans-serif;
    z-index: 3;
}
.tabbar a.button:hover {
    border-color: #000;
    z-index: 4;
}
.userlist {
    background: rgba(0, 0, 0, 0.5);
    border: none;
    right: 1px !important;
    color: #FFF;
    text-shadow: 1px 1px #000;
}
.userlist li button {
    background: rgba(0, 0, 0, 0.5);
    text-shadow: 1px 1px #000;
}
.userlist li button:hover {
    background: transparent;
}
.userlist li em.group {
    color: #DDD;
    text-shadow: 1px 1px #000;
}
.userlist li {
    border-bottom: 1px solid #000;
}
.chat-log {
    bottom: 38px;
    left: 144px !important;
    border-bottom: 1px solid #000;
    border-left: 1px solid #000;
    background: url(http://i.imgur.com/1qx6qf1.jpg?4);
    background-size: cover;
    font-weight: bold;
    color: black;
    border-width: 0;
}
.utilichart {
    position: relative;
    border: 1px solid #181818;
    border-radius: 2px;
    box-shadow: inset 0px 0px 0px 1px rgba(0, 0, 0, .2);
    background: url(http://i.imgur.com/Om5gnwJ.png);
    margin-top: 5px;
    min-height: 33px;
    width: 270px;
    height: 96px
}
.utilichart:after,
.infobox:after,
.ladder:after,
.pm-window:after,
.ps-popup:after,
#tooltipwrapper .tooltip:after {
    display: block;
    content: "";
    width: 2px;
    height: 3px;
    position: absolute;
    left: -48px;
    top: -8px
}
.utilichart .numcol,
.utilichart .movenamecol,
.utilichart .namecol {
    padding: 0;
    margin: 0;
    text-align: left;
    font-weight: bold;
    color: #161616;
    position: absolute;
    left: 10px;
    top: 2px;
    font-size: 8pt
}
.utilichart .pokemonnamecol {
    padding: 0;
    margin: 0;
    font-weight: bold;
    color: #161616;
    position: absolute;
    left: 40px;
    top: 2px;
    font-size: 8pt
}
.utilichart .typecol {
    width: 100px;
    height: 0;
    padding: 0;
    margin: 0;
    position: absolute;
    left: 10px;
    top: 18px
}
.utilichart .typecol img {
    opacity: 1;
    margin-right: 10px
}
.utilichart .iconcol,
.utilichart .itemiconcol {
    position: absolute;
    right: 5px;
    top: 3px;
}
.utilichart .abilitycol,
.utilichart .twoabilitycol {
    float: left;
    color: #1b1b1b;
    margin: 34px 40px 0 0px;
    width: 85px;
    font-size: 8pt
}
.utilichart .abilitycol em,
.utilichart .statcol em,
.utilichart .bstcol em,
.utilichart .twoabilitycol,
.utilichart .labelcol em,
.utilichart .widelabelcol em,
.utilichart .pplabelcol em {
    color: #DDD
}
.utilichart .statcol,
.utilichart .bstcol {
    position: relative;
    left: -5px;
    top: 3px;
    text-align: center;
    color: black;
    margin-right: 12px
}
.utilichart .itemdesccol,
.utilichart .abilitydesccol,
.utilichart .movedesccol {
    white-space: normal;
    padding-top: 7px;
    font-size: 8pt;
    width: 250px;
    height: 50px;
    overflow: visible;
    color: #1b1b1b;
    position: absolute;
    top: 28px;
    left: 10px
}
.utilichart .labelcol,
.utilichart .widelabelcol,
.utilichart .pplabelcol {
    padding-top: 0;
    height: 28px;
    width: 24px;
    text-align: center;
    font-size: 8pt;
    margin: 65px 45px 0 0
}

.chat-log-add {
    background: rgba(0, 0, 0, 0.5);
    border: none;
    left: 145px !important;
}
.pm-log,
.pm-window.focused .pm-log {
    border-color: #000;
}
.pxInta,
.pxInta:focus,
.pxInta:link,
.pxInta:visited,
.pxInta:active,
.pxInta:hover {
    pointer-events: none !important;
    cursor: text !important;
    border: none !important;
    color: #000 !important;
    text-decoration: none !important;
    padding-bottom: 0px !important;
}
.pm-log-add,
.pm-window.focused .pm-log-add {
    background: rgba(0, 0, 0, 0.5);
    border-color: #000;
}
.pm-window h3,
.pm-window h3:hover {
    border-color: #000;
    background: rgba(0, 0, 0, 0.5) url("http://vignette3.wikia.nocookie.net/pokemontowerdefensetwo/images/a/ac/036_normal.png/revision/latest/scale-to-width-down/44?cb=20131109042807") right top no-repeat;
    color: #FFF;
    text-shadow: 0px -1px 0px #0F1923;
}
.pm-window.focused h3,
.pm-window.focused h3:hover {
    border-color: #000;
    background: rgba(0, 0, 0, 0.5) url("http://vignette3.wikia.nocookie.net/pokemontowerdefensetwo/images/a/ac/036_normal.png/revision/latest/scale-to-width-down/44?cb=20131109042807") right top no-repeat;
    color: #FFF;
    text-shadow: 0px -1px 0px #0F1923;
}
.pm-window h3 small {
    color: #DDD;
}
.pm-window h3 .closebutton,
.pm-window h3 .minimizebutton,
.pm-window h3:hover .minimizebutton {
    color: #DDD;
}
.pm-window h3 .closebutton:hover,
.closebutton:hover {
    color: #E89797;
    background: transparent;
    border: none;
}
.pm-window h3 .minimizebutton:hover {
    color: #E8C297;
    background: transparent;
    border: none;
}
.challenge {
    color: #FFF;
    border: 1px solid #304C7E;
    background: rgba(73, 100, 145, 0.8) url("http://i40.tinypic.com/fuq5i8.png");
}
.challenge .select {
    opacity: 0.8;
}
.challenge .select:hover {
    opacity: 1;
}
.message-pm {
    font-style: italic;
    color: #000;
    padding: 0px 25px 0px 0px;
    background: url("http://www.blastyak.net/img/mail.png") top right no-repeat;
}
.textbox,
.textbox:hover {
    border-color: #000;
    box-shadow: none;
    font-family: "helvetica neue", Helvetica, Verdana, sans-serif;
}
.textbox:focus {
    box-shadow: 0px 0px 2px #3A569F;
    border-color: #3A569F;
}
button {
    border-radius: 4px;
    font-family: "helvetica neue", Helvetica, Verdana, sans-serif;
    background-image: linear-gradient(#7392A7, #496491);
    background: -moz-linear-gradient(center top, #7392A7, #496491) repeat scroll 0% 0% transparent;
    border: 1px solid #304C7E;
    color: #FFF;
    text-shadow: 0px -1px 0px #0F1923;
}
button:hover {
    background: #496491;
    border: 1px solid #304C7E;
}
button[name="newTop"],
button[name="edit"] {
    margin-bottom: 2px;
}
/*****************************/
/* Teambuilder CSS */
/*****************************/

.teamchartbox {
    border-top: 1px solid #000;
}
.teamlist .team {
    border-radius: 4px;
    font-family: "helvetica neue", Helvetica, Verdana, sans-serif;
    background-image: linear-gradient(#f3aff0, #f3aff0);
    background: -moz-linear-gradient(center top, #7392A7, #496491) repeat scroll 0% 0% transparent;
    border: 1px solid #304C7E;
    color: #000;
    text-shadow: none;
    box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.2), 0px 1px 1px rgba(255, 255, 255, 0.8) inset
}
.teamlist .team:hover {
    background: #496491;
    border: 1px solid #304C7E;
}
.setchart,
.setchart-nickname {
    background-image: linear-gradient(#98b4e1, #948ecf);
    background: -moz-linear-gradient(center top, #7392A7, #496491) repeat scroll 0% 0% transparent;
    border: 1px solid #304C7E;
    box-shadow: 0px 0px 3px rgba(0, 0, 0, 0.5), 0px 1px 1px rgba(255, 255, 255, 0.5) inset;
}
.setchart label,
.setchart-nickname label {
    color: #000;
    text-shadow: 0px 0px 0px #0F1923;
    font-family: "helvetica neue", Helvetica, Verdana, sans-serif;
}
.setmenu {
    top: 2px;
}
.setmenu button:hover {
    font-family: "helvetica neue", Helvetica, Verdana, sans-serif;
    background: #496491;
    border: 1px solid #304C7E;
}
.setchart input,
.setchart-nickname input,
.statform input.inputform,
.setchart input:hover,
.setchart-nickname input:hover,
.statform input.inputform:hover,
.setstats,
.setdetails,
.setstats:hover,
.setdetails:hover {
    border-color: #000;
    box-shadow: none;
    font-family: "helvetica neue", Helvetica, Verdana, sans-serif;
}
.setchart input:focus,
.setchart-nickname input:focus,
.statform input.inputform:focus,
.setstats:focus,
.setdetails:focus {
    box-shadow: 0px 0px 2px #3A569F;
    border-color: #3A569F;
    font-family: "helvetica neue", Helvetica, Verdana, sans-serif;
}
.setstats,
.setdetails,
.setchart .setdetails label,
.setchart .setstats label {
    color: #000;
    text-shadow: none;
}
.setstats .statrow-head em {
    color: #333;
}
.teamchart li.format-select select {
    border: 1px solid #000;
}
.teamchart li.format-select option {
    border-bottom: 1px solid #000;
}
.teambuilder-results h3,
.teambuilder-results .utilichart h3 {
    background-image: linear-gradient(#7392A7, #496491);
    background: -moz-linear-gradient(center top, #7392A7, #496491) repeat scroll 0% 0% transparent;
    border-color: #304C7E;
    box-shadow: 0px 0px 3px rgba(0, 0, 0, 0.5), 0px 1px 1px rgba(255, 255, 255, 0.5) inset;
    color: #FFF;
    text-shadow: 0px -1px 0px #0F1923;
    font-family: "helvetica neue", Helvetica, Verdana, sans-serif;
}
.utilichart .movenamecol,
.utilichart .pokemonnamecol,
.utilichart .namecol {
    color: #FFF;
    text-shadow: 0px -1px 0px #0F1923;
    font-family: "helvetica neue", Helvetica, Verdana, sans-serif;
}
.utilichart .twoabilitycol,
.utilichart .abilitycol,
.utilichart .statcol,
.utilichart .labelcol,
.utilichart .widelabelcol,
.utilichart .pplabelcol {
    color: #EEE;
    text-shadow: 0px -1px 0px #0F1923;
    font-family: "helvetica neue", Helvetica, Verdana, sans-serif;
}
.utilichart .statcol em,
.utilichart .bstcol em,
.utilichart .itemdesccol,
.utilichart .abilitydesccol,
.utilichart .movedesccol,
.utilichart .labelcol em,
.utilichart .widelabelcol em,
.utilichart .pplabelcol em,
.utilichart .numcol {
    color: #CCC;
    text-shadow: 0px -1px 0px #0F1923;
    font-family: "helvetica neue", Helvetica, Verdana, sans-serif;
}
.teambuilder-results .firstresult a,
.teambuilder-results .result a:hover {
    background: rgba(0, 0, 0, 0.5);
    border: 1px solid #000;
}
.utilichart b {
    color: #3B5F93;
}
.teambar button {
    background-image: linear-gradient(#7392A7, #496491);
    background: -moz-linear-gradient(center top, #7392A7, #496491) repeat scroll 0% 0% transparent;
    border-color: #000;
    box-shadow: 0px 0px 3px rgba(0, 0, 0, 0.5);
    color: #FFF;
    text-shadow: 0px -1px 0px #0F1923;
    font-family: "helvetica neue", Helvetica, Verdana, sans-serif;
    border-radius: 0px;
    overflow-x: hidden;
}
.teambar button:hover {
    background: #496491;
    border-color: #000;
}
.teambar button:disabled,
.teambar button:disabled:hover,
.teambar button:disabled:active {
    background: #496491;
    border-color: #000;
    color: #FFF;
    text-shadow: 0px -1px 0px #0F1923;
}
.teambuilder-clipboard-container {
    border-color: #304C7E;
    background: rgba(0, 0, 0, 0.2);
}
.teambuilder-clipboard-data,
.teambuilder-clipboard-data:hover {
    background-image: linear-gradient(#7392A7, #496491);
    background: -moz-linear-gradient(center top, #7392A7, #496491) repeat scroll 0% 0% transparent;
    border-color: #304C7E;
}
.teambuilder-clipboard-data .section {
    border-color: #304C7E;
}
.teambuilder-clipboard-button-right,
.teambuilder-clipboard-button-left {
    margin-top: 1px;
}
.jslider .jslider-value {
    color: #000;
    text-shadow: none;
}
.jslider .jslider-label {
    color: #FFF;
    opacity: 0.8;
}
/*****************************/
/** Teambuilder CSS **/
/*****************************/

.ladder table,
.ladder td,
.ladder th {
    border: 1px solid #000;
}
.ladder th {
    background: rgba(0, 0, 0, 0.6);
}
.ladder td {
    background: rgba(0, 0, 0, 0.4);
}
.ladder td:hover {
    background: rgba(0, 0, 0, 0.6);
}
.ps-popup {
    background: rgba(0, 0, 0, 0.9) url("http://cdn.bulbagarden.net/upload/thumb/f/f4/035Clefairy.png/96px-035Clefairy.png") right bottom no-repeat;
    color: #EEE;
    border: 1px solid #000;
    box-shadow: 1px 1px 0px rgba(255, 255, 255, 0.2) inset, -1px -1px 0px rgba(255, 255, 255, 0.1) inset, 0px 0px 5px #000;
}
.ps-overlay {
    background: rgba(0, 0, 0, 0.3);
}
.ps-popup hr {
    border: none;
    border-bottom: 1px solid #DDD;
}
.ps-popup a {
    color: #eea5dc;
    text-shadow: 0px -1px 0px #0F1923;
}
.ps-popup a.ilink {
    color: #eea5dc;
    text-shadow: 0px -1px 0px #0F1923;
}
.ps-popup small {
    color: #CCC;
}
.ps-popup .roomlist a.ilink {
    width: 293px;
    text-shadow: none;
    color: #FFF;
}
.ps-popup .roomlist a.ilink:hover {
    background: #496491 url("http://www.smogon.com/dex/media/sprites/rs/clefable.png") right no-repeat;
}
.popupmenu button {
    color: #EEE;
    text-shadow: 0px -1px 0px #0F1923;
    overflow-x: hidden;
}
.popupmenu button:hover {
    border: none;
    background: rgba(0, 0, 0, 0.8);
}
.popupmenu button.sel {
    border-color: #000;
}
.popupmenu button.sel:hover {
    border-color: #FFF;
    background: rgba(0, 0, 0, 0.8);
}
.popupmenu h3 {
    color: #7392A7;
    text-shadow: 0px -1px 0px #0F1923;
}
p.or {
    color: #CCC;
}
p.or::before,
p.or::after {
    color: #AAA;
}
.credits h2 {
    color: #eea5dc;
    text-shadow: 0px -1px 0px #0F1923;
}
a.subtle,
.credits ul li p,
.credits ul {
    color: #7392A7;
    text-shadow: 0px -1px 0px #0F1923;
}
a.subtle:hover {
    color: #7392A7;
}
.credits li small {
    color: #DDD;
}
.avatarlist button {
    border-color: #FFF;
}
.avatarlist button:hover {
    border-color: #F9F9F9;
    background-color: #000;
    box-shadow: none;
}
/*****************************/
/* Battle CSS */
/*****************************/

.battle-log {
