/* Battle Background */

.backdrop {
    width: 640px;
    height: 360px;
    position: absolute;
    top: 0px;
    left: 0px;
    overflow: hidden;
    background: -50px -90px;
    opacity: 1 !important;
}
.backdrop:before {
    display: block;
    content: "";
    background: url("http://i.imgur.com/Zot3gC3.gif");
    background-size: cover;
    width: 640px;
    height: 360px;
}
.backdrop:after {
    display: block;
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    width: 640px;
    height: 360px;
}
/* Battle Background ~end */
