/* Popup Info/Descriptions */

#tooltipwrapper {
    position: absolute;
    top: 400px;
    left: 100px;
    text-align: left;
    color: #8ECAFA;
    text-shadow: 1px 1px #000 !important;
}
#tooltipwrapper .tooltipinner {
    position: relative;
}
#tooltipwrapper .tooltip {
    position: absolute;
    bottom: 0;
    left: 0;
    width: 300px;
    background: rgba(0, 0, 0, 0.9);
    color: #EEE;
    border: 1px solid #000;
    box-shadow: 1px 1px 0px rgba(255, 255, 255, 0.2) inset, -1px -1px 0px rgba(255, 255, 255, 0.1) inset, 0px 0px 5px #000;
