css:
@-moz-document domain("bitskins.com") {
body {
    color: #c5c5c5;
    background-color: #1d1d1d;
}
.navbar .navbar-nav>li>a {
    color: #c5c5c5;
}
.breadcrumb-wrapper {
    background-color: #1d1d1d;
    border-bottom: 1px solid #585858;
}
.container-fluid {
    background-color: #1d1d1d;
}
.btn-default {
    color: #c5c5c5;
    background-color: #282828;
    border-color: #585858;
}
.panel-default>.panel-heading {
    color: #c5c5c5;
    background-color: #1d1d1d;
    border-color: #585858;
}
.panel {
    background-color: #282828;
    border: 1px solid #585858;
}
.navbar-inverse {
    border-color: #585858;
}
.navbar-inverse .navbar-brand {
    color: #585858;
}
.navbar {
    border: 1px solid #585858;
}
.form-control {
    color: #c5c5c5;
    background-color: #1d1d1d;
    background-image: none;
    border: 2px solid #585858;
}
input {
    background-color: #1d1d1d;
    color: #c5c5c5;
    border: 2px solid #585858;
}
::-webkit-scrollbar {
    width: 10px;
    background-color: #2b2b2b;
}
::-webkit-scrollbar-thumb {
    background-color: #444444;
    width: 10px;
}
.item-title {
    border-bottom: 1px solid #585858 !important;
    background-color: #282828 !important;
}
.scrollable-menu {
    background-color: #282828;
}
.dropdown-menu>li>a {
    color: #c5c5c5;
}
.footer {
    background-color: #282828;
}
.item-icon {
    background-color: #2B2B2B !important;
    border-color: #585858 !important
}
small,
.small {
    color: #c5c5c5;
}
.navbar-nav>li>.dropdown-menu {
    background-color: #282828;
}
.table>thead>tr>th,
.table>tbody>tr>th,
.table>tfoot>tr>th,
.table>thead>tr>td,
.table>tbody>tr>td,
.table>tfoot>tr>td {
    border-top: 1px solid #585858;
}
.table-hover>tbody>tr:hover {
    background-color: #585858;
}
.table-striped>tbody>tr:nth-of-type(odd) {
    background-color: #1d1d1d;
}
.table>thead>tr>td.danger,
.table>tbody>tr>td.danger,
.table>tfoot>tr>td.danger,
.table>thead>tr>th.danger,
.table>tbody>tr>th.danger,
.table>tfoot>tr>th.danger,
.table>thead>tr.danger>td,
.table>tbody>tr.danger>td,
.table>tfoot>tr.danger>td,
.table>thead>tr.danger>th,
.table>tbody>tr.danger>th,
.table>tfoot>tr.danger>th {
    background-color: #881919;
}
.table>thead>tr>td.success,
.table>tbody>tr>td.success,
.table>tfoot>tr>td.success,
.table>thead>tr>th.success,
.table>tbody>tr>th.success,
.table>tfoot>tr>th.success,
.table>thead>tr.success>td,
.table>tbody>tr.success>td,
.table>tfoot>tr.success>td,
.table>thead>tr.success>th,
.table>tbody>tr.success>th,
.table>tfoot>tr.success>th {
    background-color: #225d0a;
}
.nav-tabs>li.active>a,
.nav-tabs>li.active>a:hover,
.nav-tabs>li.active>a:focus {
    color: #c5c5c5;
    background-color: #585858;
    border: 1px solid #585858;
    border-bottom-color: #585858;
    cursor: default;
}
.nav-tabs {
    border-bottom: 1px solid #585858;
}
.fa-fw {
    color: #c5c5c5;
}
* {
    color: #c5c5c5;
}
.navbar-inverse .navbar-nav>.active>a,
.navbar-inverse .navbar-nav>.active>a:hover,
.navbar-inverse .navbar-nav>.active>a:focus {
    color: #c5c5c5;
    background-color: transparent;
}
.navbar-inverse .navbar-nav>.open>a,
.navbar-inverse .navbar-nav>.open>a:hover,
.navbar-inverse .navbar-nav>.open>a:focus {
    background-color: transparent;
    color: #c5c5c5;
}
pre {
    background-color: #282828;
    border: 1px solid #585858;
}
.hljs,
.hljs-subst,
.hljs-tag .hljs-title,
.nginx .hljs-title {
    color: #c5c5c5;
}
.hljs {
    background: #1d1d1d;
    border-color: #585858;
}
hr {
    border-top: 1px solid #585858;
}
#sidebar li.active {
    border: 0 #c5c5c5 solid;
    border-right-width: 5px;
}
.modal-body {
    background-color: #282828;
}
.modal-header {
    background-color: #1d1d1d;
    border-bottom: 1px solid #585858
}
.modal-footer {
    background-color: #1d1d1d;
    border-top: 1px solid #585858;
}
.chat-box {
    color: #c5c5c5;
    border: 1px solid #585858;
    background-color: #282828;
}
.btn-group>.btn:first-child:not(:last-child):not(.dropdown-toggle) {
    background-color: #1d1d1d;
    border-color: #585858;
    color: #c5c5c5;
    font-weight: bold;
}
.btn-group>.btn:hover,
.btn-group-vertical>.btn:hover,
.btn-group>.btn:focus,
.btn-group-vertical>.btn:focus,
.btn-group>.btn:active,
.btn-group-vertical>.btn:active,
.btn-group>.btn.active,
.btn-group-vertical>.btn.active {
    background-color: #1d1d1d;
    border-color: #585858;
    color: #c5c5c5;
}
.form-inline .form-control {
    background-color: #1d1d1d;
}
th.text-center {
    background-color: #282828 !important;
    border: 1px solid #585858;
}
.table-bordered>thead>tr>th,
.table-bordered>tbody>tr>th,
.table-bordered>tfoot>tr>th,
.table-bordered>thead>tr>td,
.table-bordered>tbody>tr>td,
.table-bordered>tfoot>tr>td {
    border: 1px solid #585858;
}
div {
    background-color: #1d1d1d !important;
}
img {
    mix-blend-mode: luminosity;
}
select#use_recent_sale_prices {
    background-color: #1d1d1d;
    border: 2px solid #585858;
}
input,
button,
select,
textarea {
    background-color: #1d1d1d;
    border: 1px solid #585858;
}
.input-group-addon:first-child {
    border-right: 0 #585858;
    background-color: #282828;
    border-color: #585858;
}
.jplist-label {
    float: left;
    height: 30px;
    box-shadow: 0 0 1px #585858;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    -ms-box-sizing: border-box;
    -o-box-sizing: border-box;
    box-sizing: border-box;
    border: 2px solid #585858;
    border-radius: 3px;
    text-shadow: None;
    color: #c5c5c5;
    line-height: 30px;
    padding: 0 10px;
    margin: 10px 10px 0 0;
    background: #1d1d1d;
    background: -moz-linear-gradient(top, #1d1d1d 0, #282828 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0, #1d1d1d), color-stop(100%, #282828));
    background: -webkit-linear-gradient(top, #1d1d1d 0, #282828 100%);
    background: -o-linear-gradient(top, #1d1d1d 0, #282828 100%);
    background: -ms-linear-gradient(top, #1d1d1d 0, #282828 100%);
    background: linear-gradient(to bottom, #1d1d1d 0, #282828 100%);
}
.jplist-panel button {
    box-shadow: 0 0 1px #585858;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    -ms-box-sizing: border-box;
    -o-box-sizing: border-box;
    border: 1px solid #585858;
    border-radius: 3px;
    text-shadow: none;
    color: #27252a;
    background: #1d1d1d;
    background: -moz-linear-gradient(top, #1d1d1d 0, #282828 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0, #1d1d1d), color-stop(100%, #282828));
    background: -webkit-linear-gradient(top, #1d1d1d 0, #282828 100%);
    background: -o-linear-gradient(top, #1d1d1d 0, #282828 100%);
    background: -ms-linear-gradient(top, #1d1d1d 0, #282828 100%);
    background: linear-gradient(to bottom, #1d1d1d 0, #282828 100%);
    margin: 10px 10px 0 0;
}
.jplist-drop-down ul li {
    width: 100%;
    float: left;
    height: 30px;
    line-height: 30px;
    text-indent: 10px;
    cursor: pointer;
    background-color: #1d1d1d;
}
.jplist-panel input[type="text"] {
    border: 1px solid #585858;
    border-radius: 3px;
    text-shadow: 1px 1px 1px #585858;
    color: #c5c5c5;
    width: 150px;
    text-indent: 5px;
    background: #1d1d1d;
}
.jplist-drop-down {
    border: 2px solid #585858;
    border-radius: 3px;
    text-shadow: 1px 1px 1px #585858;
    color: #585858;
}
.jplist-drop-down ul {
    border: 1px solid #585858;
    border-radius: 0 0 3px 3px;
    box-shadow: 0 3px 6px #585858;
}
.ui-state-default,
.ui-widget-content .ui-state-default,
.ui-widget-header .ui-state-default {
    background: #1d1d1d;
    font-weight: bold;
    color: #1c94c4;
    border: 1px solid #585858;
}
select {
    color: #c5c5c5 !important;
    background-color: #282828 !important;
}
}
