css:
@-moz-document url-prefix("http://202.119.113.135"),url-prefix("http://202.119.113.135"),url-prefix("http://202.119.113.147"),url-prefix("http://202.119.113.148"),url-prefix("http://jwxt1.hhu.edu.cn") {
body,input,textarea,select,button, table, td, tr, a{
	font-family: "Hiragino Sans GB", "Microsoft YaHei", "WenQuanYi Micro Hei", sans-serif!important;
    
}
body{
	background-color: #333535;
}


/*登陆界面*/
[background="/img/pic/login/bg-top.jpg"], [background="/img/pic/login/bg-top.jpg"] + table, .mainbox + table, [name="loginForm"] + table{
	display: none;
}

 [name="loginForm"] > table > tbody > tr:last-child {
 	display:none;
}
[name="loginForm"] > table > tbody > tr:first-child > td > table{
 	border-spacing: 0;
}
[name="loginForm"] > table > tbody > tr:first-child > td > table td.font12{
	height: 0;
}

/*教务系统标题*/
[name="loginForm"] > table > tbody > tr:first-child > td > table td.font12 > span{
	visibility: hidden;
	position: relative;
}
[name="loginForm"] > table > tbody > tr:first-child > td > table td.font12 > span::after{
	visibility: visible;
	position: absolute;
	top: 0;
	left: 0;
    padding: 0 30px;
    font-size: 27pt;
    color: #333535;
	content: "教务系统";
    
	text-shadow:  1px 1px 0px #666;
}

table.mainbox > tbody > tr:last-child, .mainbox > tbody > tr > td:first-child, .mainbox > tbody > tr > td:last-child {
 	display:none;
}
table.mainbox {
	border-style: none;
    background-color: rgb(71, 71, 71);
    box-shadow: 2px 2px 4px #222 inset;
    border-radius: 4px;
}
[name="loginForm"] > table {
	margin-top: 40px;
}
table.font-b {
 	padding: 50px;
    font-weight: normal;
}
table.font-b span, table.font-b td{
	font-size: 1.3em;
    color: #232525;
    text-shadow:  1px 1px 0px #666;
    text-decoration: none;
}
table.font-b td{
    padding: 7px 25px 7px 0;
}
table.font-b a{
    font-size: 12pt;
    color: #fff;
}

[onclick="m_changeOne();"]{
	visibility: hidden;
	position: relative;
}

[onclick="m_changeOne();"]::after{
	visibility: visible;
	position: absolute;
	top: 0;
	left: 0;
    color: #232525;
    text-shadow:  1px 1px 0px #666;
    font-size: 1.1em;
	content: "换一张";
}
input[name="zjh"], input[name="mm"], input[name="v_yzm"] {
    height: 28px;
    width: 220px;
    font-size: 14pt;
    border-radius: 5px;
    border: 1px;
    padding: 1px 5px;
    color: #ddd;
    background-color: #666;
}
input[name="v_yzm"] {
    width: 60px;
    vertical-align: middle;
}
/*验证码图片*/
#vchart{
	vertical-align: middle;
    height: 27px;
    width: 100;
}
/*登陆按钮*/
[src="/img/zh/login.gif"], [src="/img/zh/reset.gif"]{
	width:0px;
    height:0px;
    background:url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' version='1.1' height='36px' width='120px' fill='#000'><text x='40' y='25' fill='#aaa' font-size='20'>登陆</text></svg>");
    padding: 18px 60px;
    margin: 0 20px;
    border-radius: 5px;
    border: 2px solid rgba(85, 174, 245, 0.5);
}

[src="/img/zh/reset.gif"]{
    background:url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' version='1.1' height='36px' width='120px' fill='#000'><text x='40' y='25' fill='#aaa' font-size='20'>重设</text></svg>");
    border: 2px solid rgba(245, 85, 65, 0.5)
}

[src="/img/zh/login.gif"]:hover{
	box-shadow: 0px 0px 3px 1px rgba(85, 174, 245, 0.9);
}
[src="/img/zh/reset.gif"]:hover{
    box-shadow: 0px 0px 3px 1px rgba(245, 85, 65, 0.9);
}


/*主界面*/

.topbg{
	background-image: none; /*关闭顶部栏丑的要死渐变色*/
}
.topbg img{
	display: none;
}
/* 右上角用户名称 */
.leftuser01, .leftuser01 a{
    font-size: 11pt!important;
    margin-top: 4px;
    color: #eee;
}
/*选中顶部某标签时的文本*/
.hot_txt{
	font-size: 11pt!important;
}
#topMenu3{
	height: 5px;
}
.bg1, .bg2, .bg3, .bg4, .bg5, .bg6, .bg7{
    background-image: none;
    padding-top: 0px;
}


.bg-line1, .bg-line2, .bg-line3, .bg-line4, .bg-line5, .bg-line6, .bg-line7{
    margin-top: 0px;
}

/*顶部标签的颜色*/
.bg-line1, .bg1{
	background-color: #238BD4;
}
.bg-line2, .bg2{
	background-color: #41C8CA;
}
.bg-line3, .bg3{
	background-color: #FF9900;
}
.bg-line4, .bg4{
	background-color: #8BC34A;
}
.bg-line5, .bg5{
	background-color: #CE5B04;
}
.bg-line6, .bg6{
	background-color: #BF891D;
}
.bg-line7, .bg7{
	background-color: #4CAF50;
}

.btn1, .btn2, .btn3, .btn4, .btn5, .btn6, .btn7{
	font-size: 10pt!important;
    padding: 2px;
}

/*侧边按钮*/
.content2 td{
    font-size: 10pt;
    padding: 3px;
}

.title01 td{
	font-size: 10pt;
    padding: 2px;
    background-color: #fff;
}

/*侧边栏*/
table.border01{
	border: 0;
}

/*删掉中间的小按钮 left.jsp*/
[background="/img/left/bg.jpg"]{
    display: none;
}

/*main框架顶部*/
#tblHead td{
	background-image: none;
    font-size: 11pt;
}
/*删除边框蓝色*/
.Linetop{
	display:none;
}


/*成绩页面*/
.t_td_bj, .t_td_n_bj, .t_td_n_bj font{
    font-size: 11pt;
	background-image: none;
}
.t_td_bj{
	font-size: 12pt;
}
[src="img/b_n_left.gif"],[src="img/b_n_right.gif"],[src="img/b_left.gif"],[src="img/b_right.gif"]{
	display:none;
}
}
