css:
@-moz-document domain("eztv.ag") {
	br {
		display: none;
	}
	table {
		width: 100% !important;
	}
	span[style*="9px"] {
		display: none;
	}
	#header_logo,
	#site_menu,
	#gap,
	#line {
		display: none;
	}
	.show_info_main_logo,
	.show_info_banner_logo,
	.show_info_rating_score,
	.section_post_header,
	.show_info_description {
		display: none;
	}
	div[style*="float: left;"] {
		text-align: left;
		width: auto !important;
	}
	div[style="float: left"] {
		display: none;
		visibility: hidden;
	}
	.show_info_airs_status {
		width: auto;
		text-align: left !important;
		padding: 0 !important;
	}
	td {
		height: auto !important;
		padding-top: 0px !important;
		padding-bottom: 0px !important;
	}
	td.forum_thread_header[width="35"] {
		display: none;
	}
	td.forum_thread_post[width="35"] {
		display: none;
	}
	a.download_1,
	a.download_2,
	a.download_3 {
		display: none;
	}
	td.forum_thread_header_end {
		display: none;
	}
	td.forum_thread_post_end {
		display: none;
	}
	div[style="text-align: center;"] {
		display: none;
	}
}
