css:
/* STYLE BY UNHOLYMASTER || TUMBLR & INSTAGRAM */

@-moz-document domain("alphabetizer.flap.tv"){

#logo.img-responsive {
background: url('http://i.imgur.com/9NXtbxq.png');
}

.btn-success, .btn-primary:hover {
background: #9043cc;
border: #be7fef;
}

.btn-success:hover, .btn-primary {
background: #be7fef;
border: #9043cc;
}

.dropdown-menu, div.modal-body {
color: #a2a0a0;
background: #181717;
}

.dropdown-menu>li>a {
color: #9043cc;
}

.dropdown-menu>li>a:hover {
background: #a2a0a0;
color: #be7fef;
}

.dropdown-menu>li>a:focus {
background: #a2a0a0;
color: #181717;
}

.h4, .panel-title, .modal-title {
color: #9043cc;
}

.navbar, .modal-header {
background: #181717;
background: -webkit-linear-gradient(#181717, #000000); /* Safari 5.1 to 6.0 */
background: -o-linear-gradient(#181717, #000000); /* Opera 11.1 to 12.0 */
background: -moz-linear-gradient(#181717, #000000); /* Firefox 3.6 to 15 */
background: linear-gradient(#181717, #000000); /* Standard syntax */
border: transparent;
}

.navbar-default .navbar-nav>.open>a, .navbar-default .navbar-nav>.open>a:hover, .navbar-default .navbar-nav>.open>a:focus {
background: #9043cc;
color: #a2a0a0;
}

.navbar-default, li.divider, .panel {
border: #000000 !important;
}

.panel, .panel-default>.panel-heading, #options-list, .panel-body {
background: #181717;
color: #a2a0a0;
}

.panel-default>.panel-heading {
border: #a2a0a0;
}

.suboptions {
background: #252525;
border: #000000;
}

a {
color: #9043cc;
}

a:hover {
color: #be7fef;
}

body, .body {
background: url('http://i.imgur.com/ltO7evQ.png');
}

}
