css:
@-moz-document domain("new.vk.com") {
  .module_header,
  .ui_tabs_header,
  .page_block_header,
  .right_list_header,
  .right_list_header,
  .page_block_sub_header {
      background-color: #DEE5EB;
  }
  .page_block_sub_header {
      margin-bottom: 15px;
  }
  .apps_feed_wrap {
      height: 302px;
  }
  .right_list_header,
  .right_list_header {
      height: 38px;
  }
  .module_header {
      height: 35px;
  }
  .module_body {
      padding: 12px 14px 15px;
  }
  .page_photo {
      padding: 14px; /* Удаление деффекта с переносом кнопки подарка под фото */
  }
  .ui_filters_block {
      padding: 7px 14px 5px;
  }
  .search_row.short {
      padding: 0 9px 0 0;
  }
  .header_top .fl_l {
      margin-top: -2px;
  }
  .header_top .fl_r {
      margin-top: -2px;
  }
  #profile_photos_module .fl_l {
      margin-top: -6px;
  }
  .page_block {
      border: 1px solid #CBD6E2;
  }
  .page_photos_module {
      padding: 13px 17px 16px;
  }
  .photos_container_album_grid {
      padding: 14px;
  }
  .audio_rows_header {
      width: 548px !important;
  }
  input.ui_search_field:not(#video_search_input) {
      border-bottom: 1px solid #CBD6E2;
  }
  .apps_cat_wrap {
      margin: 0 4px 0 16px;
  }
  .fave_video_rows {
      padding: 14px 11px 14px 14px;
  }
  .fave_users_row {
      padding: 20px 10px 0 14px;
  }
  .audio_friend {
      padding: 5px 13px;
  }
  #page_photos_module .page_square_photo {
      background-position: 50% 20%;
  }
  .chat_onl_wrap {
      right: 0;
  }
  .im-page .im-page--dialogs-search .ui_search_field {
      height: 46px;
  }
  .videocat_featured_video {
      height: 139px;
      width: 243px;
  }
  .videocat_featured_big {
      height: 290px;
      width: 497px;
  }
  .photos_container_albums .photos_album_thumb {
      width: 244px;
  }
  .video_block_layout .videocat_row {
      border-top: 1px solid #DEE5EB;
      padding-top: 8px;
  }
  .video_block_layout {
      padding: 0 19px 0 20px;
  }
  /* Пункты меню как в старой версии сайта */

  #side_bar_inner,
  #side_bar_inner li {
      position: relative !important;
  }
  #side_bar_inner .left_label {
      font-size: 11px;
  }
  #l_fr .left_label::before,
  #l_ph .left_label::before,
  #l_vid .left_label::before,
  #l_vid .left_label::before,
  #l_aud .left_label::before,
  #l_msg .left_label::before,
  #l_gr .left_label::before,
  #l_nwsf .left_label::before,
  #l_fav .left_label::before {
      content: 'Мои ';
  }
  #l_ap .left_label::before {
      content: 'Приложения ';
  }
  #l_ap .left_label {
      text-overflow: clip !important;
      width: 70px;
  }
  #l_fr {
      top: -56px;
      /* Переместить пункт меню "Друзья" на позицию 2 */
  }
  #l_ph {
      top: -84px;
      /* Переместить пункт меню "Фотографии" на позицию 3 */
  }
  #l_vid {
      top: -112px;
      /* Переместить пункт меню "Видеозаписи" на позицию 4 */
  }
  #l_aud {
      top: -56px;
      /* Переместить пункт меню "Аудиозаписи" на позицию 5 */
  }
  #l_msg {
      top: 84px;
      /* Переместить пункт меню "Сообщения" на позицию 6 */
  }
  #l_gr {
      top: 56px;
      /* Переместить пункт меню "Группы" на позицию 7 */
  }
  #l_nwsf {
      top: 168px;
      /* Переместить пункт меню "Новости" на позицию 8 */
  }
  #l_ap {
      top: 47px;
      /* Переместить пункт меню "Игры" на позицию 9 */
  }
  #l_fav {
      top: -47px;
      /* Переместить пункт меню "Закладки" на позицию 10 */
  }
  .top_home_logo {
      margin-left: -25px !important;
  }
  #side_bar_inner {
      left: -25px;
  }
  #side_bar .more_div {
      margin-left: -15px;
  }
  #side_bar ol li .left_row {
      width: 175px;
  }
  #side_bar .more_div {
      margin-left: 0px !important;
  }
  .leave_redesign,
  .more_div {
      border-color: #9AB0C6 !important;
      width: 167px;
  }
  .wall_module .post {
      border-top: none;
  }
  .im-page .im-page-history-w {
      position: relative;
      width: 590px !important;
  }
  .im-chat-input {
      padding: 15px 32px 17px;
  }
  #content {
      width: 912px !important;
  }
  @media screen and (max-width: 1152px) {
      /* При переходе на страницу сообщений страница не растягивается */

      #page_header, #page_layout {
          width: 960px !important;
      }
      .im-page .im-page--history {
          width: 500px;
          /* Прижимаем меню на прежнюю позицию, сохраняю ширину элемента, в который меню вложено */
      }
      #content {
          float: right;
          position: relative;
          width: 795px !important;
      }
      .im-page .im-page--dialogs {
          width: 245px;
      }
      .im-page .im-page--history {
          margin-left: 244px;
          width: 551px;
      }
      .im-chat-input .im-chat-input--textarea {
          width: 363px;
      }
  }
  @media screen and (max-width: 1080px) {
      .scroll_fix, #content {
          max-width: 100% !important;
          width: 792px;
      }
      .apps_main .app_container {
          height: auto !important;
          width: 100% !important;
      }
      #page_body {
          width: 811px !important;
      }
      #page_header,
      #page_layout {
          margin-left: 16px;
      }
      .top_home_logo {
          margin-left: -15px !important;
          /* Подвинуть логотип */
      }
      #side_bar_inner {
          left: -15px !important;
          /* Подвинуть левое меню */
      }
      .im-page .im-page--dialogs {
          width: 235px;
      }
      .im-page .im-page--history {
          margin-left: 234px;
          width: 540px;
      }
      .im-chat-input .im-chat-input--text {
          width: 360px !important;
      }
      .im-chat-input {
          padding: 15px 25px 17px;
      }
      .im_msg_text,
      .im-mess--text {
          width: 355px;
      }
      ._im_mess,
      .im-page-history-w {
          width: 450px;
      }
      .im-page .im-page--header-chat {
          width: 540px;
      }
      .im-mess .im-mess--fav {
          margin-right: 20px;
      }
  }
  @media screen and (min-width: 1008px) {
      .scroll_fix, #content {
          max-width: 100% !important;
          overflow: hidden;
          width: 792px;
      }
  }
  /* Антибаг с сообщениями при низком разрешении */

  @media screen and (min-width: 1177px) {
      .im-page .im-page--header-chat {
          min-width: 100%;
          width: 550px;
      }
  }
  @media screen and (max-width: 1060px) {
      .im-mess-stack .im-mess-stack--tools {
          position: absolute;
          right: 75px;
      }
  }
}
