@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.skyrock.com"), domain("skyrock.com") {
  body, #sidebar, .submenu, .submenu ul {
    background: #444 !important;
  }
  
  #nav_menu, #wrap, #nav_bottom > ul > li h4, #column_left, .comment, #wrap.no_column::before, #column_right {
    background: #666 !important;
  }
  
  .nav_menu_left li a:hover, .link_active, .active, #sub_menu_left, #sidebar::before {
    filter: invert(0.70);
  }
  
  .footer_logo {
    filter: invert(0.60);
  }
  
  .events .event-primary .newsfeed_message_content, #nf-container .sub-nav, #column_right .bloc_recherche_home form, #list_whatsup .box_msg_ie8_fade_out, #list_whatsup .box_msg, #list_whatsup li, .list_intros li, .bloc_bckgrd_home, #sky_inscription, .mn-card-inner, .mm-card-friend-link, .column-right-box, .signaler_abus {
    background: #888 !important;
    border: 1px solid #aaa;
  }
  
  #bloc-profil, .container-wall, #body_messagerie_tbl, .menu-sidebar-left, .common-list-item-actions, #perso_blog, .phylac-top-left div, #f_title, #f_description, #fancybox-content, .popin-header, .popin-footer, #nf_events_filter {
    background: #888;
  }
  
  .msg_sender, .msg_infos {
    background: #888 !important;
    border: none !important;
  }
  
  #sidebar {
    border-left: 10px solid #4C4C4C;
  }
  
  .loginbox_header input {
    background-image: url("data:image/png;base64,");
    filter: invert(0.70);
    color: #222 !important;
  }
  
  .bloc_bckgrd_home span label, .bloc_bckgrd_home select, .bloc_bckgrd_home input, .title, #searchSideBarForm input, #searchSideBarForm select, .bulle-profil-conteneur, .comment-content, .newsfeed_message_content {
    background: #aaa !important;
  }
  
  #comment_list li .comment_text, .sub-nav, #column_left::before, .footer_logo::before {
    background: none;
  }
  
  .msg_checkbox {
    background: none !important;
    border: none !important;
  }
  
  #footer {
    border-top: 4px solid #888;
    background: #666;
  }
  
  #liste_message table {
    border: none !important;
  }
  
  .timeago, loginbox_header input, .submenu li em, #nav_messages a, #profiladmin_background a, #profiladmin_theme a, #profiladmin_link a, .col_third a, #barrenoire_account_menu a, #plus2rubrik-menuOptions li a, .account_pseudo, .title_actions li a {
    color: #ccc !important;
  }
  
  a {
    color: #249 !important;
  }
  
  .active a, #searchSideBarForm input, .pseudo time {
    color: #222 !important;
  }
}
