css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("noob-club.ru") {
/* NC AdFree Legion v.0.1 by Dexogen (vk.com/efiald) */

body {
    background: url(http://pikbox.ru/img/21/20160614171339317Wiki_background.jpg) #000 no-repeat !important;
    background-size: contain !important;
}
#header-new {
    background: #000 url(http://pikbox.ru/img/17/20160614143038279bg_header.jpg) 0 0 repeat-x !important;
}

.main_banner,
.right_col_banner,
.main_cactus,
.counter,
#clickable-zebra,
#clickable-banner,
#myBannerAfterPost,
#game-select,
#headjs,
#clickable-bamboo,
#clickable-bamboo-bf,
.main_bamboo,
.right_col_bamboo,
.content_bamboo,
#clickable-wolf,
#clickable-zebroid,
#vk_groups,
#footer_bg,
.content_cactus,
#clickable-zebrosha {
    display: none !important
}

#header-new {
    margin-bottom: 0 !important
}

.entry-content,
.w-arrows,
.blogs,
.list-block,
.entry-header,
.entry-footer {
    color: #efc9a0 !important;
    background: #212121 !important
}

hr.top,
hr.bottom {
    margin: auto;
    background-color: #444
}

.blogs a,
.forums a,
.tactics a,
.classes a,
ul#progress-all-25.w-arrows a,
ul#hot-week.w-arrows a,
.true-menu ul li a {
    color: #ffb100 !important
}

.list-block {
    border: 1px solid #444 !important;
    margin: -5px 0 0 -1px  !important;
    width: 100% !important
}

.right_col_header {
    width: 238px !important;
    border-left: 2px solid #444;
}

a:link.bbc_link,
a:visited.bbc_link {
    color: #ffb100 !important;
}

span.comments a {
    color: #ababab !important;
}

span.more a,
.user,
#top_toolbar a {
    color: #f3e6d0 !important;
}

span.author a {
    color: #ffb100  !important;
}

.pagination,
.right_col h2,
.right_col h2 a,
.blizzquote a:link,
.blizzquote a:visited,
.blizzquote td {
    color: #f3e6d0 !important;
}

a.navPages,
#header-new a.messages {
    color: #ffb100 !important;
}

.main-page h1 a {
    color: #ffb100 !important;
}



input[type="text"],
input[type="password"] {
    background: #f3e6d0 !important;
    border-color: #ffb100 !important
}
}
