css:
@-moz-document domain("vkontakte.ru") {
	* {
		background-color: transparent !important;
		color: #a0a0a0 !important;
		border-color: #606060 !important;
	}
	
	body {
		background-color: #404040 !important;
	}
	
	#pageHeader {
		background-color: #606060 !important;
		background-image: none !important;
		border: solid 1px #a0a0a0 !important;
		-moz-border-radius-topleft: 5px;
		-moz-border-radius-topright: 5px;
		-moz-border-radius-bottomleft: 5px;
		-moz-border-radius-bottomright: 5px;
	}
	#pageHeader * {
		background-color: #606060 !important;
	}

	#topNav li {
		background: none !important;
	}
	/*#quickSearch,#quickSearch * {
		background-color: #606060 !important;
	}
	#quickSearch #qinput {
		border:1px solid #808080 !important;
	}*/

	div#pageBody, div.pageBody {
		margin-top:47px !important;
	}
	div#sideBar {
		margin-top:48px !important;
	}
	#sideBar {
		background: none !important;
	}

	#nav li {
		border-bottom: solid 1px #404040 !important;
	}
	#nav>li>a {
		background-color: transparent !important;
		color: #e0e0e0 !important;
		border-width: 0 0 1px 0 !important;
		border-top: 0px !important;
		border-bottom: solid 1px #404040 !important;
	}
	#nav>li>a:hover {
		background-color: #484848 !important;
		color: #ffffff !important;
		border-bottom: solid 1px #606060 !important;
	}

	.moreDiv {
		border-top: solid 1px #808080 !important;
	}

	#banner1,#banner2 {
		display: none !important;
	}

	a {
		color: #e0e0e0 !important;
		background: none !important;
	}

	#userProfile {
	background: none transparent !important;
	}

	#rateLeft{
		background-color: #484848 !important;
	}

	.flexHeader, .feedDay {
		background-color: #484848 !important;
		border-bottom: solid 1px #606060 !important;
	}

	.pageList a {
		border-bottom:2px solid #404040 !important;
	}
	.pageList a:hover {
		border-bottom:2px solid #606060 !important;
	}

	.tBar {
		border-bottom: solid 1px #808080 !important;
	}
	ul.tabs li {
		background: none transparent !important;
		border: 0 !important;
	}
	.tabs > li > a {
		background: transparent none;
	}
	.tabs > li > a > b {
		visibility: hidden !important;
	}
	.tabs > li.activeLink > a {
		background-color: #606060 !important;
		border-bottom: solid 1px #a0a0a0 !important;
		margin-bottom: 2px !important;
	}
	.tabs > li:hover > a {
		background-color: #484848 !important;
		border-bottom: solid 1px #606060 !important;
	}

	#wrap1,#wrap2,#wrapH,#wrapHI {
		border-left:0px solid !important;
		border-right:0px solid !important;
		border-color: #808080 !important;
	}
	.newRow {
		background-color: #484848 !important;
	}
	#header {
		background-color: #808080 !important;
		background-image: none !important;
		border-left: 0 !important;
		border-right: 0 !important;
		border-top: 1px solid #404040 !important;
		border-bottom: 1px solid #a0a0a0 !important;
	}
	#header h1 {
		background-color: #808080 !important;
	}
	#header a {
		color:#ffffff !important;
	}

	#content,#friendsCont {
		border: 0 !important;
	}
	.result,.clearFix {
		border: 0 !important;
	}

	#checkboxFeed {
		border-bottom:1px solid #808080 !important;
	}

	.feedPhotos a img {
		border: 1px solid #606060 !important;
	}
	.feedPhotos a:hover img {
		border: 1px solid #808080 !important;
	}

	.mailbox table td {
		border-bottom:1px solid #808080 !important;
	}

	.postHeader {
		border-top: 0 !important;
	}

	#notesBar, #mainPanel {
		background: transparent none !important;
	}

	.privacy_panel_editor {
		border: 0 !important;
	}
	.privacy_panel_border {
		border:10px solid #484848 !important;
	}

	input[type="text"],input[type="password"] {
		border:1px solid #808080 !important;
	}

	#startQuote, #endQuote {
		background: transparent none !important;
	}

	.buttonRow {
		border: 0 !important;
	}
	.buttonRow a {
		background: none #484848 !important;
		border: 1px solid #606060 !important;
		-moz-border-radius-topleft: 2px;
		-moz-border-radius-topright: 2px;
		-moz-border-radius-bottomleft: 2px;
		-moz-border-radius-bottomright: 2px;
	}
	.buttonRow b {
		background: none transparent !important;
		margin: 0 !important;
		padding: 0 !important;
		border: 0 !important;
	}

	ul.nNav {
		border: 0 !important;
		margin: 0 !important;
	}
	ul.nNav .nc {
		display: none !important;
		border: 0 !important;
		margin: 0 !important;
	}
	ul.nNav a {
		background: none #484848 !important;
		border: 1px solid #606060 !important;
		-moz-border-radius-topleft: 2px;
		-moz-border-radius-topright: 2px;
		-moz-border-radius-bottomleft: 2px;
		-moz-border-radius-bottomright: 2px;
		padding: 4px 10px !important;
		margin: 0 !important;
	}

	#audioBar, .profile, #group.profile, .notesEdit {
		background: transparent none !important;
	}

	input[type="text"], input[type="password"], textarea {
		color: #202020 !important;
		background-color: #808080 !important;
	}
}
