css:
@-moz-document domain("teknoseyir.com") {.navbar-default {
    background: #ab1500;
    border-bottom: 2px solid #991300;
}
.navbar-default .navbar-nav>li>a {
    color: #eee;
    transition: all 500ms;
}
.navbar-default .navbar-nav>.open>a {
    background: #ab1500;
    color: #eee;
    transition: all 500ms;
}
.navbar-default .navbar-nav>.open>a:focus,
.navbar-default .navbar-nav>.open>a:hover {
    background-color: #991300;
    color: #fff;
}
.navbar-default .navbar-nav>li>a:hover {
    background-color: #991300;
    color: #fff;
}
.nav-icons>li>a {
    color: #eee;
}
.nav-icons>li>a {
    padding: 15px 20px;
    transition: all 500ms;
}
.nav-icons>li>a:hover {
    background-color: #991300;
    color: #fff;
}
.nav-icons>.open>a {
    background-color: #991300;
    transition: all 500ms;
    color: #fff;
}
.navbar-default .nav-icons>.open>a:focus,
.navbar-default .nav-icons>.open>a:hover {
    background-color: #991300;
    color: #fff;
}
.nav-icons>li>.dropdown-menu {
    margin-top: 2px;
}
.menu-drop {
    top: 52px
}
#sayfa-tepe {
    width: 40px;
    height: 40px;
    background-color: #ab1500;
    border-radius: 50%;
    border: 2px solid #991300;
}
#menu-drop {
    background-color: #991300;
}
#menu-drop .sub-menu {
    background-color: #991300;
}
#menu-drop .sub-menu a.active {
    background-color: #840f00;
}
.navbar-right ul.dropdown-menu {
    background-color: #991300;
}
.navbar-right ul.dropdown-menu a:hover {
    background-color: #840f00;
}
.navbar-right ul.dropdown-menu li.divider {
    background-color: #840f00;
}
.navbar-right #bildirim_liste .bildirim {
    background-color: #991300;
    border-bottom-color: #840f00
}
.navbar-right .bildirim_tumu a {
    background-color: #840f00;
}

.col-move{
	display: none!important;
}

.home_videos_inner{
	display: flex;
    justify-content: space-between;
}

.home-videos-left{
 	width: 60% !important;
}

.home-videos-left .vid_thumb img{
    height: 60vh!important;
    object-fit: cover;
}

.home-videos-left article{
	height:auto!important;
    border-radius:5px!important;
    overflow:hidden;
}

.home-videos-left article h1{
    white-space: nowrap;
  	overflow: hidden;
  	text-overflow: ellipsis;
}

.home-videos-right{
 	width: 40% !important;
	height:auto!important;
    background-color: transparent;
    border: none;
}

.home-videos-right .tabs{
    display: flex;
    justify-content: space-between;
    border-radius: 5px;
    overflow:hidden;
    margin-bottom: 5px!important;
}

.home-videos-right .tabs li{
	padding: 0px;
	width: 50%;
    display:flex;
    justify-content:center;
    border-bottom: 4px solid #111!important;
}

.home-videos-right .tabs li.active{
	border-bottom: 4px solid #ab1500!important;
}

.home-videos-right .tabs li a{
	width: 100%;
    display: block;
    background-size: 60%!important;
	background-position: 10% center !important;
}

.home-videos-right .tabs li a[title="OtoSeyir"]{
	background-position: 90% center !important;
}

.home-videos-right .articles{
	display:flex;
    height: 61.5vh !important;
    flex-wrap: wrap;
}

.home-videos-right .articles article{
	height: auto !important;
    width: calc(50% - 5px) !important;
    padding: 0 !important;
    display: flex;
    flex-direction: column;
    margin-bottom: 10px;
    border-radius: 5px;
    overflow:hidden;
}

.home-videos-right .articles article .social_badge{
    position: static !important;
    display:flex;
    justify-content: space-between;
    align-items: baseline;
    margin:0 !important;
    padding: 5px 10px !important;
    height: auto !important;
    background-color: rgba(255,255,255,0.05);
}

.home-videos-right .articles article .social_badge a,
.home-videos-right .articles article .social_badge .stats{
    margin:0 !important;
    padding: 0 !important;
}

.home-videos-right .articles article:nth-child(odd){
	margin-right: 10px;
}

.home-videos-right .articles article > a{
    display: flex;
    flex-direction: column;
}

.home-videos-right .articles article > a .vid_thumb{
	margin:0px !important;
}

.home-videos-right .articles article > a img{
	width: 100% !important;
}

.home-videos-right .articles article > a h1{
	padding: 0px 8px;
    margin: 10px 0px;
    white-space: nowrap;
  	overflow: hidden;
  	text-overflow: ellipsis;
}

@media (max-width: 768px) {
	.home-videos-left .vid_thumb img{
    	object-fit: contain;
	}
    
    .home-videos-right .articles article{
    	width: 100% !important;
   		margin-right: 10px;
	}
    
    .home-videos-left {
    	width: 70% !important;
        margin-right: 10px;
	}
    
    .home-videos-right {
    	width: 30% !important;
	}
}






}
