css:
@-moz-document domain("movienight.ws") {
#contenedor {
    background: #101010;
}
ul.home_links li.current-menu-item
 a {
    color: #fff;
}
ul.home_links
 li {
    border-bottom: solid 3px #043a3c;
}
#contenedor .items .it_header
 h1 {
    color: #ffffff;
}
#contenedor .items
 .it_header {
    border-bottom: 2px solid #4cbfda;
}
.header_slider .customNavigation
 a.btn {
    border: solid 1px #4cbfda;
    color: #fff;
    background-color: #4cbfda;
    border-radius: 3px;
}
#contenedor .items .it_header .buscador input[type='text'] {
    background: #1d1d1d url(https://cdn1.iconfinder.com/data/icons/hawcons/32/698627-icon-111-search-128.png) 10px 50% no-repeat;
    border: solid 2px #838383;
    color: #e4e4e4;
    border-radius: 3px;
    background-size: 20px;
}
#owl-demo2 .item span.ttps, #series .item
 span.ttps {
    color: #ffffff;
}
.header_slider
 span.titulo_2 {
    color: #c8c8c8;
}
.items .movie
 h2 {
    color: #ffffff;
}
.random {
    border-bottom: 1px solid transparent;
}
#contenedor
 .sidebar_right {
    background: #171717;
    box-shadow: inset 1px 0 0 0 rgba(0, 0, 0, 0.2);
}
#contenedor .sidebar_right
 .footer {
    background: #212020;
    color: #8C8C9C;
}
ul.totales
 li {
    background: #171717;
    border: solid 1px #171717;
}
.sidebar_right .links ul.lista
 li {
    border-bottom: solid 2px #212020;
}
.sidebar_right .links ul.lista li
 b {
    background: #444444;
	color: #d0d0d0;
}
.sidebar_right .links ul.lista li
 a {
    color: #a3a3a3;
}
.relacionados .movie-r .data-r
 span.rating {
    background: #444444;
    color: #d0d0d0;
}
.relacionados .movie-r .data-r
 h4 {
    color: #bfbfbf;
}
.sidebar_right .links
 h3 {
    color: #ffffff;
    font-size: 24px;
    text-align: center;
    margin-top: 5px;
    padding: 20px;
}
.ptts {
    background: #101010;
}

#movie .post
 .datos {
    background: #101010;
}
#player-container {
    background: #101010;
}
.player-menu {
    background-color: #171717;
    padding: 10px;
    width: 690px;
    border-radius: 3px;
}
.footer_c
 a.top {
    background: #212020;
    border: 1px solid #212020;
}
.footer_c {
    border-top: solid 1px #212020;
}
.comentarios {
    display: none;
}
/* Scrollbar */ 

::-webkit-scrollbar{width: 10px;}

::-webkit-scrollbar-thumb{background-color:rgb(51, 0, 51); border-radius: 0;}
::-webkit-scrollbar-thumb:hover{background-color:rgb(33, 32, 32);}

::-webkit-scrollbar-track{background-color:rgb(16, 16, 16);}


/* End Scrollbar */
.header_slider .customNavigation a.btn:hover {
    color: #d8d8d8;
}
ul.years li
 a {
    color: #8d8d8d;
    background: #212020;
}
.ui.label.clip.orange {
    opacity: 0.9;
}
.ui.label.clip {
    padding: 0px 12px;
    bottom: 3px;
    border-top-right-radius: 3px;
    border-bottom-right-radius: 3px;
}
.place_top
 span {
    color: #fff;
    background: #4cbfda;
    border-radius: 3px;
}
.place_top {
    border-bottom: 2px solid #4cbfda;
}
#contenedor .items .it_header .buscador input[type='text']:focus {
    border: 2px solid #4cbfda;
}
#largo .contenido .header input[type='text'] {
    border-radius: 3px;
}
#largo .contenido .header input[type='text']:focus {
    border: 1px solid #4cbfda;
}
ul.home_links li a:hover {
    color: #b7b7b7;
}
ul.years li a:hover {
    color: #fff;
    background: #4cbfda;
    transition: .2s all ease-in-out;
}
ul.totales li
 i {
    color: #4cbfda;
}
ul.leftmenu li.current-menu-item a i {
    color: #4cbfda;
}
ul.leftmenu li.current-menu-item a
 i {
    color: #4cbfda;
}
}
