css:
@namespace url(http://www.w3.org/1999/xhtml);

/* Allgemein  */
@-moz-document url-prefix("https://vertec.cocus.com") {
  /* */
  #pageheader {
        width: 90%!important;
      margin-left: 7%;
      left: 0;
  }
  #content {
     width: 90%!important;
  }
  body {
        font-size: 0.9em!important;
  }
  #navigation {
    bottom: auto!important;
      width: 6.5%;
  }
  .navi > li {
        padding-top: 5px;
        padding-bottom: 5px;
  }
    #content {
        left: 7%;
    }
}


/* Leistungsliste */
@-moz-document  url-prefix("https://vertec.cocus.com/service_list/") {
   .plainlist {
        width:100% !important;
   }
   #ext-gen17 {
        width:100% !important;
   } 
    
    .list ,     td.firstcol {
        border-collapse: unset !important;
    }
    
    /* Drop Down Resize */
    #ext-gen12 {
        width: 60% !important;
    }
    
    #ext-gen13 {
        width: 100% !important;
    }
    div.x-combo-list-item {
        width: 100% !important;
        
    }
    
    
    .x-combo-list-item > .comboDiv {
        width: 33% !important;
        font-size: 0.9em;
    }
    
    
    div.x-combo-list-item > div:nth-child(3) {
        float:none !important;;
    }
    
    .xsc {
        height: auto;
    }
    
}

/* LeistungsÃ¼bersciht */
@-moz-document url-prefix("https://vertec.cocus.com/service_overview/?") {
    .list  {
        width: 100% !important;
    }
    
    .even > td {
        background: lightyellow!important;
    }
    td {
        padding-bottom: 0.5em!important;
        padding-top: 0.5em!important;
    }
   
}



/* Meine Projekte */
@-moz-document url("https://vertec.cocus.com/responsible_phase/") {
    .header > td {
        border: 1px dashed!important;
        font: 1.3em bold!important;
    }
    .even > td {
        background: lightyellow!important;
    }
    .header > td:nth-child(1) {
        width: 20% !important;
    }
    .header > td:nth-child(2) {
        width: 20% !important;
    }
    .header > td:nth-child(3) {
        width: 50% !important;
    }
    /* Zeitraum von, Zeitraum bis Aufwand geplant Aufwand effectiv , Restbudget */
    .header > td:nth-child(4),
    td:nth-child(5),
    td:nth-child(6),
    td:nth-child(7),
    td:nth-child(8) {
        font-size: 0.9em !important;
    }
    td {
        padding-bottom: 0.5em!important;
    }
    /* */
    .even td:nth-child(3) {
        font-style: italic !important;
        font-size: smaller !important;
    }
    .uneven td:nth-child(3) {
        font-style: italic !important;
        font-size: smaller !important;
    }
}
