css:
@-moz-document regexp("^http://live\\.bilibili\\.com/\\d+(.+)?") {
.announcement-container,
.live-ad-full,
.super-gift-ctnr,
.gift-msg-1000,
.gift-msg,
.live-haruna-ctnr,
/*.chat-msg >:nth-last-child(n+4):not(.admin),*/
/*[[tx]]*/
/*[[xz]]*/
.system-msg > a,
.system-msg > a ~ span,
.splashing,
.row-item:not(.bili-link),
.body-merge,
#profile-ctrl,
#rank-list-ctnr
{
    display: none !important;
}
#chat-msg-list
{
    height: 100%;
    padding: 0px;
}
div.system-msg
{
    padding-top: 0px !important;
    padding-bottom: 0px !important;
}
#chat-list-ctnr,
.bk-img
{
    background-image: none !important;
}
.chat-list-ctnr
{
    height: calc(455px + 24px + 128px - 5px - 5px - 5px);
}
body.player-full-win .chat-list-ctnr
{
    height: calc(100% - 128px - 150px + 24px + 128px - 5px - 5px - 5px);
}
.treasure-box-ctnr
{
    left: 10px;
    bottom: -89px !important;
}
.live-top-nav-ctnr,
.head-info-panel,
.chat-list-ctnr,
.chat-ctrl-panel,
.live-top-hover-panel,
.room-body .main-section,
.live-footer,
.level-hint-panel,
.video-block,
.video-props,
.help-center,
.room-left-sidebar,
.ctrl-panel,
.room-silent-merge,
.gift-info-panel,
.gifts-package-panel,
.live-hover-panel,
.user-level-info,
.panel-content,
input.live-input,
body.live-room-body,
#head-info-panel>.treasure-info
{
    background-color: #2f2f2f !important;
}
textarea,
input.live-input
{
    color: #DDD !important;
}
.chat-ctnr
{
    padding-top: 5px !important;
}
#chat-list-ctnr
{
    padding: 5px;
}
.room-title,
.video-title,
.icon-span,
.user-name-text,
.capsule-toy,
.live-footer-links dd a,
.live-footer-info .live-footer-right .icon-item .icon-text,
.room-left-sidebar .small-wrap
{
    color: #999 !important;
}
}
