css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.koreus.com") {

/**************************************/
/* MODIFICATION SUR LE MENU DE GAUCHE */
/**************************************/
	
	/* Vignette menu jaune/orange */
	td#leftcolumn div.blockTitle{
	  color: #306;
  	background: linear-gradient(#fbe38b, #fecc53);
		padding: 4px 3px 4px 3px;
  }
	
	/* Suppression du décalage des TD dans Menu Utilisateur */
	td#usermenu{
		padding: 0px;
	}
	/* Suppression des marges des blocs des menus */
	td#leftcolumn div.blockContent{
	  padding: 0px;
	}
	
	/* Changement sur les cases|liens du Menu Utilisateur */ 
	td#usermenu a{
    transition: background 0.3s;
		padding: 2px 0px 2px 6px;
		border-right: 0px;
		border-color: #cccccc; 
	}
	
	/*changement taille liste membres*/
	td#leftcolumn div.blockContent{
		font-size: 10px;
	}
	
	td#mainmenu {
	  padding: 0px;
	}
	td#mainmenu a{
    transition: background 0.3s;
		padding: 2px 0px 2px 6px;
		border-right: 0px;
	}
	td#mainmenu a.menuMain{
		border-right: 0px;
		padding-left: 6px;
		
	}
	td#mainmenu a.menuTop{
		border-right: 0px;
	  border-bottom: 1px solid #cccccc;
		padding-left: 6px;
	}
	td#mainmenu a.menuSub{
		border-right: 0px;
		background-color: #E0DFE7;
		border-left: 10px solid #cccccc;
		border-color: #cccccc;
		
		padding-left: 8px;
	}
	td#usermenu a:hover{
		background-color: #FFF;
		box-shadow: 0px 0px 5px 3px rgba(0,0,0,0.2);
	}
	td#mainmenu a.menuSub:hover,a.menuTop:hover,a.menuMain:hover{
    background-color: #FFF;
		box-shadow: 0px 0px 5px 3px rgba(0,0,0,0.2);
	}
	
	td#leftcolumn div.blockContent ul{
	  padding: 0px;
		margin: 0px;
	}
	td#leftcolumn div.blockContent li{
		margin: 0px;
		transition: background 0.3s;
		padding: 2px 0px 2px 6px;
		border-bottom: 1px solid #cccccc;
		list-style: none;
	}
	td#leftcolumn div.blockContent li:hover{
		background-color: #FFF;
		box-shadow: 0px 0px 5px 3px rgba(0,0,0,0.2);
	}
	
	
/**************************************/
/* MODIFICATION SUR LE CORPS DU FORUM */
/**************************************/
	td#centercolumn div#content table.outer tbody tr.odd td{
	  vertical-align: middle;
	}
	td#centercolumn div#content table.outer tbody tr.even td{
	  vertical-align: middle;
	}
  table.index_category tbody .head{
  	background: linear-gradient(#BEBEE4, #9A9ACE);
	  font-weight: bold;
		box-shadow: 0px 2px 6px 0px rgba(0,0,0,0.15);
		height: 25px;
  }
	table.index_category td{
		padding: 3px 3px 3px 3px;
		vertical-align: middle;
	}
  
	.item{
		margin-left: 10px;
	}
	.itemFoot{
		margin-left: 10px;
	}
	
	td[width="4%"], td[valign="middle"]{
		vertical-align: middle;
	}
	
	/* suppression des espaces entre les forums et sous-forum*/
	table.index_category{
		margin-top: 0px;
		margin-bottom: 0px;
	}
	
/*suppression des lignes grises sur les cases du forum*/
	td#centercolumn div#content div#cat_1{
		background-color: #fff;
	}
/*recherche*/
	div[style="margin-left: auto; margin-right: auto;text-align:center; width:500px"]{
		margin-left: auto !important;
		margin-right: 20px !important;
		text-align: right !important;
		width: 500px !important;
	}
	
/* les menus déroulant */
	select{
    background-color: #FFF;
		border-top-left-radius: 4px;

    box-shadow: 0px 2px 6px 0px rgba(0,0,0,0.15);
    z-index: 1;
		border: 0px !important;
		cursor: pointer;
	}
	div.dropdown .menu, div.dropdown .userbar{
		padding-left: 0px;
		text-align: center;
	}
	
	table.outer > tbody > tr > th > div{
		padding-left: 10px;
	}
	
/*page principale forum*/
		#mainoption option{
			box-shadow: 0px 1px 6px 0px rgba(0,0,0,0.15);
			padding: 5px 5px;
		}
	table.outer{
		border: 0px;
	}
	/* espacement entre les posts*/
	#content > br[clear="all"]{
	}

	table.outer > tbody > tr:nth-child(4){
		box-shadow: 0px 6px 4px 0px rgba(0,0,0,0.15);
	}
	table.outer > tbody > tr:nth-child(1) > th{
		padding-right: 10px;
	}
	
	div#content{
		margin-left: 10px; 
	}
	
/* dans un sous-forum */
		#forumoption option{
			box-shadow: 0px 1px 6px 0px rgba(0,0,0,0.15);
			padding: 3px 5px;
		}
	#content > div:nth-child(5){
		padding-left: 20px;
	}

	#content div.dropdown #topicoption{
		margin-left: 20px; 
	}
	#content > div:nth-child(13){
		padding-left: 20px;
	}
	
/* Dans un topic */
  table.outer > tbody > tr:nth-child(4) {
		border-bottom-right-radius: 15px;
		border-bottom-left-radius: 15px;
	}
	table.outer > tbody > tr{
		border-bottom-right-radius: 0px;
		border-bottom-left-radius: 0px;
	}
	table.outer > tbody > tr:nth-child(4) > td:nth-child(1) {
		border-bottom-left-radius: 15px;
	}
	table.outer > tbody > tr:nth-child(4) > td:nth-child(2) {
		border-bottom-right-radius: 15px;
		padding-right: 10px;
	}
	table.outer > tbody > tr:nth-child(1) > th:nth-child(1) {
		border-top-left-radius: 15px;
	}
	table.outer > tbody > tr:nth-child(1) > th:nth-child(3) {
		border-top-right-radius: 15px;
	}
	td#centercolumn div#content table.outer tbody tr.odd td{
		border-bottom-right-radius: 0px;
		border-bottom-left-radius: 0px;
	}
	td#centercolumn div#content table.outer tbody tr.odd{
		border-bottom-right-radius: 0px;
		border-bottom-left-radius: 0px;
		box-shadow: none;
	}

/* En Article */
	/* onglet pseudo du poster*/
	span.itemPoster{
	 padding: 5px 1px 4px 5px;
		margin-right: 5px;
		background-color: #E0DFE7;
		border-top-left-radius: 10px;
		border-top-right-radius: 10px;
	}
	
	/* contour article */
	div.item{
		border-left: 1px solid rgb(200, 200, 200);
		border-right: 1px solid rgb(200, 200, 200);
		border-top-left-radius: 10px;
		border-top-right-radius: 10px;
		border-bottom-width: 1px;
		border-bottom-color: #E0DFE7;
	}
	div.itemHead{
		border-top-left-radius: 10px;
		border-top-right-radius: 10px;
		border-top-width: 1px;
	}
	div.item > div > h1{
		padding-left: 10px;
	}
	div.itemFoot{
		border-bottom-left-radius: 10px;
		border-bottom-right-radius: 10px;
		border-bottom-width: 1px;
		box-shadow: 0px 6px 4px 0px rgba(0,0,0,0.15);
		background-color: white;
	}
	div.itemFoot > span{
		padding-right: 10px;
	}

	/*décalage des articles: partie droite*/
	#centercolumn{
		padding-right: 10px;
	}
	
	/*décalage contenu d'un article vers la droite*/
	div.item > p{
		padding-left: 10px;
	}
	
	/* image thumb article*/
	div.item > p > a > img{
		background-color: #9A9ACE;
    display: inline-block;
    border: 0px;
    border-radius: 4px;
    padding: 5px;
    transition: 0.3s;
	}
	div.item > p > a > img:hover{
		box-shadow: 0px 0px 2px 1px rgba(0, 0, 0, 0.5);
		background-color: white;
	}
	
	/* annulation du décalage du premier article */
	div#content{
		padding: 0px;
	}
	
/*dans un edit de topic */
		#subject_pre, #messageColor, #messageFont, #messageSize, #newbb_form, select[name="add"], #\$dates_msg, #limit{
			box-shadow: 0px 1px 6px 0px rgba(0,0,0,0.15);
			padding: 2px 5px;
			border-top-left-radius: 0px;
		}
	
	/* les inputs */
	input[type="text"], textarea{
    box-shadow: 0px 2px 6px 0px rgba(0,0,0,0.15);
    z-index: 1;
		border: 0px !important;
		min-height: 21px;
	}
	 input[type="checkbox"]{
		 box-shadow: 0px 2px 6px 0px rgba(0,0,0,0.15);
    z-index: 1;
		border: 0px !important;
		cursor: pointer;
	}
	
	input[type="submit"],input[type="button"],input[type="reset"]{
		min-height: 21px;
		box-shadow: 0px 2px 6px 0px rgba(0,0,0,0.15);
    z-index: 1;
		border: 0px !important;
		color: #306;
		background-color: #FFF;
		cursor: pointer;
	}
	input[type="submit"]:hover,input[type="button"]:hover,input[type="reset"]:hover{
		box-shadow: 0px 2px 2px 0px rgba(0,0,0,0.5);
		background: none;
		background-color: #FFF;
	}
	input[value="Recherche"],[value="Rechercher"],[value="Valider"],[value="Ok !"],[value="Ok"]{
	  background: linear-gradient(#fbe38b, #fecc53);
		font-weight: bold;
		cursor: pointer;
	}
	input#contents_submit:hover,input[value="Recherche"]:hover,input[value="Rechercher"]:hover,input[value="Valider"]:hover,input[value="Ok !"]:hover,input[value="Ok"]:hover{
	  background: linear-gradient(#fbe38b, #fecc53);
	}

	/*bouton dans la messagerie*/
	input#move,input#lu,input#nlu,input#del,input[value="?"],input[name="sa"],input[value="Citation"],input[value="Réponse Rapide"],input[value="Vider"],input[value="Prévisualiser"],input[value="Soumettre"],input[value="Ajouter"]{
    background: none;
		background-color: #FFF;
		margin-bottom: 2px;
		cursor: pointer;
	}
	td#centercolumn div form input[name="sa"]{
		background: linear-gradient(#fbe38b, #fecc53);
		font-weight: bold;
		font-size: 12px;
	}
	input#reply{
		background: linear-gradient(#fbe38b, #fecc53);
		margin-bottom: 2px;
		cursor: pointer;
		font-weight: bold;
	}
	
/*************************/
/* MODIFICATION D'IMAGES */
/*************************/

	img[src="http://static.koreus.com/images/logo.jpg"]{
			height: 0 !important;
			width: 0 !important;
			padding-left: 368px !important;
			padding-top: 80px !important;
			background: url(http://k.img.mu/GYD7KO.png) no-repeat !important;
	}

	img[src="images/plus.png"]{
			height: 0 !important;
			width: 0 !important;
			padding-left: 12px !important;
			padding-top: 12px !important;
			background: url(http://k.img.mu/nBathd.png) no-repeat !important;
		cursor: pointer;
	}

	img[src="images/minus.png"]{
			height: 0 !important;
			width: 0 !important;
			padding-left: 12px !important;
			padding-top: 12px !important;
			background: url(http://k.img.mu/qyCcMR.png) no-repeat !important;
		cursor: pointer;
	}

	.arrow-down{
			height: 0 !important;
			width: 0 !important;
			padding-left: 16px !important;
			padding-top: 16px !important;
			background: url(http://k.img.mu/tnFYF6.png) no-repeat !important;
		cursor: pointer;
	}
	.arrow-down2{
			height: 0 !important;
			width: 0 !important;
			padding-left: 16px !important;
			padding-top: 16px !important;
			background: url(http://k.img.mu/eJJuLg.png) no-repeat !important;
		cursor: pointer;
	}
	.arrow-up{
			height: 0 !important;
			width: 0 !important;
			padding-left: 16px !important;
			padding-top: 16px !important;
			background: url(http://k.img.mu/u7vuIx.png) no-repeat !important;
		cursor: pointer;
	}
	.arrow-up2{
			height: 0 !important;
			width: 0 !important;
			padding-left: 16px !important;
			padding-top: 16px !important;
			background: url(http://k.img.mu/PNzh1i.png) no-repeat !important;
		cursor: pointer;
	}

	img[src="http://static.koreus.cdn.li/images/flag_red.gif"]{
			height: 0 !important;
			width: 0 !important;
			padding-left: 16px !important;
			padding-top: 16px !important;
			background: url(http://k.img.mu/iQrUBz.gif) no-repeat !important;
		cursor: pointer;
	}	
	
/* icones B I U D*/	
	
	img[src="//media.koreus.com/images/bold.gif"]{
			height: 0 !important;
			width: 0 !important;
			padding-left: 30px !important;
			padding-top: 21px !important;
			background: url(http://k.img.mu/6Jd7x2.png) no-repeat !important;
		box-shadow: 0px 2px 6px 0px rgba(0,0,0,0.15);
		cursor: pointer;
	}
	 img[src="//media.koreus.com/images/bold.gif"]:hover{
		 box-shadow: 0px 2px 2px 0px rgba(0,0,0,0.5);
	}
	img[src="//media.koreus.com/images/italic.gif"]{
			height: 0 !important;
			width: 0 !important;
		  margin-top: 10px;
			padding-left: 30px !important;
			padding-top: 21px !important;
			background: url(http://k.img.mu/422Io9.png) no-repeat !important;
		box-shadow: 0px 2px 6px 0px rgba(0,0,0,0.15);
		cursor: pointer;
	}
  	img[src="//media.koreus.com/images/italic.gif"]:hover{
			box-shadow: 0px 2px 2px 0px rgba(0,0,0,0.5);
	}
	img[src="//media.koreus.com/images/underline.gif"]{
			height: 0 !important;
			width: 0 !important;
			padding-left: 30px !important;
			padding-top: 21px !important;
			background: url(http://k.img.mu/ezaJZh.png) no-repeat !important;
		box-shadow: 0px 2px 6px 0px rgba(0,0,0,0.15);
		cursor: pointer;
	}	
  	img[src="//media.koreus.com/images/underline.gif"]:hover{
			box-shadow: 0px 2px 2px 0px rgba(0,0,0,0.5);
	}
	img[src="//media.koreus.com/images/linethrough.gif"]{
			height: 0 !important;
			width: 0 !important;
			padding-left: 30px !important;
			padding-top: 21px !important;
			background: url(http://k.img.mu/riRMkn.png) no-repeat !important;
		box-shadow: 0px 2px 6px 0px rgba(0,0,0,0.15);
		cursor: pointer;
	}
	  img[src="//media.koreus.com/images/linethrough.gif"]:hover{
			box-shadow: 0px 2px 2px 0px rgba(0,0,0,0.5);
	}
	
	
	
}
