@-moz-document domain("mail.yahoo.com"), domain("calendar.yahoo.com"), domain("addressbook.yahoo.com") {


  /* remove CSS3 transition effects for faster loading and interaction */
  * {
  	transition: none !important;
  }
  .YLogoMY{display:none!important;}
  /* remove elements */
  #yucs-top-bar, #masterNav, /* top bar with quick links */
  #yucs-logo, .YLogoMY, /* Yahoo logo */
  #yucs-web_search_button_wrapper, #UHSearchWeb, #web-search-btn, /* web search button in search bar */
  #theAd, /* right ad */
  #slot_TL1, #slot_mbrec
  {
    display: none;
  }
a#yucs-home_link{display:none!important;}
  /* top bar layer above main content */
  #UH {
    position: absolute;
    top: 0;
    left: auto;
    right: 0;
	  min-width: auto;
    height: 2.5em;
    width: 35% !important;
  }
  #uhWrapper {
    z-index: 100;
  }
  /* smaller search and better position */
  #uhWrapper > table {
    float: right;
    min-width: 600px !important;
  }
  #uhWrapper > table tr td:first-child {
    width: 0 !important;
  }
  .search-container, .search-box-inner {
    display: flex;
    flex-flow: row nowrap;
    align-items: center;
  }
  #Stencil .lt .typeahead-input-container {
    min-width: 200px;
  }
  
  /* adjust tab bar to top bar layer size */
  #shellcontent #tabs {
    margin-right: 40%;
  }

  /* remove margin right of main content */
  #shellcontent, .frost-container {
  	right: 0;
  }
  
  /* move main content to top */
  #main {
    top: 0 !important;
  }
  
  /* move main content back to right border */
  .wide-right-rail #shellcontent,
  .wide-right-rail .frost-container {
    right: 0;
  }
  
  /* right controls */
  #yucs .yucs-fl-right {
    margin-top: 7px !important;
  }
  
  /* less height in folder list to show more folders at once */
  .listnav .listnav-folder li {
    padding-bottom: 0px;
  }
  
  /* hide scrollbar in msg list if not needed */
  #msg-list .list-view-items {
    overflow-y: auto !important;
  }
  


.srp-filters-origin button[aria-pressed=true], .srp-filters-origin button[aria-pressed=true]:focus, .srp-filters-origin button[aria-pressed=true]:hover, .srp-filters-type button[aria-pressed=true], .srp-filters-type button[aria-pressed=true]:focus, .srp-filters-type button[aria-pressed=true]:hover{background:#1FA4C2;border:1px solid #1FA4C2;color:#fff;}
.subselected {background:#1FA4C2 !important;}
#shellinner {border-top:3px solid #1FA4C2;}
#shellnavigation{border-top:3px solid #1FA4C2;}
#shellinner {border-top:3px solid #1FA4C2;}
#nav .icon, .nav-tabs .nav-tab, .nav-tabs .icon{color:#1FA4C2;}
.has-tabs .nav-lnk.active, .nav-tab-li.active{background:#1FA4C2;}
.has-tabs .nav-lnk:hover, .nav-tab-li:hover{background:#1FA4C2;}
.expanded .list-view-item:focus, .keyboard-active .expanded .list-view-item:focus, .list-view .selected, .keyboard-active .list-view .selected, .expanded .list-view-item-master.selected, .keyboard-active .expanded .list-view-item-master.selected, .items li.cntxtmenu{background:#1FA4C2;color:#fff;}
.list-view .selected .contact-detail{color:#fff;}
body.notepad .ml-notepad .list-view-item.selected .note-details{color:#fff;}
body.notepad .ml-notepad .list-view-item.selected .note-name{color:#fff;}
body.notepad .ml-notepad .list-view-item.selected{background:#1FA4C2;}
.has-tabs .active .nav-lnk{background:#1FA4C2 !important;}
#newsfeed-filterbar{background:#1FA4C2 !important;}
.this-day, .select{background:#1FA4C2 !important;}
.select a {color:#dedede!important;}
.this-day a {color:#fff!important;}

html{background:url(http://i.imgur.com/fDHnyJW.png)!important;}
.threadpane{background:transparent!important;}
.pc.calendar #shellnavigation{background:url(http://i.imgur.com/fDHnyJW.png)!important;}
#main, #paneshell, #shellcontent, #shellnavigation, #theAd {background:url(http://i.imgur.com/fDHnyJW.png)!important;}
.bg-container{background-image:url(http://i.imgur.com/fDHnyJW.png)!important;}
#calList{background:url(http://i.imgur.com/fDHnyJW.png)!important;}
.content {background:url(http://i.imgur.com/fDHnyJW.pngj.png)!important;}
#shellcontent {background:/*url(http://i.imgur.com/fDHnyJW.png)*/ #fff  !important;}
.commontasks{background:url(http://i.imgur.com/lyOftxY.png)!important;}
li.compose{background:url(http://i.imgur.com/lyOftxY.png)!important;}
.switcher, .list-view, .listnav-label{background:url(http://i.imgur.com/lyOftxY.png)!important;}
#toolbar{background:url(http://i.imgur.com/UuVBL4f.png)!important;}
.listnav-outter, .attribution-bar, #shellnavigation {border-right:1px solid #ddd!important;}
.threadpane{border-left:1px solid #ddd!important;}
.list-view-item-date-label {background:#eee!important;}
