.nav-item .active nav-lnk{background:url(http://i.imgur.com/972sn0h.png);}
#storm-listnav li.selected{background:#eee;}
#nav{transition:none!important;}
#nav .icon{opacity:1!important;}
.compose{border-bottom:1px solid #ccc;}
#mail-search-btn, #web-search-btn{background-color:#1fa4c2!important;box-shadow:none!important;}
#Stencil .right-folder-dd .search-box-inner{border:1px solid #ddd!important;height:28px;}
#newsfeed-filterbar{top:0!important;}
li.newsfeed-mb-ad{display:none!important;}
}
