    padding: 0 0 5px 0 !important;
}


/* закругления */

.b-feed-panel, 
.b-social, 
.b-community-header,
.b-user-menu,
.note_wrap,
.community-search,
div.b-user-menu__main,
.menu-block,
.menu-block-title,
.menu-best-comm ,
