.login_f {border-radius:5px}

/* Тени */

.lo1gin_f.menu-block, .b-u1ser-menu {box-shadow: 0 1px 5px #ccc;}

/*  */

#wrap {max-width: 1210px !important; margin: 0 auto !important}


.b-comment__content,
.b-comment__content p {
    font-size: 14px;
}
.b-promo-wrapper,
.b-promo-wrapper + div {
    height: 0 !important;
}
.b-comments_type_main {
    background: white;
    border: 0px solid #ddd ;
    border-radius: 0px;
    margin-top:5px;
    width:auto !important;
    padding: 25px;
}


/* пост */
/* ------------------------- */
/* ------------------------- */


.story__main {
    background: white;
    position: relative;
    box-shadow: 0 1px 5px #ccc;
    box-sizing: content-box;
    margin: 0 20px 56px 0;
    max-width: 800px;
    padding: 0px;
    width: 800px;
    border-radius:3px;
}

.story__wrapper {
    margin: 0;
}
.story__left { margin: 0 5px 0 0px; }

.story-search {margin-top: 14px; margin-bottom: 10px;}
.story__scroll-wrapper_enabled:before {display: none;}

.b-story__content_type_text,
.b-story-blocks {
    color: #555;
    line-height: 1.4;
}

.b-story-blocks {
    border: none;
    border-radius: 0;
    font-size: 15px;
    width: 100%;
}
.b-story-blocks_with-border,
.b-story__content_type_text {
    background-color: #FFF;
    width: auto;
    border:none;
    padding:0;
}

.b-story__content_type_text { font-size: 17px; }


/* хедер поста */
/* ------------------------- */

.story__header {
    box-sizing: border-box;
    margin-bottom: 10px;
    padding: 20px 23px 5px 23px;
    position: static;
}
.story__header-additional {
    font-size: 13px;
    margin: 5px 0 0 0;
    max-width: 100%;
    line-height:20px;
    color:#bbb;
}

.story__scroll_enabled {
      top: 38px;
    
}


.story__toggle-button {display:block; margin: 10px auto; width:36px; height: 17px;}
.story__toggle-button i {opacity:0.5; vertical-align:baseline;   box-shadow: inset 0 0 0 1px white;}

.story__scroll_enabled i {opacity:0.8; box-shadow: inset 0 0 0 1px white, 0 1px 5px #ccc;}
.story__toggle-button:hover i {opacity:1; box-shadow:none}

.story__header-additional .story__toggle-button {display:none;}

.story__header-additional-wrapper {
    display: inline;
}


.story__title-link {
    font-size: 24px;
    margin-right:3px;
}

.story__title-link_visited_yes { color: #666;}

.story__authors {text-decoration: none; color:#aaa}
.s1tory__authors {
    display: inline-block;
    color: transparent;
    font-size:0;
}

.s1tory__authors:after { 
    content:'моё'; 
    color: white;
    font-size:11px;
    display: inline-block;
    line-height: 1;
    padding:1px 3px;
    background: #c8a374;
    border-radius: 3px;
    transition:background-color 0.2s ease;
    vertical-align:middle;
}

.story__authors:hover:after {background: #dea55d;}

.story__author, .story__header-additional-wrapper a, .info_c a {
    color: #77ad5f !important;
    text-decoration: none !important;
	}
.story__to-comments:hover,
.story__author:hover, .story__header-additional-wrapper a:hover {
    color: #67bb58 !important;
}

.story__user-icon {display:none}

/* футер поста */
/* ------------------------- */

.story__main:after {
    content:'';
	position:absolute; 
    bottom:-41px; 
    height:42px; 
    background: #f7f7f5;
    b1ackground: linear-gradient(to top, #f7f7f5, #f7f7f5, #f7f7f5, #F2F2F0);
    left:0; right:0;
    box-shadow: 0 1px 5px #ccc;
    z-index:0;
    border-radius:0 0 2px 2px;
}


.story__footer {
    padding: 0 0;
    position:absolute;
    bottom:-29px;
    left:145px;
    width:auto;
    z-index:1;
}

.story__save {
    right:20px;
}
.story__save,
.story__share,
.story__share-buttons {
    position: absolute;
    bottom: -27px;
    z-index:1;
}

.story__share {display: none;}

.story__share-buttons {
    line-height: 10px !important;
    display: block !important;
    right: 40px;
}

a.story__to-comments {
    display: block;
    position: absolute;
    bottom: -30px;
    font-size: 14px;
    font-weight:normal;
    left: 20px;
    z-index:1;
    color: #999 !important;
  }

.b-story__show-all {
    transition: background-color 200ms;
    padding: 10px 20px;
    color: #949596!important;
    line-height: 32px;
    background: rgba(242,242,237, 0.9);
    background: linear-gradient(to top, rgba(240,240,235,1), rgba(240,240,235,0.93), rgba(240,240,235,0.75));
    position: absolute;
    bottom: 0px;
    top:auto;
    left: 0;
    right: 0;
    margin-bottom: 0px;
    font-size: 16px;
}

.b-story__show-all:hover { background-color: rgb(239,239,232); }

.b-story-blocks__wrapper_slice_yes:before {
    height: 0px;
    display:none;    
	}

/* отступы в посте */

.b-story__content img, .b-video, .b-video iframe {width: 100% !important; height:auto;}
.b-video, .b-video iframe {height: 445px !important;}

.b-story-block_type_image img,
.b-story__content_type_media img,
.b-story-block_type_image canvas,
.b-story__content_type_media 	{ max-width: none; }

.b-story-block_type_text .b-story-block__content,
.b-story__content_type_text  		{ margin: 10px 23px; }

.b-gifx 							{ margin: 0 23px; }


.b-story__content_type_media .b-gifx,
.b-story__content.b-story__content_type_text,
.b-story-block_type_image:last-child .b-gifx {margin-bottom:20px !important;}

.b-story__content {padding-top:0}

.b-story-block_type_text:last-child  {margin-bottom:10px;}

.b-story-block_type_video + .b-story-block_type_text,
.b-story-block_type_image + .b-story-block_type_text,
.b-story-block_type_text + .b-story-block_type_image,
.b-story-block_type_text + .b-story-block_type_video,
.b-story-block_type_text + .b-story-blocks__hide-wrapper,
.b-story-blocks__hide-wrapper > .b-story-block_type_text {margin-top:10px;}

.b-story-block__content_margin {margin:0}
.b-story__content_type_media  {padding:0;}
.b-story__content.b-story__content_type_text {padding-bottom:0}

.b-story-block_type_image:last-child,
.b-story-block:last-child .b-story-block__content_margin
 {margin-bottom:0 !important}

.b-story__content_type_media .b-gifx,
.b-story__content.b-story__content_type_text,
.b-story-block:first-child .b-story-block__content,
.b-story-blocks__wrapper > .b-story-block_type_image:first-child .b-gifx  {margin-top:0px;}

.page-story { margin-top: 15px;}


/* теги */

.story__tags {
    padding-left:0px;
    display: inline-block;
    vertical-align: bottom;
}
.story__tags a {
    background: white;
    border: none;
    box-shadow: inset 0px 0px 2px #bbb;
    color: #999 !important;
    font-size: 12px;
    margin: 0;
    padding: 1px 5px 3px 5px;
    font-weight:normal;
}
.tag {
    background: white;
    color: #888;
    padding: 1px 6px 2px 6px;
    font-size: 13px;
    border-radius: 2px;
    border:none;
}
.tag:hover {border:none;}


.story__tags a:hover {
    background: #eee !important;
    color: #888 !important;
    
}

/*  */

.b-feed-panel {
    border: 0px solid #ddd;
    margin-bottom: 5px;
    padding:10px;
    font-size:14px;
}
.menu-block-branding + table {
    display: none;
}
.rating_bl,
div.menu-top-tags .menu-top-tags-cont,
.menu-top-tags-links {
    padding-left: 0 !important;
}

.menu-block .rating_bl,
.best_comm .rating_bl {
    
    padding: 14px 20px 10px 20px !important;
    border:none;
}

.rating_bl {font-size:17px;}

.menu-block, .menu-block-branding {border:none  !important}

td.menu-best-comm {padding: 0px;}

td.menu-best-comm > div.menu-story-link {padding:0 0 5px 20px; margin:0;}
td.menu-best-comm > div.menu-story-link:before {
    content:'';
    display:inline-block;
    background-image: url("http://cs.pikabu.ru/app/1.1.72/images/i-sprite.png");
    background-position: -101px -545px;
    width: 13px;
    height: 15px;
    margin-right:0px;
    vertical-align: bottom;
  }


td.menu-best-comm > div.menu-comm-head {padding: 0 0 5px 20px;}

td.menu-best-comm > div.menu-story-link > a {font-size:16px; text-decoration:none;}
td.menu-best-comm > div.menu-story-link > a:hover {text-decoration:underline;}

td.menu-best-comm > div.menu-comm-desc {
    padding:17px 20px;  background: #f7f7f5; 
    background: linear-gradient(to top, #f7f7f5, #f7f7f5, #f7f7f5, #F2F2F0);
    border-radius:0 0 3px 3px !important
}

td.menu-best-comm > div.menu-comm-desc p {display:inline}

.b-comment-best {font-size:16px; font-style:italic; padding-top:0; color:#888} 
.b-comment-best:before {content:'„ '; color:#bbb}
.b-comment-best:after {content:' ”';  color:#bbb}



/* рейтинг */

.story__rating-count {
    font-size: 22px;
    color: #888;
    margin: 0px 0;
}
.story__rating-up,
.story__rating-down {
    padding: 0 0;
    width: 36px;
    text-align: center;
    box-sizing: border-box;
    border-radius: 2px;
    border: 0px solid #d5d5d5;
  }

.story__rating-up { background: linear-gradient(to bottom, #f7f7f5, #eeeeec); border-top:0px solid #ddd}
.story__rating-down { background: linear-gradient(to top,  #f7f7f5, #eeeeec); border-bottom:0px solid #ddd}


.story__rating-up:hover,
.story__rating-down:hover {
    background: #fff;
   }

.story__rating-up svg,
.story__rating-down svg {position:static !important}

.story__rating-up:before {
   	margin:8px auto 9px auto;
 }

.story__rating-down:before {
	margin:9px auto 8px auto;
 }

.story__rating-down_active {
    background: #ffe7b0;
    border-color: #F5C689;
    box-shadow: 0 1px 5px #bbb, inset 0 0 0 1px #F1BC6A;
}
.story__rating-up_active {
    background: #E9F7C9;
    border-color: #B1D951;
    box-shadow: 0 1px 5px #bbb, inset 0 0 0 1px #B1D951;
}

/* */

#blk-upcoming .a-selected {
    background: none;
}


/* шапка */

#right_menu {
    position: relative;
    z-index: 2;
}
#header_t {
    height: 70px !important;
}
.header-cont {
    background: #333;
    background:linear-gradient(to top, #333, #222);
    border-bottom: 4px solid #81bf66;
    position: relative !important;
    padding-bottom: 3px !important;
}
.header-logo {
    vertical-align: bottom !important;
    width: 230px !important;
}
.header-nav {
    text-align: left !important;
    vertical-align: bottom !important;
    padding-bottom: 10px !important;
}
.header-nav table,
.header-nav tr,
.header-nav td,
.header-nav tbody,
.header-nav colgroup,
.header-nav col {
    display: inline !important;
}
.header-nav .menu {
    float: none !important;
    margin-top: 0px !important;
    ;
}
.header-nav .menu li {
    width: auto !important;
    height: auto !important;
    margin: 0 5px 0 0 !important;
}
.header-nav .menu li a {
    width: auto !important;
    height: auto !important;
    font-size: 17px !important;
    color: rgba(255, 255, 255, 0.8) !important;
    background: none !important;
    margin: 0 !important;
    border: none !important;
    border-radius: 20px !important;
    padding: 6px 12px !important;
}
.header-nav .menu li a:hover {
    background: #444 !important;
}

.header-nav .menu li a:active {box-shadow: inset 0 1px 8px rgba(0,0,0,0.4); background: #444}

.header-nav .menu li.active a,
.header-nav .menu li.active2 a,
.header-nav .menu li.active3 a {
    border: none !important;
    background: #81bf66 !important;
    color: #444 !important;
    text-shadow: none !important;
}

.header-nav .menu li.active:hover a,
.header-nav .menu li.active2:hover a,
.header-nav .menu li.active3:hover a {
    background: #67bb58 !important;
}




.header-nav-top {
    height: 34px;
}
td.header-nav-bottom > ul.menu > li.menu-item-default > a {
    position: static;
}
div.menu-cont div.header-nav-top > ul > li > a {
    color: #777;
    text-decoration: none;
    padding: 5px;
    font-size: 12px;
}
.header-search-box {
    position: absolute;
    width: 300px;
    left: 0px;
    top: -66px;
    margin-right: 0px;
    border-radius: 17px;
    background: #eee;
    padding: 4px !important;
    opacity: 1;
    box-shadow: inset 0 0 3px #444;
}
.search_block {
    border-radius: 15px 0 0 15px !important;
    font-size: 15px !important;
    background: #eee !important;
}
.search_btn {
    border-radius: 0 15px 15px 0 !important;
    font-size: 15px !important;
    background: #eee !important;
}
.header_links {
    position: absolute !important;
    width: 300px !important;
    top: -46px !important;
    left: 2px !important;
    margin: 0 !important;
}
#branding_ad_header {
    background-size: 0, auto !important;
    margin-bottom: -73px  !important;
}

.community-bg img {
	border-color:#f5f5f5 !important;
}

.b-community-header[data-has-bg="1"] div.community-info .community-bg img {margin-top: -70px !important;}
.b-community-header div.community-info .community-bg img {margin-top: 0px !important;}


.b-community-header div.community-info>div h1 {
    color: #444;
    font-size: 21px;
    margin: 5px 0 7px 0;
}

.community-info {padding-bottom: 5px;}

.feed-panel-transparent {padding-left:0}
.b-community-h-menu {margin-left:0}

/* юзерменю */

.b-user-menu {
    margin-top: 16px;
    display: flex;
    justify-content: space-between;
    position:relative;
   
}


.b-user-menu__main {
    width:190px;
    border:none !important;
}
.b-user-menu__logo {
    width:110px;
    background:linear-gradient(to left, #f7f7f5, #f7f7f5, #f7f7f5, #EFEFED)
}
.b-user-menu__logo:before, .b-user-menu__logo:after {display:none !important}

.b-user-menu__avatar {border:0}
.b-user-menu__avatar img {float:none; height:80px; width:80px; margin:12px 12px 6px 12px; border-radius:50%;}


.b-user-menu__header {border:none; font-size:19px; padding: 15px 15px 4px 15px}

.b-user-menu-list {
    font-size: 16px;
    margin: 5px 0 15px 20px;
  }

.b-user-menu-list__count {display:inline-block; font-weight:normal; 
    width:22px; line-height:22px; padding:0;
	border-radius:50%; text-align:center;
	background:#77ad5f; color:#fff !important}

.b-user-menu-list__count:hover {
    background: #67bb58 !important;
}



.b-user-menu-list li {
    line-height: 1.7;
}

.b-user-menu-list a {
    display: inline-block;
    vertical-align: middle;
}


.b-user-menu-list li:first-child  {
    bottom:13px; 
    padding:5px 23px;
   }

.b-user-menu-list li:first-child + li {
    bottom:64px;
    padding:5px 10px;
  	}

.b-user-menu-list li:first-child , .b-user-menu-list li:first-child + li {
	position:absolute;
    display:block;
    width:110px; right:0;
    z-index: 1;
    text-align:center;
    font-size:18px;
    line-height:1.3;
    box-sizing:border-box;
    font-weight:bold;
    color:#819BA7;
    }

.b-user-menu-list li:first-child:first-line, .b-user-menu-list li:first-child + li:first-line  {
    font-size:14px;
    color:#aaa;
