}


.b-user-menu__strawberry span {height:14px; width:22px;}
.b-user-menu__strawberry small {width:14px; height:14px;}

.b-button_type_green {background:#77ad5f;}


/* комментарии */

.b-social {   width:auto; }

.b-story-info, .b-comments__reply { 
    width:auto;  
    margin-top:5px  !important;
   
}
.b-comments-reply { max-width:none !important;  }

.b-position-button {background: rgba(255,255,255,0.2) !important; width:45px  !important;}
.b-position-button:hover {background: rgba(255,255,255,0.6)  !important;}

.b-comments-navigation__button-refresh {background:white;}

.profile_wrap {background:white!important; width:auto;}


.story__gag {padding-left:25px;}

