.b-comments-reply__editable {border-width:1px;margin-top:10px;}

@media screen and (max-width: 1400px) {
	#wrap {max-width: 1102px!important;}
    .story__main {width: 690px!important;}
    
    .b-position-button { width:35px !important;}
    .b-video, .b-video iframe {  height: 380px !important; }
}

@media screen and (max-width: 1140px) {
    
	 #wrap {max-width: 1012px!important;}
     .story__main {width: 600px!important;}
    
    .b-gifx, .b-video {margin:0 auto !important;}
    .b-story-block_type_text .b-story-block__content {margin-top:15px !important}
    
    .b-story__content_type_text, .b-story__content_type_media,
    .b-story__content_type_media img {max-width:none !important} 
   
	.b-position-button { width:26px !important;}
    .b-video, .b-video iframe {  height: 334px !important; }
}


}
