css:
@-moz-document domain(youtube.com) {
body {
    background: #111;
}
#yt-masthead-container {
    background: #333;
    border-bottom: 1px solid #000000;
    min-width: 0;
    padding-bottom: 8px;
    padding-top: 7px;
    position: relative;
}
#masthead-appbar {
    position: relative;
    background-color: #333;
    border-bottom: 1px solid #000000;
    height: 40px;
    line-height: 40px;
}
.masthead-search-terms-border {
    border: 1px solid #000;
    box-shadow: inset 0 1px 2px #000;
}
#masthead-search-terms {
    background-color: #404040;
    font-size: 14px;
    height: 29px;
    line-height: 30px;
    margin: 0 0 2px;
    overflow: hidden;
    position: relative;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    -moz-transition: border-color .2s ease;
    -webkit-transition: border-color .2s ease;
    transition: border-color .2s ease;
    transition-property: border-color;
    transition-duration: 0.2s;
    transition-timing-function: ease;
    transition-delay: initial;
}
element.style {
}
hr, html, i, iframe, img, ins, kbd, label, legend, li, menu, object, ol, p, pre, q, s, samp, small, span, strike, strong, sub {
    margin: 0;
    padding: 0;
    border: 0;
    font-size: 100%;
    background: #333;
}
.show-guide #appbar-guide-menu {
    box-shadow: 5px 10px 15px 5px #222;
}
#appbar-guide-menu, .guide-flyout {
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: #333;
    z-index: 1999999999;
}
.yt-ui-ellipsis {
    background-color: rgba(0,0,0,0);
    display: block;
    line-height: 1.3em;
    overflow: hidden;
    position: relative;
    text-overflow: ellipsis;
    white-space: normal;
    word-wrap: break-word;
}
.yt-lockup-title a, .yt-lockup:hover a, .yt-lockup:hover .yt-lockup-meta a, .yt-lockup:hover .yt-lockup-description a {
    color: #ddd;
}
body #footer-container {
    margin-top: -145px;
    background-color: #222;
    border-top: 1px solid #000;
}
#footer-main {
    border-bottom: 1px solid #000;
    padding-bottom: 15px;
}
#footer-links-primary a {
    color: #b8b8b8;
}
.yt-uix-button-icon-wrapper {
    /* display: inline-block; */
    font-size: 0;
    vertical-align: middle;
}
hr, html, i, iframe, img, ins, kbd, label, legend, li, menu, object, ol, p, pre, q, s, samp, small, span, strike, strong, sub {
    margin: 0;
    padding: 0;
    border: 0;
    font-size: 100%;
    background: rgba(0,0,0,0);
}
.yt-card {
    margin: 0 0 10px;
    border: 0;
    background: #222;
    box-shadow: 0 1px 2px rgba(0,0,0,.1);
    -moz-box-sizing: border-box;
    box-sizing: border-box;
}
yt-lockup .yt-lockup-meta a, .yt-lockup .yt-lockup-description a, .yt-lockup .yt-lockup-byline a {
    color: #eee;
}
.guide-item {
    color: #eee;
}
.branded-page-module-title a {
    color: #f0f0f0;
}
#appbar-nav .yt-uix-button.yt-uix-button-epic-nav-item {
    height: 40px;
    padding: 0;
    line-height: 40px;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    font-weight: normal;
    color: #f0f0f0;
}
a.yt-uix-button-epic-nav-item.selected, a.yt-uix-button-epic-nav-item.yt-uix-button-toggled, button.yt-uix-button-epic-nav-item.selected, button.yt-uix-button-epic-nav-item.yt-uix-button-toggled, .epic-nav-item.selected, .epic-nav-item.yt-uix-button-toggled, .epic-nav-item-heading {
    color: #f0f0f0;
    font-weight: 500;
}
.compact-shelf .yt-uix-button-shelf-slider-pager {
    position: absolute;
    bottom: 10px;
    background: #666;
    border: none;
    width: 15px;
    height: 60px;
    top: 50%;
    margin-top: -60px;
}
.branded-page-box, .section-list li .item-section .branded-page-box {
    border-bottom: 1px solid #000;
}
.guide-pinned .guide-pinning-enabled #appbar-guide-menu {
    border-right: 1px solid #222;
    box-shadow: none;
}
.channel-header .secondary-header-contents {
    position: relative;
    padding: 0 15px;
    height: 38px;
    font-size: 0;
    background-color: #333;
    border-bottom: 1px solid #e2e2e2;
}
.secondary-header-contents .nav-text {
    color: #f0f0f0;
    font-size: 11px;
    font-weight: 500;
    vertical-align: middle;
}
.channel-header .branded-page-header-title .branded-page-header-title-link {
    color: #eee;
    font-size: 20px;
    font-weight: normal;
    text-shadow: none;
}
a.yt-uix-button.yt-uix-button-epic-nav-item, button.yt-uix-button-epic-nav-item, .epic-nav-item {
    border: none;
    padding: 0 3px 3px;
    cursor: pointer;
    background: none;
    color: #f0f0f0;
    font-size: 13px;
    font-weight: normal;
    height: 29px;
    line-height: 29px;
    vertical-align: bottom;
    -moz-box-sizing: content-box;
    box-sizing: content-box;
    border-radius: 0;
    display: inline-block;
}
.branded-page-v2-primary-col .yt-uix-inlineedit-view-container .branded-page-box {
    border-bottom: 1px solid #111;
}
.gssb_m {
    color: #000;
    background: #666;
}
#watch-description {
    color: #f0f0f0;
    line-height: 17px;
    overflow: hidden;
    position: relative;
    z-index: 1;
}
.video-list .video-list-item .title {
    color: #eee;
    font-size: 13px;
    font-weight: 500;
}
.comment-renderer-header .comment-author-text {
    color: #eee;
    font-weight: 500;
    text-decoration: none;
    word-break: break-all;
}
.comment-renderer {
    overflow: hidden;
    position: relative;
    padding-right: 20px;
    background: #333;
}
#comment-section-renderer .comment-renderer-reply {
    color: #f8f8f8;
    opacity: 1;
}
.video-list-item .content-wrapper a {
    background: #333;
    padding: 0;
}
#watch8-secondary-actions {
    position: absolute;
    left: -10px;
    background: #aaa;
}
#watch-header .yt-user-info a {
    display: inline-block;
    height: 22px;
    color: #666;
    font-weight: 500;
    max-width: 315px;
    overflow: hidden;
    text-overflow: ellipsis;
    vertical-align: top;
}
.yt-uix-button-default, .yt-uix-button-default[disabled], .yt-uix-button-default[disabled]:hover, .yt-uix-button-default[disabled]:active, .yt-uix-button-default[disabled]:focus {
    /* border-color: #000000; */
    background: #eee;
}
.comment-renderer-text {
    color: #f0f0f0;
    margin: 3px 0 8px;
}
.pl-video-title-link {
    color: #eee;
    display: inline-block;
    font-size: 13px;
    font-weight: 500;
    line-height: 1.3em;
}
#pl-header .pl-header-title {
    color: #f0f0f0;
    font-size: 20px;
    font-weight: 500;
    padding: 1px;
    padding-left: 0;
    vertical-align: middle;
    line-height: 33px;
}
}
