css:
@-moz-document domain("emuparadise.me") {
div#eplogo > a:before {
    content: "emu";
    position: fixed!important;
    top: -1px;
    left: 10px;
    width: 160px;
    height: 35px;
    display: block!important;
    z-index: 1001;
    font-family: Arial, sans-serif!important;
    font-size: 1.625rem!important;
    line-height: 1.875rem!important;
    font-weight: bold;
    color: #ffffff!important;
    opacity: 0.77!important
}
div#eplogo > a:after {
    content: "paradise";
    position: fixed!important;
    top: -1px;
    left: 9px;
    width: 110px;
    height: 35px;
    display: block!important;
    z-index: 1001;
    font-family: Arial, sans-serif!important;
    font-size: 1.625rem!important;
    line-height: 1.875rem!important;
    font-weight: bold;
    color: #5298ee!important;
    padding-left: 54px!important;
    opacity: 0.77!important
}
div#eplogo > a:hover:after,
div#eplogo > a:hover:before {
    opacity: 1!important;
}
div#eplogo > a > img,
li.top-menu-social-icon {
    display: none!important;
}
.menu {
    border-bottom-color: #242424!important;
    background-color: #242424!important;
    box-shadow: 0px 5px 5px -3px rgba(0, 0, 0, 0.14), 0px 8px 10px 1px rgba(0, 0, 0, 0.098), 0px 3px 14px 2px rgba(0, 0, 0, 0.082)!important;
    padding-left: 190px!important;
    width: 100%!important;
}
a.top-level {
    border: 0 solid #4c6977;
    border-radius: 0!important;
    color: #d0d0d0!important;
}
a.top-level:hover,
.menu:hover>a,
.menu :hover>a{
    background-color: #3e3e3e!important;
    background: #3e3e3e!important;
}
*::-webkit-scrollbar {
    width: 10px ! important;
    height: 10px ! important;
    background: #1e1e1e ! important;
    border: 1px solid #252525 ! important;
}
*::-webkit-scrollbar-button {
    display: none ! important;
}
*::-webkit-scrollbar-thumb,
*::-webkit-scrollbar-track {
    border: 0 ! important;
    box-shadow: none ! important;
}
*::-webkit-scrollbar-thumb {
    min-height: 28px ! important;
    background: #3a3a3a ! important;
}
*::-webkit-scrollbar-corner,
*::-webkit-scrollbar-track {
    background: #292929 ! important;
}
.menu ul ul li >a,
.menu:hover>a {
    background-color: #3e3e3e!important;
    border-radius: 0!important
}
.menu ul ul ul a, .menu ul ul ul a:visited {
    background: #545454!important;
}
.menu ul ul li >a:hover,
.menu ul ul :hover>a.drop{
    background-color: #333!important;
}
.box {
    background: #1C1C1C!important;
    border-color: #1C1C1C!important;
    border-radius: 0!important
}
body {
    background-image: none!important;
    background-color: #161616!important;
    color: #fbf8f1!important;
}
input#searchbox.search.ui-autocomplete-input {
    background: #202020!important;
    border-color: #323232!important;
    color: #acacac!important;
    border-radius: 0!important
}
input#mainpagesearchbox.search.ui-autocomplete-input,
select,
input {
    background-color: #202020!important;
    border-color: #323232!important;
    color: #acacac!important;
    border-radius: 0!important
}
.box-header,
.share-buttons-top,
.ui-widget-header {
    background: #262626!important;
    color: #C4C4C4!important;
    border: 1px solid #2E2E2E!important;
    box-shadow: 0px 0px 5px rgba(19, 19, 19, 0.74)!important;
    border-radius: 0!important;
}
.help,
.filter,
#region-filters {
    background-color: #262626!important;
    color: #C4C4C4!important;
    border: 1px solid #2E2E2E!important;
    box-shadow: 0px 0px 5px rgba(19, 19, 19, 0.74)!important;
    border-radius: 0!important;
}
#logo-row {
    height: 100px!important;
}
.share-the-love {
    background-color: transparent!important;
}
.ui-widget-content {
    border-color: #2C2C2C!important;
    background: #2C2C2C!important;
    box-shadow: 0px 0px 5px rgba(19, 19, 19, 0.74)!important;
    border-radius: 0!important;
}
.ui-state-hover,
.ui-widget-content .ui-state-hover,
.ui-widget-header .ui-state-hover,
.ui-state-focus,
.ui-widget-content .ui-state-focus,
.ui-widget-header .ui-state-focus {
    background: #262626!important;
    color: #C4C4C4!important;
    border-color: #2E2E2E!important;
}
li.ui-autocomplete-category {
    background: #262626!important;
    color: #C4C4C4!important;
    border: 1px solid #2E2E2E!important;
    border-radius: 0!important;
}
.sys-label {
    color: #B34242 !important;
}
a.genre {
    border-radius: 0.125rem!important;
    border: 0.0625rem solid #333333!important;
    background: #2E2E2E!important;
    color: #d4d4d4!important;
    box-shadow: 0px 0px 5px rgba(19, 19, 19, 0.74)!important;
}
a.genre:hover {
    background: #272727!important;
    color: #e7e7e7!important;
    border: 0.0625rem solid #2E2E2E!important;
}
.description-text,
#user-uploaded-images,
#content.main-page-div > table.mobile-hide > tbody > tr:nth-child(1) > td,
.showDisqus {
    background: #262626!important;
    border: 1px solid #262626!important;
    box-shadow: 0px 0px 5px rgba(19, 19, 19, 0.74)!important;
    border-radius: 0!important;
}
.ui-state-default,
.ui-widget-content .ui-state-default,
.ui-widget-header .ui-state-default {
    border-color: #393939!important;
    background-color: #353535!important;
}
.ui-state-active,
.ui-widget-content .ui-state-active,
.ui-widget-header .ui-state-active {
    border-color: #2b414c!important;
    background-color: #9a3b3b!important;
}
#content > div.col_4.suf_1 > table > tbody > tr > td,
#content > div.col_4.last > table > tbody > tr > td {
    border-color: #373737!important;
    background: #373737!important;
    color: #e7e7e7!important;
}
div.steps {
    background: #272727!important;
    color: #e7e7e7!important;
    border: 0.0625rem solid #2E2E2E!important;
    border-radius: 0!important;
}
input.prettybutton,
a.prettybutton {
    background: #272727!important;
    color: #e7e7e7!important;
    border: 0.0625rem solid #2E2E2E!important;
    border-radius: 0!important;
    box-shadow: none!important;
}
input.prettybutton:hover,
a.prettybutton:hover {
    border: 0.0625rem solid #333333!important;
    background: #2E2E2E!important;
    color: #d4d4d4!important;
}
.fancybox-outer {
    background: #2d2d2d!important;
}
.roms:hover,
.emulators:hover,
.videos:hover,
.scans:hover,
.music:hover,
.tgv:hover {
    background: #262626!important;
}
.handle {
    -webkit-filter: grayscale(100%) brightness(100%)!important;
    filter: grayscale(100%) brightness(100%)!important;
    opacity: .4!important;
    background-color: #505050!important;
}
.handle:hover {
    opacity: .7!important;
}
#content > form:nth-child(13) > table > tbody > tr > td[bgcolor="#4C6977"] {
    background-color: transparent!important;
}
#content > table {
    background: #2A2A2A!important;
    border: 0.0625rem solid #2A2A2A!important;
    padding: 0!important;
    border-spacing: 0!important;
    box-shadow: 0px 0px 5px rgba(19, 19, 19, 0.74)!important;
}
#content > table > tbody > tr > td[bgcolor="#4C6977"] {
    background: transparent !important;
}
#content > table > tbody > tr:nth-child(1) > td[bgcolor="#4C6977"] {
    background: #242424 !important;
    color: #C4C4C4!important;
}
#content > table > tbody > tr:nth-of-type(even) {
    background: #2A2A2A!important;
}
#content > table > tbody > tr:nth-of-type(odd) {
    background: #2E2E2E!important;
}
#content > table > tbody > tr:hover {
    background: #202020!important;
}
#Emulators > table > tbody > tr > td,
#rating-form > table > tbody > tr > td,
#content > div:nth-child(16) > form > table > tbody > tr > td[bgcolor="#4C6977"]{
    background-color: #262626!important;
}
#main-search-submit-button, #search-submit-button {
    background: url('http://image.noelshack.com/fichiers/2016/25/1466464842-search-icon-hi2.png') no-repeat 0px 5px!important;
    background-size: 40px 40px!important;
    opacity: .3!important;
}
#main-search-submit-button:hover, #search-submit-button:hover {
    opacity: .6!important;
}
td.snifDir {
    background: #242424 !important;
    color: #C4C4C4!important;
}
tr.snifEven {
    background-color: #2A2A2A!important;
}
tr.snifOdd {
    background-color: #2E2E2E!important;
}
tr.snifEven:hover,
tr.snifOdd:hover {
    background-color: #202020!important;
}
.main-title {
    background: #232323!important;
    border-bottom: 1px solid #2B2B2B!important;
    margin-left: 0px!important;
    margin-right: 0px!important;
    padding-left: 5px!important;
    padding-right: 5px!important;
}
#content > div:nth-child(2) > table > tbody > tr:nth-child(5) > td[height="3"][bgcolor="#3C545F"] {
    background-color: #232323!important;
}
}
