css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dynalist.io") {
  
    .AppHeader-desktopControls {background-color: #6c6;color:#7a7;}
  .AppHeader-desktopControls a {color:#fff;font-weight: bold;}
  .Pane-content {background-color: #cf9;}
  
  FolderItem-title,
.DocumentItem-title,
  .BookmarkItem-title {color:#000;font-weight: bold;}
  
.FolderItem-toggler {color:#000;font-weight: bold;}
  
  .DocumentItem.is-open .DocumentItem-title {
  color:#000;background-color: #fff;
}
  
    .DocumentItem.is-open {
  background-color: #fff;
}
  
  
  
  /*EXTERNAL LINKS*/



#blank-slate div ul li a {text-decoration: none;}

  

    

a {

	color:#66f;

        font-weight: 600;

	text-decoration:none;

}



/*ELIMINATE VERTICAL LINES*/



.Node-children {border-left: 0px;font-weight:normal;}



/*INDENTING*/



.Node {

  display: block;

  padding-left: 25px;

  position: relative;

}



.Node.is-currentRoot > .Node-self > .Node-contentContainer,

.Node.is-currentRoot > .Node-self > .Node-noteContainer {

  left: 18px;

}



/*TITLES BOLD*/



.Node.is-currentRoot > .Node-self {font-weight: bold;}



/*BLUE BULLETS*/



.Node-bullet:before {

	color:#66f;

}





/*INTERNAL LINKS and inline images*/



.node-inline-image,

.node-link-internal.is-displayed {

  vertical-align: top;

  display: inline-block;

  height: 24px;

  line-height: 22px;

  font-size: 1em;

  border-radius: 0px;

  cursor: pointer;

  text-decoration: none;

  position: relative;

  z-index: 2;

  padding: 0 5px 0 0px;

  pointer-events: auto;

	background-color: white;

	border: none;

}

.node-inline-image:before,

.node-link-internal.is-displayed:before {

  font-family: "IcoMoon", sans-serif;

  color: #888;

  position: absolute;

  left: 0px;

}

.node-link-internal.is-displayed {

  text-overflow: ellipsis;

  overflow: hidden;

  white-space: nowrap;

}

@media screen and (max-width: 800px) {

  .node-link-internal.is-displayed {

    max-width: 500px;

  }

}

body.is-iOS .node-link-internal.is-displayed {

  max-width: 500px;

}

@media screen and (min-width: 800px) {

  body:not(.is-iOS) .node-link-internal.is-displayed {

    max-width: 1000px;

  }

}

.node-inline-image:before {

  font-size: 18px;

  content: none;

}

.node-link-internal.is-displayed:before {

  font-size: 18px;

  content: none;

  position: absolute;

  left: 0px;

}



/*TAGS*/



.node-tag {

	background-color:#FFF;

	color:#66f;

	font-weight:normal;

	padding-left: 5px;

  padding-right: 5px;

  padding-top: 1px;

  padding-bottom: 1px

}
}
