css:
@-moz-document domain("play.pokemonshowdown.com"), domain("psim.us") {

  /**************************
   * Impulse Additional CSS *
   *************************/
   /* The alert message box */
   .alert {
    padding: 20px;
    background-color: #f44336; /* Red */
    color: white;
    margin-bottom: 15px;
  }

/* The close button */
   .closebtn {
    margin-left: 15px;
    color: white;
    font-weight: bold;
    float: right;
    font-size: 22px;
    line-height: 20px;
    cursor: pointer;
    transition: 0.3s;
  }

   /* When moving the mouse over the close button */
   .closebtn:hover {
    color: black;
  }
   /*********************
   * Additional CSS End *
   *********************/

  /**************
  * GENERAL CSS *
  **************/
 
  body {
    background-image: url("https://images2.alphacoders.com/723/723236.png") !important;
/*("http://img02.deviantart.net/12dd/i/2013/074/c/1/lugia_in_water_wallpaper_by_apexx_ipredator-d5y6o10.jpg") !important; */
  }
 
   .ps-room.ps-room-light {
    background-color: rgba(24, 116, 205, 0.5) !important;
    border-color: #000 !important;
    border-radius: 5px !important;
  }
 
  .ps-room.ps-room-light.scrollable {
    background-color: rgba(24, 116, 205, 0.5) !important;
  }
 
  .pad .button, .ps-room.ps-room-light.scrollable p .button {
    background: rgba(24, 116, 205, 0.5) !important;
    border-color: #000;
    font-family: Georgia, Times, 'Times New Roman', serif !important;
    color: #FFF !important;
    text-shadow: 0px 0px 0px !important;
  }
 
  .pad .button:hover, .ps-room.ps-room-light.scrollable p .button:hover {
    background: rgba(24, 116, 205, 0.7) !important;
    border-color: #000;
    font-family: Georgia, Times, 'Times New Roman', serif !important;
    text-shadow: 0px 0px 0px !important;
    color: #FFF;
  }
 
  .ps-room.ps-room-light .roomlist, .roomlisttop {
    width: 100%;
    max-width: 100% !important;
  }
 
  .ps-room.ps-room-light .roomlist {
    background: none !important;
  }
 
  .roomlist a.ilink {
    background: rgba(24, 116, 205, 0.6);
    border-color: #000;
    font-family: Georgia, Times, 'Times New Roman', serif !important;
    border-radius: 6px;
    text-shadow: 0px 0px 0px !important;
    color: #FFF;
}
 
  .roomlist a.ilink:hover {
    background: rgba(24, 116, 205, 0.8) url("http://pldh.net/media/pokemon/md/249.png") right no-repeat;
    border-color: #000;
    color: #FFF;
    text-shadow: 0px 0px 0px !important;
    font-family: Georgia, Times, 'Times New Roman', serif !important;
  }
 
  .roomlist a.ilink small {
    text-shadow: none !important;
    color: #FFF;
  }
 
  .roomcounters .picon {
    background: url("http://pldh.net/media/pokexycons/249.png") bottom no-repeat !important;
  }
 
  .roomcounters button, roomcounters button:havor {
    background: none !important;
    border-color: #000;
    font-family: Georgia, Times, 'Times New Roman', serif !important;
    text-shadow: 0px 0px 0px !important;
    color: #FFF !important;
  }
 
  .textbox, .textbox:hover, .textbox:focus {
    background: #1c76ca;
    border-color: #000;
    text-shadow: 0px 0px 0px !important;
    color: white;
  }
 
  .broadcast-blue {
    background-color: #B3E6FF;
    border: 3px #000 !important;
    border-radius: 6px !important;
    color: #000 !important;
    font-family: Georgia, Times, 'Times New Roman', serif !important;
    text-shadow: 0px 0px 0px;
    box-shadow: 0 4px 8px 0 rgba(0,0,0,0.2)!important;
    transition: 0.3s !important;
    text-align: center;
  }
 
  .broadcast-blue a {
    color: #0B1361;
  }
 
  .broadcast-red {
    background-color: #FFB3B3;
    border: 3px #000 !important;
    border-radius: 6px !important;
    font-family: Georgia, Times, 'Times New Roman', serif !important;
    text-shadow: 0px 0px 0px;
    box-shadow: 0 4px 8px 0 rgba(0,0,0,0.2)!important;
    transition: 0.3s !important;
    color: #610B0B;
    text-align: center;
  }
 
  .broadcast-red a {
    color: #610B0B;
  }
 
  .broadcast-green {
    background-color: #C3FFB3;
    border: 3px #000 !important;
    border-radius: 6px !important;
    font-family: Georgia, Times, 'Times New Roman', serif !important;
    text-shadow: 0px 0px 0px;
    box-shadow: 0 4px 8px 0 rgba(0,0,0,0.2)!important;
    transition: 0.3s !important;
    color: #19610B;
    text-align: center;
  }
 
  .broadcast-green a {
    color: #19610B;
  }
 
  .message-announce {
    background: #1c76ca;
    font-style: italic; 
    font-family: Georgia, Times, 'Times New Roman', serif !important;
   color: white;
  }
   /******************
    * General CSS End *
    ******************/
 /*************
  * HEADER CSS *
  *************/
  /* old logo http://i.imgur.com/5onrVwe.png */
  .header {
    background: rgba(24, 116, 205, 0.5) !important;
  }
 
  .header .logo {
    opacity: 0;
  }
 
  .maintabbarbottom {
    background: #1874cd;
    border-color: #000;
    font-family: Georgia, Times, 'Times New Roman', serif !important;
    color: #FFF;
    text-shadow: 0px 0px 0px !important;
  }
 
  .tabbar a.button.notifying {
    background: #1874cd;
    border-color: #000;
    font-family: Georgia, Times, 'Times New Roman', serif !important;
    color: #FFF;
    text-shadow: 0px 0px 0px !important;
  }
 
  .tabbar a.button.notifying:hover {
    background: #1566b7;
  }
 
  .tabbar a.button.subtle-notifying {
    background: #1874CD;
    border-color: #000;
    color: #FFF;
   font-family: Georgia, Times, 'Times New Roman', serif !important;
   text-shadow: 0px 0px 0px !important;
  }
 
  .tabbar a.button.cur, .tabbar a.button.cur:hover {
    background: #1874CD;
    border-color: #000;
    color: #FFF;
    font-family: Georgia, Times, 'Times New Roman', serif !important;
     text-shadow: 0px 0px 0px !important;
  }
 
  .tabbar a.cur + .closebutton {
    color: #000000;
  }
 
  .tabbar a.button, .userbar button.icon {
    background: #1874CD;
    border-color: #000;
    font-family: Georgia, Times, 'Times New Roman', serif !important;
    color: #FFF;
    text-shadow: 0px 0px 0px;
  }
 
  .tabbar .closebutton {
    color: #FFF;
  }
 
  .tabbar .closebutton:hover, .tabbar a.cur + .closebutton:hover {
    color: #FFF;
  }

  .tabbar a.button:hover, .userbar button.icon:hover {
    background: #1874CD;
    border-color: #000;
    color: #FFF;
    font-family: Georgia, Times, 'Times New Roman', serif !important;
     text-shadow: 0px 0px 0px !important;
  }
 
  .userbar .username {
    color: black;
    font-family: Georgia, Times, 'Times New Roman', serif !important;
    text-shadow: 0px 0px 0px !important;
  }
 
  /******************
  * HEADER CSS ~end *
  ******************/
   
  /***********
  * CHAT CSS *
  ************/
 
  .chat-log {
    background: rgb(255, 255, 255);
    border-width: 0px 0px 1px 1px;
    border-style: solid;
    border-color: #000;
    color: #000;
    font-family: Georgia, Times, 'Times New Roman', serif !important;
     text-shadow: 0px 0px 0px;
  }
 
  .chat small {
    color: #000;
  }
 
  .infobox {
    background: transparent !important;
    border: 3px #000 !important;
    border-radius: 6px !important;
    color: #000 !important;
    font-family: Georgia, Times, 'Times New Roman', serif !important;
    text-shadow: 0px 0px 0px;
    box-shadow: 0 4px 8px 0 rgba(0,0,0,0.2)!important;
    transition: 0.3s !important;
  }
 
  .infobox code {
    color: #000;
  }
 
  .message small {
    color: #000 !important;
  }
 
  .message .utilichart .result {
    border-color: #000;
    border-radius: 2px;
    font-family: Georgia, Times, 'Times New Roman', serif !important;
    color: black;
    text-shadow: 0px 0px 0px;
    box-shadow: 0 4px 8px 0 rgba(0,0,0,0.2) !important;
    transition: 0.3s !important;
  }
  
  .pm-log a.ilink, pm-log a.ilink:havor {
    color: #FFF !important;
    font-style: bold !important;
    font-style: underline !important;
    font-family: Georgia, Times, 'Times New Roman', serif !important;
   }

  .chat-log a.ilink, .chat-log a.ilink:havor {
    background: #FFF !important;
    color: darkblue !important;
    font-style: bold;
    font-style: underline;
    font-family: Georgia, Times, 'Times New Roman', serif !important;
  }
 
  .infobox a.ilink, .infobox a.ilink:havor {
    color: darkblue!important;
    font-style: bold;
    font-style: underline;
    font-family: Georgia, Times, 'Times New Roman', serif !important;
  }
 
  .chat.mine {
    background: transparent;
  }
 
  .message-pm {
    color: #FFF;
  }
 
  .chat-log-add {
    border-color: #000;
    font-family: Georgia, Times, 'Times New Roman', serif !important;
    text-shadow: 0px 0px 0px;
    color: #000;
}
 
  .chat-log-add .chatbox label {
    text-shadow: 0px 0px 0px;
  }
 
  .userlist {
    background: rgb(09, 37, 67);
    border-color: rgba(0, 0, 0, 0.5);
    font-family: Georgia, Times, 'Times New Roman', serif !important;
    text-shadow: 0px 0px 0px;
  }

  .userlist li {
    border-color: #000;
  }
 
  .userlist li span, .userlist li strong {
    text-shadow: none;
  }
 
  .userlist li button:hover {
    background: rgba(142, 191, 241, 0.5);
  }
 
  .userlist-count {
    color: #FFF;
  }
 
  .userlist li em.group {
    color: #FFF;
  }
 
  /****************
  * CHAT CSS ~end *
  ****************/

   /**********************
  * MAINMENUWRAPPER CSS *
  ************************/
 
  .menugroup {
    background: none !important;
    border: none !important;
    color: white;
    font-style: bold;
    font-family: Georgia, Times, 'Times New Roman', serif;
    text-shadow: 0px 0px 0px;
  }
 
  .menugroup button, .select, .menugroup .button.mainmenu2, .menugroup .button.mainmenu4 {
    background: #0f4d8a;
    border: 4px #000;
    border-radius: 5px;
    color: #FFF;
   font-family: Georgia, Times, 'Times New Roman', serif;
   text-shadow: 0px 0px 0px;
  }
 
  .menugroup button:hover, .select:hover, .menugroup .button.mainmenu2:hover, .menugroup .button.mainmenu4:hover {
    background: #0a335c;
  }
 
  .menugroup a.ilink, .menugroup a.ilink:havor {
   font-family: Georgia, Times, 'Times New Roman', serif;
    font-weight: bold;
    font-style: italic;
    color: #FFF;
    text-shadow: 0px 0px 0px;
  }
 
  .select:disabled, .select:disabled:hover {
    background: #0F4D8A;
    border: 2px #000;
    border-radius: 5px;
    color: #FFF;
    font-style: bold;
    font-family: Georgia, Times, 'Times New Roman', serif;
    text-shadow: 0px 0px 0px;
  }

 .select:disabled:havor {
   background: #0A335C;
  }
 
  .teamselect:disabled strong {
    color: #FFF;
  }
 
  .menugroup .button.mainmenu1, .menugroup .button.mainmenu3, .menugroup .button.mainmenu5 {
    background: #0A335C;
    border: 4px #000;
    border-radius: 5px;
    color: #FFF;
    font-family: Georgia, Times, 'Times New Roman', serif;
    text-shadow: 0px 0px 0px;
  }
 
  .menugroup .button.mainmenu1:hover, .menugroup .button.mainmenu3:hover, .menugroup .button.mainmenu5:hover {
    background: #0F4D8A;
  }

  .battleform label {
    background: url("http://i.imgur.com/yFWeHx0.png") no-repeat scroll left center transparent !important;
    padding: 4px;
    color: #FFF;
    text-align:left;
  }

  .pm-window h3 {
    background: #0A335C;
    border: 4px #000;
    border-radius: 5px;
    color: #FFF;
   font-family: Georgia, Times, 'Times New Roman', serif;
   text-shadow: 0px 0px 0px;
  }
 
  .pm-window .closebutton, .pm-window .minimizebutton {
    color: #FFF;
  }
 
  .pm-window h3 small {
    color: #FFF;
  }
 
  .pm-window h3:hover {
    background: #0F4D8A;
  }
 
  .pm-window .closebutton:hover, .pm-window .minimizebutton:hover, .pm-window h3:hover .minimizebutton {
    color: #FFF;
  }
 
  .pm-window .closebutton:hover + .minimizebutton {
    color: #FFF !important;
  }
 
  .pm-window.focused h3 {
    background:#0A335C;
    border: 2px #000;
    border-radius: 4px;
    color: #FFF;
    font-family: Georgia, Times, 'Times New Roman', serif;
    text-shadow: 0px 0px 0px;
  }

  .pm-window.focused h3:hover {
    background: #0F4D8A;
}

  .pm-log, .pm-window.focused .pm-log {
    background: #FFF !important;
    border-color: #000;
    color: #000 !important;
  }
 
  .unread {
    background: none;
  }
 
  .challenge {
    background:  #0b335b;
    border: 4px #000;
    border-radius: 5px;
    color: #FFF;
    font-family: Georgia, Times, 'Times New Roman', serif;
    text-shadow: 0px 0px 0px;
  }
 
  .challenge .buttonbar button {
    background: #0A335C;;
    border: 4px #000;
    border-radius: 5px;
    color: #FFF;
    font-family: Georgia, Times, 'Times New Roman', serif;
    text-shadow: 0px 0px 0px;
  }
 
  .challenge .buttonbar button:hover {
    background: #4DFAD8A;
  }
 
  .pm-log-add, .pm-window.focused .pm-log-add {
    background: #FFF !important;
    border: 0px #000;
    color: #000 !important;
    font-family: Georgia, Times, 'Times New Roman', serif;
    text-shadow: 0px 0px 0px;
  }
 
  .pm-log-add .textbox {
    color: #000;
  }

  .mainmenufooter {
    background: none !important;
    border: none !important;
    color: #FFF;
    font-family: Georgia, Times, 'Times New Roman', serif;
    text-shadow: 0px 0px 0px;
  }
 
  .mainmenufooter a {
    color: #FFF;
  }
 
  .mainmenufooter a:hover {
    color: #FFF;
  }
 
  /***************************
  * MAINMENUWRAPPER CSS ~end *
  ****************************/
  
  /******************
  * TOURNAMENTS CSS *
  ******************/
 
  .tournament-title {
    background:  #0e4071;
    color: #FFF;
    border-color: #000;
    font-family: Georgia, Times, 'Times New Roman', serif;
    text-shadow: 0px 0px 0px;
  }
 
  .tournament-status, .tournament-toggle {
    background: #082644;
    border-color: #000;
    color: #FFF;
    font-family: Georgia, Times, 'Times New Roman', serif;
    text-shadow: 0px 0px 0px;
  }
 
  .tournament-box {
    border-color: #000;
    color: #FFF;
    font-family: Georgia, Times, 'Times New Roman', serif;
    text-shadow: 0px 0px 0px;
  }
 
  .tournament-bracket {
     background: #0e4071;
     border-color: #000;
     color: #FFF;
     font-family: Georgia, Times, 'Times New Roman', serif;
    text-shadow: 0px 0px 0px;
  }
 
  .tournament-bracket-tree {
    border-color: #000;
    color: #FFF;
    font-family: Georgia, Times, 'Times New Roman', serif;
    text-shadow: 0px 0px 0px;
  }
 
  .tournament-tools {
    background: 0e4071;
    border-color: #000;
    color: #FFF;
    font-family: Georgia, Times, 'Times New Roman', serif;
    text-shadow: 0px 0px 0px;
  }
 
  .tournament-tools .button {
    background: #082644;
    border: 4px #000;
    border-radius: 5px;
    color: #FFF;
    font-family: Georgia, Times, 'Times New Roman', serif;
    text-shadow: 0px 0px 0px;
  }
 
  .tournament-tools .button:hover {
    background: #0e4071;
  }
 
  .tournament-tools .select:disabled {
    background: #082644;
    border: 4px #000;
    border-radius: 5px;
    color: #FFF; 
    font-family: Georgia, Times, 'Times New Roman', serif;
    text-shadow: 0px 0px 0px;
  }
 
.tournaments-tools .select:disabled:havor {
    background: #0e4071;
    border: 4px #000;
    border-radius: 5px;
    color: #FFF; 
    font-family: Georgia, Times, 'Times New Roman', serif;
    text-shadow: 0px 0px 0px;
}

  .tournament-tools .teamselect:disabled strong {
    color: #FFF;
  }
 
  .tournament-tools .select {
    background: #082644;
    border: 4px #000;
    border-radius: 5px;
    color: #FFF; 
    font-family: Georgia, Times, 'Times New Roman', serif !important;
    text-shadow: 0px 0px 0px;
  }
 
  .tournament-tools .select:hover {
    background: #0e4071;
  }
 
  .tournament-format {
    color: #FFF !important;
    font-style: bold;
    font-size: 14pt;
    font-family: Gerogia,Times,'Times New Roman',serif !important;
  }
 
  .ps-popup.tournament-popout-bracket {
     background: rgba(15, 62, 112, 0.5);
     color: white;
  }
 
  /**********************
  * TOURNAMENT CSS ~end *
  **********************/
  
  /************
  * POPUP CSS *
  ************/
 
  .ps-popup {
    background: rgb(255, 255, 255);
    color: #000 !important;
    border: 4px #000;
    border-radius: 5px;
    font-family: Georgia, Times,'Times New Roman',serif !important;
    text-shadow: 0px 0px 0px;
  }
 
  .ps-popup .buttonbar button {
    background: #0e4071;
    border: 4px #000;
    border-radius: 5px;
    color: white !important;
    font-family: Georgia, Times,'Times New Roman',serif !important;
    text-shadow: 0px 0px 0px;
  }
 
  .ps-popup .buttonbar button:hover {
    background: #0b335b;
  }
 
  .ps-popup a, .ps-popup a.ilink, .popupmenu h3 {
    color: darkblue !important;
    font-style: bold;
    font-family: Georgia,Times,'Times New Roman',serif !important;
    text-shadow: 0px 0px 0px !important;
  }
 
  .ps-popup a:hover, .ps-popup a.ilink:hover {
    font-style: bold;
  }
 
  .ps-popup a.ilink.yours {
     color: darkblue !important;
     font-width: bold;
     font-style: italic;
     font-family: Georgia,Times,'Times New Roman',serif !important;
     text-shadow: 0px 0px 0px !important;
  }

  .popupmenu button.sel {
    color: black !important;
    border-color: #000;
  }
 
  .popupmenu button.sel:hover {
    background: transparent;
    color: white !important;
    border-color: #000;
  }

 
  .popupmenu button:hover {
    background: #0b335b;
    color: white !important;
    font-style: bold;
    font-family: Georgia,Times,'Times New Roman',serif !important;
  }
 
