css:
@-moz-document url-prefix("https://s.pictbland.net/") {
footer > div.container.margin-top.margin-bottom + div,
img[width="320"],
.pimage,
footer .container:not(.margin-bottom),
[id^="item"] + div.row,
.navbar-fixed-top + div + div.text-center {
    display: none !important;
    height: 0 !important;
}
.protect {
    user-select: text !important;
    -webkit-user-select: text !important;
    -moz-user-select: text !important;
    -khtml-user-select: text !important;
    -webkit-user-drag: element !important;
    -khtml-user-drag: element !important;
}

body {
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
    font-size: /*[[font-size]]*/ !important;
    line-height: 1.42857143;
    background-color: /*[[background-color]]*/ !important;
    color: /*[[color]]*/ !important;
}

/* ---- item ---- */
[id^="item"] .panel {
    background-color: transparent !important;
    border: 1px #666 dotted !important;
}
.menurow {
    background-color: transparent !important;
    border: none !important;
}

[id^="item"] .href .col-xs-12 {
    padding: 10px 10px 5px 10px !important;
}
[id^="item"] .border-top:not(.href) {
    border: 0 !important;
}
.border-top:not(.href) .padding {
    padding: 5px 10px 0 15px !important;
}
.menurow {
    padding-bottom: 10px !important;
}

/* ---- list-group ---- */
.list-group-item {
    background-color: #555;
}
.list-group-item-warning {
    background-color: #fcf8e3;
}
.list-group-item.disabled {
    background-color: #eee !important;
}
.list-group-item:last-of-type {
    border-bottom-left-radius: 4px;
    border-bottom-right-radius: 4px;
}

/* ---- label ---- */
.label {
    font-weight: normal !important;
}
.label-info {
    line-height: 2;
}

/* ---- navbar ---- */
.navbar-default {
    background-color: #1b1a1a !important;
}
.navbar-default td > a {
    color: #eee !important;
}
.navbar-fixed-bottom,
.table-responsive {
    border: 0 !important;
}
.navbar-brand {
    padding: 12px 15px !important;
}
.navbar-brand > img {
    background-color: rgb(246, 248, 247) !important;
    padding: 3px !important;
    box-sizing: content-box !important;
}
.dropdown-menu {
    background-color: #e6e6e6;
}
.popover {
    color: #333 !important;
    background-color: #f8f8f8 !important;
}

/* ---- button ---- */
[id^="item"] .btn-default,
.navbar-default .btn-default{
    color: #eee;
    background-color: #777;
    border-color: #888;
}
.btn {
    /*font-size: 100%;*/
}

/* ---- menu ---- */
.modal-content {
    background-color: #333 !important;
    color: #fff !important;
}
[class*="gmenu_box"]:first-of-type {
    border-left: 1px solid #e0e0e0;
}

/* ---- novel ---- */
[class^="novel_"] {
    background-color: transparent !important;
    color: #fff !important;
}
.novelbody {
    font-size: /*[[novel-font-size]]*/;
    line-height: /*[[novel-line-height]]*/ !important;
}
/*#novelall span[style^="color:"] {background: rgba(255, 255, 255, 0.7) !important;}*/

/* ---- input ---- */
.form-control {
    color: #555;
    background-color: #f1f1f1;
    /*font-size: 100%;*/
}

/* ---- panel ---- */
.panel {
    background-color: #f5f5f5;
    border: 1px solid #eee;
}
.panel-body {
    color: #555;
}
.panel-footer {
    background-color: #ccc;
}
#view_rnd_items {
    background-color: #ccc;
}
[data-action^="/statics/event"] {
    background-color: #777 !important;
}


a {
    color: #5bc0de;
}
a:focus,
a:hover {
    color: #46b8da;
}
.text-muted {
    color: #ddd !important;
}
legend {
    color: #fff !important;
}
.well {
    background-color: #555;
    border: 1px solid #888;
}
.tapon {
    background: rgba(255, 255, 255, 0.1) !important;
}

/* ---- font-size ---- */
.small, small {
    font-size: /*[[small-font-size]]*/; /* 12px */
}
.h4, h4 {
    font-size: 129%; /* 18px */
}
legend {
    font-size: 150%; /* 21px */
}
.lead {
    font-size: 115%; /* 16px */
}
.label {
    font-size: 75%;
}
.fa-lg {
    font-size: 19px;
}
}


@-moz-document url-prefix("https://s.pictbland.net/items/") {
.panel {
    background-color: transparent !important;
    border: 1px #666 dotted !important;
}
}

@-moz-document url("https://s.pictbland.net/users/attention") {
footer .container:not(.margin-bottom) {
    display: block !important;
    height: auto !important;
}
}
