}
#ticketflagmenu > li:nth-child(6) > div:nth-child(1) > div:nth-child(2) {
    background-color: #5C83B4;
    height: 25px!important;
    padding-top: 10px!important;
}
#ticketflagmenu > li:nth-child(7) > div:nth-child(1) > div:nth-child(2) {
    background-color: #00BFFF;
    height: 25px!important;
    padding-top: 10px!important;
}
#ticketflagmenu > li:nth-child(8) > div:nth-child(1) > div:nth-child(2) {
    background-color: #FFC0CB;
    height: 25px!important;
    padding-top: 10px!important;
}
#ticketflagmenu > li:nth-child(9) > div:nth-child(1) > div:nth-child(2) {
    background-color: #964B00;
    height: 25px!important;
    padding-top: 10px!important;
}
#ticketflagmenu > li:nth-child(1) > div:nth-child(1) > div:nth-child(2):hover {
    background-color: #A5587C!important;
    height: 25px!important;
    padding-top: 10px!important;
    opacity: 0.85;
}
#ticketflagmenu > li:nth-child(2) > div:nth-child(1) > div:nth-child(2):hover {
    background-color: #FF8C5A!important;
    height: 25px!important;
    padding-top: 10px!important;
    opacity: 0.85;
}
#ticketflagmenu > li:nth-child(3) > div:nth-child(1) > div:nth-child(2):hover {
    background-color: #8BB467!important;
    height: 25px!important;
    padding-top: 10px!important;
    opacity: 0.85;
}
#ticketflagmenu > li:nth-child(4) > div:nth-child(1) > div:nth-child(2):hover {
    background-color: #FFC160!important;
    height: 25px!important;
    padding-top: 10px!important;
    opacity: 0.85;
}
#ticketflagmenu > li:nth-child(5) > div:nth-child(1) > div:nth-child(2):hover {
    background-color: #CF5D60!important;
    height: 25px!important;
    padding-top: 10px!important;
    opacity: 0.85;
}
#ticketflagmenu > li:nth-child(6) > div:nth-child(1) > div:nth-child(2):hover {
    background-color: #5C83B4!important;
