    opacity: 0.85;
}
#ticketflagmenu > li:nth-child(7) > div:nth-child(1) > div:nth-child(2):hover {
    background-color: #0099cc!important;
    height: 25px!important;
    padding-top: 10px!important;
    opacity: 0.85;
}
#ticketflagmenu > li:nth-child(8) > div:nth-child(1) > div:nth-child(2):hover {
    background-color: #ff8da1!important;
    height: 25px!important;
    padding-top: 10px!important;
    opacity: 0.85;
}
#ticketflagmenu > li:nth-child(9) > div:nth-child(1) > div:nth-child(2):hover {
    background-color: #633200!important;
    height: 25px!important;
    padding-top: 10px!important;
    opacity: 0.85;
}
#ticketreplyflagmenu {
    background-color: #1E1E1E!important;
}
[class="swiftdropdownitemparent newticketflagli"] {
    background-color: #1E1E1E!important;
    border: 2px solid #1E1E1E !important;
}
li.swiftdropdownitemparent.newticketflagli[style="display: inline-block; cursor: pointer; border: medium none;"]:hover {
    background-color: #1E1E1E!important;
    border: 2px solid #1E1E1E !important;
}
li.swiftdropdownitemparent.newticketflagli[style="display: inline-block; cursor: pointer; border: 3px solid black;"] {
    background-color: #1E1E1E!important;
    border: 2px solid #FFF !important;
}
/*подсветка ссылок*/

a:hover {}
/*подсветка кнопок*/

.topmenulinksdiv ul li,
.topnavmenuitem:hover {
    background: #222222;
    background-color: #222222;
}
/*подсветка вкладок*/

.gridtabletitlerowhover {
    padding: 5px;
    cursor: pointer;
    font-size: 13px;
    background-color: #2D2D30;
    color: #F8FEDD!important;
    text-align: left;
    border: 0px solid #696969;
    border-radius: 0px 0px 0 0;
    border-bottom: 0px solid #2D2D30;
    background-image: none !important;
}
/*подсветка фильтров*/

#ticketfiltermenu > li:nth-child(n+1) > div:nth-child(n+1) > div:nth-child(n+1):hover {
    background: #2D2D30 !important;
    background-color: #696969 !important;
    color: #F8FEDD !important;
    background-image: none !important;
}
/*вью*/

.swiftdropdownitemtext {
    font-size: 13px;
    float: none;
    margin-top: 0px;
    padding: 4px !important;
    height: 17px!important;
}
.swiftdropdownitemtext:hover {
    background-color: #696969 !important;
}
.smalltext {
    background-color: #1E1E1E !important;
    color: #F8FEDD !important;
    background-image: none !important;
}
#dashboardtabs,
.dashboardlayoutborder > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) > div:nth-child(1),
#dashboardtitle,
.ui-tabs-nav,
#dashboardtabs-welcome {
    background: #1E1E1E !important;
    background-color: #1E1E1E !important;
    color: #F8FEDD !important;
    background-image: none !important;
    border: 0px none !important;
    text-shadow: 0px 1px 1px #2D2D30;
    border-left: 1px solid #2D2D30;
    border-bottom: 1px solid #2D2D30;
}
.dashboardlayoutborder {
    background-color: #2D2D30;
    float: left;
    position: relative;
    border: 1px solid #2D2D30;
}
#dashboardcontainer {
    height: 45px;
    box-shadow: 0px 0px 5px #FFF inset;
    border-bottom: 1px solid #3C3E43;
    vertical-align: top !important;
}
.ui-tabs-vertical .ui-tabs-nav li,
.ui-tabs-vertical .ui-tabs-nav li.ui-tabs-selected {
    clear: left;
    display: block;
    border: 1px solid #3C3E43 !important;
}
/*подсветка придефов*/

.fg-menu > li:nth-child(n+1) > a:nth-child(n+1):hover {
    background: #2D2D30 !important;
    background-color: #696969 !important;
    color: #F8FEDD !important;
    background-image: none !important;
    border: 0px none !important;
}
.fg-menu > li:nth-child(n+1) > ul:nth-child(n+1) > li:nth-child(n+1) > a:nth-child(n+1):hover {
    background: #2D2D30 !important;
    background-color: #696969 !important;
    color: #F8FEDD !important;
    background-image: none !important;
    border: 0px none !important;
}
.fg-menu > li:nth-child(n+1) > ul:nth-child(n+1) > li:nth-child(n+1) > ul:nth-child(n+1) > li:nth-child(n+1) > a:nth-child(n+1):hover {
    background: #2D2D30 !important;
    background-color: #696969 !important;
    color: #F8FEDD !important;
    background-image: none !important;
    border: 0px none !important;
}
/*поиск*/

#searchcrmenuform,
.swiftdropdowninput,
.swiftdropdownitemtext,
.swiftdropdown {
    color: #F8FEDD!important;
    background-color: #222222;
    border: 0px solid #696969
}
#searchcrmenuform:hover {
    color: #F8FEDD!important;
    background-color: #696969!important;
    border: 0px solid #696969
}
textarea,
input[type="text"],
input[type="password"],
input[type="datetime"],
input[type="datetime-local"],
input[type="date"],
input[type="month"],
input[type="time"],
input[type="week"],
input[type="number"],
input[type="email"],
input[type="url"],
input[type="search"],
input[type="tel"],
input[type="color"],
.uneditable-input {
    background-color: #696969!important;
    border: 1px solid #222222;
    box-shadow: 0px 1px 1px rgba(0, 0, 0, 0.075) inset;
    transition: border 0.2s linear 0s, box-shadow 0.2s linear 0s;
    border-radius: 0px;
}
.swiftdropdownitem {
    color: #F8FEDD!important;
    background-color: #222222 !important;
    background-image: none!important;
    border-bottom: 1px solid #696969!important;
    border: 1px solid #696969!important;
}
.swiftdropdownitemimage > img:nth-child(1) {
    height: 0px;
    width: 0px;
    background-color: #222222 !important;
    background-image: none !important;
    color: #F8FEDD!important;
}
.swiftdropdownitem .swiftdropdownitemimage {
    border-right: 0px !important;
}
/*текст вкладок*/

.ui-state-default a,
.ui-state-default a:link,
.ui-state-default a:visited {
    color: rgba(0, 0, 0, 1);
    text-decoration: none;
    outline: medium none;
}
/*боковая панель, выпелена*/

.staffnavbarclickable {
    cursor: auto;
    background: linear-gradient(#222222 100%, #222222 100%) !important;
    width: 1px;
}
/*списки, фильтры*/

#ticketfiltermenu > li:nth-child(n+1) > div:nth-child(n+1),
#replycontents,
#reply_departmentid,
#selectreplyownerstaffid,
#selectreplytickettypeid,
#selectreplyticketstatusid,
#selectreplyfrom,
#reply_ticketpriorityid,
