css:
@-moz-document url-prefix("https://www.reddit.com/r/wicked_edge/") {
/* latin-ext */

@font-face {
    font-family: 'Open Sans';
    font-style: normal;
    font-weight: 600;
    src: local('Open Sans Semibold'), local('OpenSans-Semibold'), url(https://fonts.gstatic.com/s/opensans/v13/MTP_ySUJH_bn48VBG8sNSj0LW-43aMEzIO6XUTLjad8.woff2) format('woff2');
    unicode-range: U+0100-024F, U+1E00-1EFF, U+20A0-20AB, U+20AD-20CF, U+2C60-2C7F, U+A720-A7FF;
}
/* latin */

@font-face {
    font-family: 'Open Sans';
    font-style: normal;
    font-weight: 600;
    src: local('Open Sans Semibold'), local('OpenSans-Semibold'), url(https://fonts.gstatic.com/s/opensans/v13/MTP_ySUJH_bn48VBG8sNSugdm0LZdjqr5-oayXSOefg.woff2) format('woff2');
    unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
}
body {
    background-color: #fff;
    margin-top: 85px;
}
#siteTable_organic {
    display: none!important;
}
#sr-header-area {
    font-size: 120%;
    font-family: 'Open Sans'!important;
    background-color: #2f364a;
    /*background-color: #8da83f;*/
}
#header-bottom-left {
    background-image: url("http://i.imgur.com/s1i8dNY.png");
    background-color: #fff;
    padding-bottom: 4px;
    font-family: 'Open Sans'!important;
    font-size: 135%;
    box-shadow: 0 3px 3px rgba(0, 0, 0, 0.12), 0 1px 2px rgba(0, 0, 0, 0.24);
}
#header-bottom-right {
    background-color: rgba(255, 255, 255, 0.3);
    line-height: 12px;
    border-top-left-radius: 0px;
    font-size: 135%;
    font-family: 'Open Sans'!important;
}
#header {
    border-bottom: 1px solid #dadfea;
    /*position: relative;*/
    
    background-color: #cee3f8;
    z-index: 99;
    top: 0;
    height: 66px;
    width: 100%;
    position: fixed;
}
.dropdown.srdrop .selected {
    color: white;
}
.sr-bar a {
    color: white;
}
#sr-header-area .selected a {
    color: #f39c12;
}
.pagename a {
    color: #8ca4cc;
    font-family: 'Open Sans'!important;
    font-size: 115%;
}
.tabmenu li a {
    padding: 2px 6px 0 6px;
    background-color: rgba(255, 255, 255, 0.3);
}
a {
    text-decoration: none;
    color: #8ca4cc;
}
a:hover {
    color: #f39c12;
}
.tabmenu li.selected a {
    color: #8ca4cc;
    background-color: rgba(255, 255, 255, 0.3);
    border: 0px solid #5f99cf;
    border-bottom: 4px solid #f39c12;
    z-index: 100;
}
#header-img-a {
    background-color: white;
}
.thing.odd {
    border: 1px solid #dadfea;
    background: #fbfcff;
    overflow: hidden;
    margin-right: 320px;
    margin-left: 0px;
    margin-top: 0px;
    padding-top: 20px;
    padding-bottom: 20px;
}
.thing.even {
    border: 1px solid #dadfea;
    overflow: hidden;
    background: #fff;
    margin-right: 320px;
    margin-left: 0px;
    margin-top: -1px;
    margin-bottom: -1px;
    padding-top: 20px;
    padding-bottom: 20px;
}
.thing:hover {
    background-color: #f2f5ff;
}
p.title {
    font-family: 'Open Sans'!important;
}
.link {
    margin-bottom: 0px;
}
.link .title {
    color: #444c93;
    font-size: 135%;
}
.link .title:visited {
    color: #f39c12;
    font-size: 135%;
}
.link .thumbnail {
    border-radius: 6px;
    box-shadow: rgba(0, 0, 0, 0.2) 2px 2px 8px !important;
    margin-right: 12px !important;
    margin-top: 4px;
    margin-bottom: 4px;
    height: 50px !important;
    width: 100px !important;
}
.link .thumbnail img {
    border: 0 !important;
    box-shadow: 0 0 0 !important;
    height: 50px !important;
    width: 100px !important;
}
.flat-list,
.tagline {
    font-size: 140%;
    font-family: 'Open Sans'!important;
}
}
