css:
/* AGENT_SHEET */ @-moz-document domain("play.spotify.com") {

:root {     

    --wallpaper: url("/*[[yyy]]*/")  ; 
/*
Wallpaper, recommendations only, see line above =  --wallpaper: url("....."):

           http://subtlepatterns2015.subtlepatterns.netdna-cdn.com/patterns/fake_brick.png    
           http://www.designbolts.com/wp-content/uploads/2012/11/Free-Stressed-linen-patterns.jpg     (default choice)
           http://subtlepatterns2015.subtlepatterns.netdna-cdn.com/patterns/black_linen_v2.png 
           http://subtlepatterns2015.subtlepatterns.netdna-cdn.com/patterns/cartographer.png
           http://www.designbolts.com/wp-content/uploads/2012/11/Dark-Denim-Seamless-Pattern-for-clothing-websites.jpg
           http://subtlepatterns2015.subtlepatterns.netdna-cdn.com/patterns/vertical_cloth.png
           http://subtlepatterns2015.subtlepatterns.netdna-cdn.com/patterns/dark_dotted.png
           http://subtlepatterns2015.subtlepatterns.netdna-cdn.com/patterns/dark_matter.png
           http://subtlepatterns2015.subtlepatterns.netdna-cdn.com/patterns/rubber_grip.png
*/ 

    --image: url("/*[[img]]*/") ;
/*
Image, lower area of the right collumn, replacing the Spotify add. Default is empty (no image), but easy to pick one from the webb or your disc-drive, see line above   --image: url("...")
*/
    
    --marker: /*[[mar]]*/ ;
    --playCol: /*[[pla]]*/ ;
    --hoverCol: /*[[hov]]*/ ;
/*
Colors, make your picks in the color chart, or write specific hex-numbers. This is just recommendations, see lines above = --marker, --playCol, and --hoverCol ):

           turquoise  #47FFF8    
           orange     #FF9900    

           blue       #0099FF    (default choice on current playing + active progress bar, shuffle, repeat & volume bar), (default choice for all other selected options)
           red        #FF6666    (default choice when hover search in playlist)

           pink       #FFC1C1
           blueberry  #ADBBFF 
*/

    --imageadjust: /*[[adj]]*/ ;
    --line: #080808 ;}    

.main-menu.narrow-menu .logo {
   background: url( data:image/png;base64,) no-repeat center !important;  
   background-size: 67% !important;
   opacity: 0.75 !important;}  

.main-menu.narrow-menu .logo:hover {
   opacity: 1 !important;}  


* {
   text-decoration: none!important;
   text-shadow: 1px 1px 0px #000!important;
   border: none !important;}

*:hover, .active a {
   color: #fff !important;}

.ads-leaderboard, .jumbotron-account, #hpto-container, #footer, #footer-main, .tl-header.tl-header-hidden {
    display: none!important;}

.btn, .btn.btn-add, .btn-primary, .genreImage:hover span, .media-info .media-title span, .page-homepage .premium-option .button, .panel-group-account .panel-heading, .reboot .button-facebook, .reboot .button-highlight {
    border: none !important;}

.page-homepage .feature.premium .premium-container,.header-bar {
    background: transparent !important;}

a:hover:not(.btn), .btn-primary:not(.just-changed):not(.stay-added), .mo-title:hover .mo-title-border {
    border: none!important;}

.tl-cell.tl-explicit, .label {
    color: #999 !important;}

a, .tl-highlight, .tl-cell.tl-date-added, .tl-cell.tl-time, .tl-header, .tl-cell.tl-listen-count, .tl-cell.tl-number, .list-item-hl, .added, #tracklist > TABLE:last-child > TBODY > TR:nth-child(n) > TD:nth-child(2), #tracklist > TABLE:last-child > TBODY > TR:nth-child(n) > TD:nth-child(4) > A, #tracklist > TABLE:last-child > TBODY > TR:nth-child(n) > TD:nth-child(5) > A, #tracklist > TABLE:last-child > TBODY > TR:nth-child(n) > TD:nth-child(6), #tracklist > TABLE:last-child > TBODY > TR:nth-child(n) > TD:nth-child(4) {
    color: #bbb !important;}

.tl-row.added .tl-save .btn:before {
    color: #bbb !important; 
    font-size: .9em !important;}
   
.tl-row.added .tl-save:hover .btn:before {
    color: #bbb !important;
    background-color: transparent !important;}

html, body, .block, col-md-12, .dropdown-interior-menu, .dropdown-menu, .dropdown-menu>li>a, .get-started .article, .layout-signup .l-signup-body, .list-group-item.list-group-item-media,.list-group-account>.list-group-item, .main-menu.narrow-menu, .media-wrapper .media-info, .mo-image-wrapper, .page-download .download-instructions .step:hover p, .page-download .premium-promo-banner, .page-homepage section, .page-homepage .feature.premium .premium-container, .page-homepage .feature.premium .premium-container, .page-homepage .premium-details-list, .sign-up .content, .single-column, .story .story-item, .top-solutions, #article-list, #main > div.stacked > div.root { 
    background: var(--wallpaper), center bottom repeat!important;
    background-attachment: fixed !important;}

body, .new-payment-form .after-trial-disclaimer, .page-download .wrap, .page-homepage .premium-option , .header.playlist, HEADER {
    background: var(--wallpaper), center bottom repeat !important;
    background-attachment: fixed !important;}

/* Hover row playlist */

#tracklist > TABLE:last-child > TBODY > TR:nth-child(n):hover > TD:nth-child(3) > DIV >SPAN, #tracklist > TABLE:last-child > TBODY > TR:nth-child(n):hover > TD:nth-child(2), #tracklist > TABLE:last-child > TBODY > TR:nth-child(n):hover > TD:nth-child(4) > A, #tracklist > TABLE:last-child > TBODY > TR:nth-child(n):hover > TD:nth-child(5) > A, #tracklist > TABLE:last-child > TBODY > TR:nth-child(n):hover > TD:nth-child(6), #tracklist > TABLE:last-child > TBODY > TR:nth-child(n):hover > TD:nth-child(4), .list-group-item-wrapper:hover span, .page-homepage .lead, .page-homepage .premium-details-list li, .story:hover .text-meta, .selected .tl-artists a, .tl-row:hover .tl-highlight, .tl-row:hover .tl-artists a, .tl-row:hover .tl-albums a, .tl-row:hover .tl-cell, #community-bar:hover p, .search-banner-text p {
    color: var(--hoverCol)!important; 
    font-size: 1.1em !important;} 

#tracklist > TABLE:last-child > TBODY > TR:nth-child(n):hover, .tl-row:hover, .tracklist-playlist .tl-row:hover {
    background: linear-gradient(to bottom, rgba(95,95,97,.8) 2%, rgba(80,85,87,.4) 98%, rgba(20,20,20,1) 100%) !important; 
    box-shadow: 0px 1px 0px rgba(50,50,50,.2)!important;}

#tracklist > TABLE:nth-child(2) > TBODY > TR > TD:nth-child(n) > DIV > SPAN, TABLE:nth-child(2) > TBODY > TR > .list-row-artists A, #tracklist > TABLE:nth-child(2) > TBODY > TR > TD:nth-child(4), #tracklist > TABLE:nth-child(2) > TBODY > TR > TD:nth-child(5) > A, #tracklist > TABLE:nth-child(2) > TBODY > TR > TD:nth-child(6), .tl-row.playing .tl-cell, .tl-row.playing .tl-cell a:link, .tl-row.playing .tl-highlight, .tl-row.playing .tl-cell a:visited, .tl-row.paused .tl-cell, .tl-row.paused .tl-cell a:link, .tl-row.paused .tl-highlight, .tl-row.paused .tl-cell a:visited {
    color: var(--playCol) !important; 
    font-size: 1.1em !important;}

/* Now playing */

#tracklist > TABLE:nth-child(2) > TBODY > TR, .tl-row.playing, .tl-row.paused, .tracklist-playlist .tl-row.added.playing, .tracklist-playlist .tl-row.added.paused {
    background: linear-gradient(to bottom, rgba(95,95,97,.8) 2%, rgba(80,85,87,.4) 98%, rgba(20,20,20,1) 100%)  !important; 
    box-shadow: 0px 1px 0px rgba(50,50,50,.2)!important;}

#bar-click #position, #volume-bar #vol-position {
    box-shadow: 0 0 1px black!important; 
    background: radial-gradient(at top, var(--playCol), rgba(0,0,0,1))!important;
    border: 0 !important;}

#volume-bar {
    border:0px solid transparent !important;
    height: 6px !important;
    background: #080808 !important;}

#volume {
    background: transparent !important}

.navbar, SECTION {
    background: var(--wallpaper), center bottom repeat!important;
    background-attachment:fixed !important;}

#now-playing {
    min-width: 250px !important;
    box-shadow: -2px 0px 0px var(--line) !important;}

#art-area, #cover-art, #playback-controls {
    transform: scale(1.083) translate(-.25px, .25px) !important; 
    background: top center !important;}

.started #main {
    right: 253px !important;}

#track-name > A, #track-artist > A {
    color: #fff!important; font-size: 1.2em !important; }

#controls, #progress {
    top: 20px !important;}

#track-name-wrapper, #artist-name-wrapper {
    top: 10px !important;}

#desktop {
    visibility:hidden !important;} 
 
#now-playing-widgets {
    top: 500px !important;
    background-image: var(--image), var(--wallpaper) !important; 
    background-position: var(--imageadjust) bottom, center top !important;
    background-repeat: no-repeat, repeat, repeat-x !important;
    background-size:  cover, auto !important;}
 
.main-menu.narrow-menu a.active, .main-menu.narrow-menu a.active:hover {
    color: #fff !important;
    background: transparent !important;}

.main-menu.narrow-menu a {
    width: 90px !important;}

.main-menu.narrow-menu a#nav-profile .media {
    margin: 18px !important;}

.main-menu.narrow-menu {
    box-shadow: 2px 0px 0px var(--line) !important; }

.main-menu.narrow-menu a:hover {
    color: #fff!important;}

.etched-top, #now-playing-widgets {
    border-top: 2px solid var(--line) !important;}

.main-menu.narrow-menu a#nav-messages, .main-menu.narrow-menu a#nav-notifications {
    right: 20px !important;}

SPAN#position, SPAN#vol-position, #vol-bar-inner {
    background: var(--playCol) !important;}

#play-pause {
    background: linear-gradient(to bottom, rgba(255,255,255,0.15) 0%, rgba(0,0,0,0.5) 100%) !important;}

#previous:hover, #next:hover, #play-pause:hover {
    color: #fff !important;}

.h-controllers .button.button-add,.h-controllers .button.button-icon-only.spoticon-more-16, #biography > DIV:first-child > BUTTON, .list-group-item.list-group-item-social .button.button-add, .col-md-8.col-sm-12.current.playing > .button, .col-md-8.col-sm-12 > .button {
    border:none !important;
    background: rgba(0,0,0,.2) !important;}

BUTTON[data-bind="click: playPauseButtonClicked, visible: isPlayButtonVisible"][class="btn btn-small btn-icon btn-play"][type="button"], .btn.btn-small.btn-icon.btn-play, .button.button-icon-only.button-play , .btn.btn-icon.btn-large.btn-play.list-group-item-media-play, .added.btn.btn-add.btn-small, .button.button-primary.button-play, .added.button.button-add, .added.list-group-item.list-group-item-social .button.button-add {
    background: linear-gradient(to bottom, rgba(255,255,255,0.2) 0%, rgba(0,0,0,0.2) 100%) !important; 
    border:none !important;
    margin-top:2px !important;}

.btn.btn-large.btn-play.btn-icon.media-play, .button.button-icon-only.button-play.mo-play {
    background: linear-gradient(to bottom, rgba(255,255,255,0.8) 0%, rgba(0,0,0,0.5) 100%) !important;
    color:#FFF !important;  
    border:none !important;}

#bar-inner {
    border:1px solid transparent !important;
    background: var(--playCol) !important;}

#bar-outer {
    background: #080808 !important;}

.main-menu.narrow-menu a.active:after, .main-menu.narrow-menu a.selected:after {
    background-color: var(--marker) !important;}

.main-menu.narrow-menu a#nav-messages, .main-menu.narrow-menu a#nav-notifications {
    margin-left: 28px !important;
    width:25px !important;}

#alert-container { 
    background: rgba(0,0,0,.25) !important;
    border: 1px solid #2E2F33 !important; 
    border-radius: 4px !important;}

#nav-notifications.spoticon-notifications-32.active:before, #nav-messages.spoticon-inbox-32.active:before {
    color: var(--marker) !important;}

.nav > li.active > a:after {
    background-color: var(--marker) !important;}

.nav {
    border:none !important;}

.container .nav > li > a {
    min-width:100px !important; 
    text-align:center !important;}

.btn:hover:not(.just-changed):not(.stay-added) {
    color: #fff!important;
    border-color: var(--marker) !important;}

.btn-info {
    background: #1a1a1a!important;}

#btn-upgrade {
    color: var(--marker) !important;
    background: #111!important;}

.btn.btn-info:hover, #btn-upgrade:hover {
    background: #111!important;}

.list-group-item.list-group-item-media.active:before {
    color: #FFFFFF!important;
    background: transparent !important;}

.list-group-item.list-group-item-media {
    border-left: 3px solid transparent !important;}

.list-group-item.list-group-item-media.active {
    border-left: 3px solid var(--marker) !important;}

BUTTON[class="btn btn-link btn-icon spoticon-skip-back-24"], BUTTON[class="btn btn-link btn-icon spoticon-skip-forward-24"], #volume-show, #track-current, #track-length {
    color: #bbb !important;}

BUTTON[title="Open Queue app"], BUTTON[title="Shuffle"], BUTTON[title="Repeat"], #volume-show {
    padding: 5px 8px 5px !important;
   color: #bbb !important;}

BUTTON[title="Queue"][class="active"], BUTTON[title="Shuffle"][class="active"], BUTTON[title="Repeat"][class="active"], #volume-show:hover, BUTTON[title="handle"][class="active"] {
    color: var(--playCol) !important;}

.tl-cell, .pf-nav.col-lg-3.col-md-4.col-sm-4.col-xs-6, .input-wrapper, .mo-wrapper, .mo-info-wrapper, .mo-info.meta, .mo-info.subtitle, .col-xs-6.col-sm-4.col-md-3.col-lg-2.image-grid {
    background: none !important;}

.overlay, .info, .pf-info-container {
    background: rgba(0,0,0,.3) !important;}

.mo-info.description {
    background: rgba(0,0,0,0) !important;}

#tracklist TD, TD.list-row:hover {
    background: transparent !important;}

#tracklist > TABLE:nth-child(n) > TBODY > TR > TD:last-child > DIV > BUTTON:nth-child(n) {
    background: transparent !important;}

#tracklist > TABLE:nth-child(2) > TBODY > TR > TD:nth-child(2),  #tracklist TR.added:hover TD[data-field="number"][class="list-row-number"], #tracklist > TABLE:last-child > TBODY > TR:nth-child(n):hover > TD:nth-child(6), #tracklist > TABLE:nth-child(2) > TBODY >  TR:nth-child(n):hover  > TD:nth-child(6), #tracklist > TABLE:last-child > TBODY > TR:nth-child(n):hover > TD:nth-child(2) {
    color: transparent !important;
    text-shadow: none !important;}

#menu > UL, .dropdown-interior-menu.icon-menu {
    border-radius: 1px !important;
    background: rgba(20,20,20,.5) !important;
    border: 1px solid #222!important; 
    border-bottom: 2px solid #222!important;}

#modal-notification-area > DIV > P {
    text-shadow: none !important; 
    color:#000 !important;}

#notification-area /* > DIV > P > STRONG, #notification-area > DIV > P */ , 
A[onclick="return false;"][href="https://play.spotify.com/?utm_source=web-player&utm_campaign=bookmark"] {

    display:none !important;

    text-shadow: none !important; 
    color:#fff !important;}

#section-settings .sp-toggle.active .handle , .added.btn.btn-add.btn-icon.btn-large.btn-link.mo-add.active {
    color: var(--marker) !important;}

#section-settings #lang-select select {
    border: 1px solid #333!important;}

.btn.btn-icon.btn-link.btn-add.list-track-plus {
    transform: scale(.6,.6) !important;}

#nav-profile > SPAN > DIV:first-child > DIV > DIV:last-child, #main > HEADER > SECTION > DIV:first-child > DIV > DIV > DIV:nth-child(3), #account-info-pic > DIV > DIV { 
    box-shadow: 0 0 1px rgba(30,30,30,1) !important; 
    background: radial-gradient(at left top, var(--marker), rgba(0,0,0,1))!important;}

#main > HEADER > SECTION > DIV:first-child > DIV > DIV > svg > text:nth-child(5), .ellipsis {
    display:none !important;}

.spoticon-search-16:before, .anonymous-div, .spoticon-x-16.cross {
    color: #fff !important;}

.form-control.focus{ 
    background-color: rgba(194,203,203,.06) !important;
    border-radius: 2px !important;
    border: 1px solid rgba(220,220,220,.06) !important; 
    border-top: 1px solid rgba(255,255,255,.1) !important;
    box-shadow: 1px 1px 5px rgba(0,0,0,.6) !important;}

.selectable {
    background: transparent !important;}

.selectable a:hover {
    background-color: rgba(194,203,203,.06) !important;
    box-shadow: 1px 1px 5px rgba(0,0,0,.6) !important;}

#tracklist > DIV:first-child > DIV, #tracklist > TABLE:nth-child(2) > THEAD > TR, #tracklist > DIV:nth-child(3) > DIV, #tracklist > TABLE:last-child > THEAD > TR {
    color: #bbb !important;}

/*[[x-scrthumb-x]]*/
}

style_setting_options.value:
scrollbar [orient="vertical"] thumb, scrollbar[orient="horizontal"] thumb {
    -moz-appearance: none !important;
    opacity: 0.5 !important;}

scrollbar[orient="vertical"]:hover thumb, scrollbar[orient="horizontal"]:hover thumb {
    -moz-appearance: none !important;
    opacity: .75 !important;}

scrollbar[orient="vertical"]:active thumb, scrollbar[orient="horizontal"]:active thumb {
     -moz-appearance: none !important;
     opacity: 1 !important;}

scrollbarbutton[sbattr="scrollbar-up-top"], scrollbarbutton[sbattr="scrollbar-down-top"], scrollbarbutton[sbattr="scrollbar-up-bottom"], scrollbarbutton[sbattr="scrollbar-down-bottom"] {
    -moz-appearance: none !important;
    display: none !important;}

select scrollbarbutton[sbattr="scrollbar-up-top"], select scrollbarbutton[sbattr="scrollbar-down-top"], select scrollbarbutton[sbattr="scrollbar-up-bottom"], select scrollbarbutton[sbattr="scrollbar-down-bottom"] {
    -moz-appearance: scalethumb-vertical !important;
    display: block !important;
    -moz-margin-start: 5px !important;}

scrollbar {
    -moz-appearance: none !important;
    background: none !important;
    -moz-box-align: start !important;
    position: relative !important;
    z-index: 1999999999 !important;}

richlistbox scrollbarbutton[sbattr="scrollbar-up-top"], richlistbox scrollbarbutton[sbattr="scrollbar-down-top"], richlistbox scrollbarbutton[sbattr="scrollbar-up-bottom"], richlistbox scrollbarbutton[sbattr="scrollbar-down-bottom"] {
    -moz-appearance: separator !important;
    display: block!important;
    -moz-margin-start: -10000px !important;}

richlistbox scrollbar[orient="vertical"] {
    -moz-margin-start: 0px !important;
    width: 7px !important;}

scrollbar[orient="vertical"] {
    -moz-margin-start: -7px !important;}

scrollbar[orient="vertical"] thumb {
    max-width: 7px !important;
    min-width: 7px !important;
    min-height: 80px !important;}

scrollbar[orient="horizontal"] thumb {
    max-height: 7px !important;
    min-height: 7px !important;
    min-width: 80px !important;}
