css:
@-moz-document domain("dishots.com") {
/* 
	Created: Rave
	Profile: http://dishots.com/u/Rave
	Date: 21.06.2016
	Update: 22.06.2016
	Website: http://dcorearts.com
*/


/* background */
body {
    margin: 0px;
    color: #363636;
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif !important;
    font-size: 12px;
    background: #333333 !important;
}

.content_bg {
    background: #333333 !important;
    color: #a0a0a0 !important;
}

/* Header */

#header {
    background: #2e2e2e !important;
    height: 62px;
    color: #a0a1ab;
    padding: 0px 27px;
}

.header_fixed {
    box-shadow: none !important;
}

.drop_menu {
    color: #9b9b9b !important;
    background: rgb(51, 51, 51);
    box-shadow: 0px 0px 15px rgba(12, 12, 12, 0.81) !important;
    -moz-border-radius: 0 !important;
    -webkit-border-radius: 0 !important;
    -khtml-border-radius: 0 !important;
    border-radius: 0 !important;
}

.drop_menu:before {
    border-bottom: 10px solid rgb(51, 51, 51) !important;
}

.drop_menu a span {
    color: #9c9c9c !important;
}

.drop_menu a:hover {
    color: #EF4040 !important;
}

#load a {
    -moz-transform: rotate(-180deg) !important;
    -ms-transform: rotate(-180deg) !important;
    -webkit-transform: rotate(-180deg) !important;
    -o-transform: rotate(-180deg) !important;
    transform: rotate(-180deg) !important;
}

.checkbox:checked + label:before {
        background: #ef4040;
}

.checkbox:not(checked) + label:before {
        background: #292929;
}

.checkbox:not(checked) + label {
    color: #989898 !important;
}


#filters, #dialogues, #search_cat {
    background: #333333;
    box-shadow: 0px 5px 10px rgba(29, 29, 29, 0.24);
    text-align: center;
    font-size: 13px !important;
}

#filters a:hover {
    color: #ef4040 !important;
    padding-bottom: 19px !important;
    border-bottom: 2px solid #ef4040 !important;
}

#filters a img {
    display: none;
}

#filter_active {
    color: #ef4040 !important;
    padding-bottom: 19px !important;
    border-bottom: 2px solid #ef4040 !important;
}

.filter_p {
    background: rgb(64, 64, 64) !important;
}

.filter_row_up, .filter_row_down {
    border: 1px solid rgb(64, 64, 64) !important;
}

.filter_row_up {
    background: url(http://i.imgur.com/fWteFLU.png) 50% 50% no-repeat !important;
}

.filter_row_down {
    background: url(http://i.imgur.com/Ga12bdY.png) 50% 50% no-repeat !important;
}

.hide:checked + label + div {
    background: #2e2e2e !important;
}

#search input, #search select {
    color: #fff !important;
    background: #292929 !important;
}

#search button {
    background-color: transparent !important;
    position: absolute !important;
    margin-left: -40px !important;
    margin-top: 3px;
}

/* menu */

#menu a {
    font-size: 14px !important;
    color: #9c9c9c !important;
    line-height: 59px !important;
    text-transform: none !important;
    border-bottom: none !important;
}

#menu a:hover {
    color: #ef4040 !important;
    border-bottom: 2px solid #ef4040 !important;
    background: none !important;
}

#tags_panel {
    background: #2e2e2e !important;
    color: #676767 !important;
    box-shadow: 0px 5px 10px rgba(29, 29, 29, 0.39);
}

#tags_padding:before {
    border-bottom: 8px solid #2e2e2e !important;
}

#tags_categories a:hover {
    color: #ef4040 !important;
    border-bottom: 2px solid #ef4040 !important;
    padding-bottom: 7px !important;
}

#tags_categories a {
    color: #888888;
}

#tags_categories span {
    background: none !important;
    width: 22px !important;
}

#tags_search input {
    border: 2px solid #333333 !important;
    color: #848484 !important;
}

#tags_search input:focus {
    border: 2px solid #ef4040 !important;
}

#tags_line {
    background: #3a3a3a !important;
}

/* Main shots grid */
#shot_left {
    border-right: none !important;
    background-color: #333;
}

#shot_view {
    background: #333 !important;
    border-bottom: none !important;
    -moz-border-radius: 0 !important;
    -webkit-border-radius: 0 !important;
    -khtml-border-radius: 0 !important;
    border-radius: 0 !important;
}

.shot {
    font-size: 13px;
    background: rgba(21, 21, 21, 0.24);
    text-align: left;
    -moz-border-radius: 0 !important;
    -webkit-border-radius: 0 !important;
    -khtml-border-radius: 0 !important;
    border-radius: 0 !important;
    box-shadow: none !important;
    color: #9c9c9c !important
}

.shot:hover {
	box-shadow: 0px 5px 50px rgb(29, 29, 29) !important;
}

.shot_stats {
    background: rgb(44, 44, 44);
    color: #fff;
    font-size: 13px !important;
    -moz-border-radius: 0 !important;
    -webkit-border-radius: 0 !important;
    -khtml-border-radius: 0 !important;
    border-radius: 0 !important;
}

.shot_name {
    font-size: 14px !important;
    line-height: 16px;
    margin-bottom: 2px;
}

.shot_name:hover {
    color: #ef4040 !important
}

.shot_img img {
    width: 100%;
    cursor: pointer;
     -moz-border-radius: 0 !important;
    -webkit-border-radius: 0 !important;
    -khtml-border-radius: 0 !important;
    border-radius: 0 !important;
}



#flipper #comments .view_more_button, #shots .view_more_button {
    background: #2c2c2c;
}

button {
    border: 2px solid #EF4040;
    color: #fff;
    background: #EF4040;
    font-size: 17px;
}

#sign_in a {
    color: #9c9c9c !important
}

#sign_in a:hover {
    background: none !important;
    color: #ef4040;
    border-bottom: 2px solid #ef4040 !important;
}

#weelky_sticker {

    background: #ef4040 !important;
    color: #ffffff !important;
    content: "Шот недели" !important;
    -moz-border-radius: 0 !important;
    -webkit-border-radius: 0 !important;
    -khtml-border-radius: 0 !important;
    border-radius: 0 !important;
}

#weelky_sticker:after {
    border-top: 6px solid #ef4040 !important;
}

/* User modal */
#uib_content {
    background-color: #333333 !important;
}

#user_info_block {
     padding: 0px;
}
   
#uib_title {
    color: #363636;
    font-size: 23px;
    padding-top: 10px;
}

#uib_standart {
    background-color: #333333 !important;
}

/* Scrollbar */ 

::-webkit-scrollbar {
    width: 6px;
}
::-webkit-scrollbar-thumb {
    background-color: #ef4040; 
    border-radius: 0;
}
::-webkit-scrollbar-thumb:hover {
    opacity: 0.7;
}
::-webkit-scrollbar-track {
    background-color: #363636;
}

/* Topics ---------------------------------- */

#topics .ui_info h3 a {
    color: #b9b9b9;
}

#topics .ui_info a {
    color: #ef4040;
}

.topic_tags a:hover {
    background: #ef4040 !important;
    color: #fff !important;
}
.topic_tags a {
    background: #383838 !important;
    color: #989898 !important;
}

.ui_info {
    color: #6f6f6f !important;
}

.user_item, .user_item_pr {
    border-bottom: 1px solid #3a3a3a !important;
}

.reply_info a {
    color: #ef4040;
}

#topics .view_more_button {
    background: #2c2c2c;
}

#topic_stats {
    border-bottom: 1px solid #464646 !important;
}

#topic_wrapper #tags {
    border-bottom: 1px solid #464646 !important;
}

#tags a {
    background: #383838 !important;
    color: #989898 !important;
}

#tags a:hover {
    background: #EF4040 !important;
    color: #fff !important;
}

.p_edit a:hover {
    color: #EF4040 !important;
}

.shot_stats_num a:hover {
    color: #EF4040 !important;
}

.vote {
    background: #383838 !important;
    -moz-border-radius: 0 !important;
    -webkit-border-radius: 0 !important;
    -khtml-border-radius: 0 !important;
    border-radius: 0 !important;
}

#topic_vote {
    border-bottom: 1px solid #464646 !important;
}

#topic_vote h3 {
    font-size: 15px !important;
    font-weight: 500 !important;
    background: #2e2e2e !important;
    padding: 12px !important;
    padding-bottom: 16px !important;
    -moz-border-radius: 0 !important;
    -webkit-border-radius: 0 !important;
    -khtml-border-radius: 0 !important;
    border-radius: 0 !important;
}

.question_item {
    padding-left: 10px;
    padding-right: 10px;
}


.vi_bar {
    margin-top: 15px !important;
    background: #3e3e3e !important;
}

#topic_title {
    border-bottom: 1px solid #464646 !important;
}

#st_subtitle a {
	color: #ef4040;
}

.shot_like:hover {
    opacity: 0.6 !important;
	background: url(http://i.imgur.com/hqTWd0p.png) left 1px no-repeat !important;
    color: #a0a0a0 !important;
}

/* User profile */

#profile_avatar .online, .widthout_branding #profile_avatar .online {
    border: 3px solid #2e2e2e !important;
}

#profile_avatar {
    background: #2e2e2e !important;
    box-shadow: none !important;
}

#last_visit {
    background: #7d7d7d !important;
}

#user_status {
	border: none !important;
    margin-left: -16px !important;
}

#status_corner {
	background: none !important;
}

#user_status a {
    color: #797979 !important;
}

#user_header {
    position: relative;
    background: #2e2e2e;
    box-shadow: 0px 0px 5px rgba(0,0,0,0.1);
}

#profile_name a {
    color: #ef4040;
}

#profile_rating {
    color: #848484 !important;
}

#profile_rating a {
    color: #848484 !important;
}

#user_menu {
    background: #2e2e2e;
    box-shadow: 0px 0px 5px rgba(0,0,0,0.1);
    color: #989898;
}

#user_menu a {
    border-bottom: 2px solid #2e2e2e !important;
}

#user_menu a:hover {
    border-bottom: 2px solid #ef4040 !important;
}

#user_menu {
     border-bottom: none;
}

#user_menu a span, #user_menu .content_wrapper > div span {
    font-size: 13px !important;
    color: #6d6d6d !important;
}

#user_menu .content_wrapper > div {
    border-bottom: none !important;
}

.review {
    color: #7b7b7b !important;
}

.ui_info h3 a {
    color: #989898 !important;
}

.right_block, .block {
    background: #2e2e2e !important;
    color: #808080 !important;
}

.subject_comment {
    color: #a0a0a0 !important;
}

#comments .event_header {
    background: #333333 !important;
    border-bottom: 1px solid #464646 !important;
}

#comments .view_more_button {
    background: #2c2c2c !important;
}

#add_comment_form button, #dialogue_answer button, .shot_stats button {
    background: #ef4040 !important;
    border: 1px solid #ef4040 !important;
}

.event_header {
    background: #333333 !important;
    -moz-border-radius: 0 !important;
    -webkit-border-radius: 0 !important;
    -khtml-border-radius: 0 !important;
    border-radius: 0 !important;
}

/* Footer */

#footer {
    border-top: 1px solid #383838 !important;
}

#lang {
    background: url(http://i.imgur.com/Ga12bdY.png) left 50% no-repeat !important;
    color: #a0a1ab !important;
}

/* Modal window */
/*.modalx-overlay {
   background: none !important; 
}*/     
.modal_window {
    box-shadow: 0px 5px 50px rgb(29, 29, 29) !important;
    background: #333333 !important;
    color: #989898;
    -moz-border-radius: 0 !important;
    -webkit-border-radius: 0 !important;
    -khtml-border-radius: 0 !important;
    border-radius: 0 !important;
}

#modal_header {
    background: #333333 !important;
    -moz-border-radius: 0 !important;
    -webkit-border-radius: 0 !important;
    -khtml-border-radius: 0 !important;
    border-radius: 0 !important;
}

#modal_button {
    background: #333333 !important;
    -moz-border-radius: 0 !important;
    -webkit-border-radius: 0 !important;
    -khtml-border-radius: 0 !important;
    border-radius: 0 !important;
}

#login_help {
    border-top: 1px solid #444444 !important;
}

#login_help a {
    color: #ef4040;

}

input, textarea {
    color: #989898 !important;
    background: none !important;
    border: 2px solid #444444 !important;
}

input:focus, textarea:focus {
    border: 2px solid #ef4040 !important;
}
}
