css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("shifti.org") {
p {
    color: #ccc;
    background-color: #222;
}
  .mw-body {
    background-color: #222 !important;
    border: 0px solid #a7d7f9 !important;
    box-shadow: -10px 0px 16px -6px rgba(0,0,0,0.68);
}
  body {
    background-color: #222 !important;
}
  li {
    color: #666;
}
  .mw-body-content {
    color: #666;
}
  div#mw-head {
    background-color: #222;
}
  div.vectorTabs span {
    background-color: #252525;
    background-image: none !important;
}
  div.vectorTabs span a {
    color: #777
}
  div#simpleSearch {
  border: solid 0px #aaa;
  color: #666;
  background-color: rgba(0,0,0,0);
  background-image: none;
  box-shadow: inset 0px -2px 18px -1px rgba(0,0,0,1);
   border-radius: 5px;
  }
  div#simpleSearch Input{
    color: #666;
    padding-left:4px;
  }
  div.vectorTabs {
    background-image:none;
  }
  #toc, .toc, .mw-warning, .toccolours {
    border: 0px solid #aaa;
    background-color: #222;
    background-image: linear-gradient(to bottom, #333 0%, #000000 100%);
    border-radius: 7px;
    box-shadow: 0px 4px 12px 0px rgba(0,0,0,1);
}
}
