css:
@-moz-document domain(forums.gameclub.ph) {
/*start*/

*, :after, :before {
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
}

a, a:link, a:visited {
    color: #cacaca;
    /* color: #cdd5e0; */
    
    text-decoration: none;
    -webkit-transition: color .1s;
    -o-transition: color .1s;
    transition: color .15s, box-shadow .15s .05s;
}

a:hover {
    color: #ce4848;
    -webkit-box-shadow: 0 1px 0 0 #ce4848;
    box-shadow: 0 1px 0 0 #ce4848;
    text-decoration: none;
}

body {
    margin: 0;
    font-family: -apple-system, "Helvetica Neue", Roboto, "Segoe UI", Helvetica, sans-serif;
    font-size: 1.3rem;
    line-height: 1.4;
    color: #969696;
}

td {
    font-family: inherit;
    color: inherit
}
/*cleanup*/

.windowbg, .windowbg2,.windowbg_m,.windowbg_m2,.catbg_disp,.catbg, .catbg2 {
    background: transparent!important;
    border: 0!important;
}

img[src*='http://forums.gameclub.ph/Themes/0906_crossfire/images/icons/folder_open.gif'], img[src='http://forums.gameclub.ph/Themes/0906_crossfire/images/find.gif'], td[style*='background-image: url(http://forums.gameclub.ph/Themes/0906_crossfire/images/leftback.gif)'], td[style*='background-image: url(http://forums.gameclub.ph/Themes/0906_crossfire/images/rightback.gif)'] {
    display: none;
}
table[cellpadding="3"] * {
    padding: 0!important;
}

table[cellspacing="3"] {
    border-spacing: 0px!important
}
div[style="background-color: #666666;"],.t1,.t2,.t3,.t4,.t5,table[style*="0906_crossfire/images/topbarmid.gif"] {
    background: transparent!important;
}.titlebg, tr.titlebg td, .titlebg a:link, .titlebg a:visited,.catbg_mess,.bordercolor,.tborder{
    background: transparent!important;
}

div[style="background-color: #666666;"]>table td {
    border: 0!important;
    vertical-align: middle;
}
/*forum general*/

td .smalltext {
    font-family: Segoe UI, Open Sans, Roboto, Helvetica Neue, Helvetica, Arial, sans-serif;
    font-size: 11px;
    letter-spacing: -0.02em;
}

div[style="background-color: #666666;"], #quickModForm[action*="quickmod;board"] {
    padding-left: 15px;
    padding-right: 15px;
}


/*board list*/

div[style="background-color: #666666;"]>table {
    background-color: hsla(0, 0%, 16%, 1)!important;
}

div[style="background-color: #666666;"]>br {
    display: none
}

div[style="background-color: #666666;"]>table td.windowbg>b:first-child a {
    font-weight: normal;
    font-size: 15px;
}

div[style="background-color: #666666;"]>table td {
    padding: 14px 0 16px 0!important;
    border-bottom: 1px solid #404040!important;
}

div[style="background-color: #666666;"]>table tr:last-child td {
    border: 0!important;
}

td[style*="/Themes/0906_crossfire"] + td td.windowbg[style*="border-bottom: solid 4px #666666;padding: 6px"] i {
    font-style: initial;
    padding-top: 1px;
    display: inline-block;
}

div[style="background-color: #666666;"]>table td:first-child {
    width: 60px;
    padding-left: 6px!important;
    text-align: left;
}

div[style="background-color: #666666;"]>table td:last-child {
    padding: 0px!important;
    line-height: 17px;
    width: 170px;
}

div[style="background-color: #666666;"]>table td:nth-child(2) {
    padding-left: 3px!important;
}

div[style="background-color: #666666;"]>table td:nth-child(3) {
    color: #cacaca;
    padding-left: 18px!important;
    padding-right: 9px!important
}

div[style="background-color: #666666;"]>table td:nth-child(4) {
    color: #cacaca;
    padding-left: 9px!important;
    padding-right: 18px!important
}

div[style="background-color: #666666;"]>table td:nth-child(3):after {
    content: 'posts';
    display: block;
    color: #969696;
    font-size: 12px
}

div[style="background-color: #666666;"]>table td:nth-child(4):after {
    content: 'posts';
    display: block;
    color: #969696;
    font-size: 12px
}

img[src*="0906_crossfire/images/on.gif"], img[src*="0906_crossfire/images/on2.gif"] {
    background: url(http://i.imgur.com/xg6HmUU.png);
    width: 40px;
    height: 40px;
    background-size: 40px 40px;
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    padding-left: 40px;
    margin-top: 1px;
}

img[src*="0906_crossfire/images/off.gif"] {
    background: url(http://i.imgur.com/fQHo3Go.png);
    width: 40px;
    height: 40px;
    background-size: 40px 40px;
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    padding-left: 40px;
    margin-top: 1px;
}

/*nav*/

div.nav[style] {
    font-size: 0!important;
    margin: 0;
    color: #777;
    line-height: 40px;
    height: 40px;
    cursor: default;
    padding: 0 15px!important;
    background: hsla(0, 0%, 10%, 1);
}

div.nav[style] b a {
    font-size: 13px;
    font-weight: normal;
    color: #b7b7b7;
    -moz-box-shadow: 0 0;
    -webkit-box-shadow: 0 0;
    box-shadow: 0 0;
}

td[style*="/Themes/0906_crossfire"] + td .nav b:first-of-type {
    display: none;
}

div.nav[style] b a:before {
    font-size: 16px;
    content: "";
    margin: 0 4px;
    position: relative;
    top: 0;
    vertical-align: text-bottom;
    display: inline-block;
    height: 16px;
    width: 16px;
    background-image: url(http://i.imgur.com/T8uxSnt.png);
    background-size: 16px 16px;
}

div.nav[style] b:nth-of-type(2) a:before {
    font-size: 16px;
    content: "";
    margin: 0 6px 0 0;
    background-image: url(http://i.imgur.com/rSlOEBl.png);
    opacity: .7
}

/*###################################################################*/

td[style="padding: 4px 6px 4px 6px;"]{}
#quickModForm {
    /* border: 1px solid #900; */
}
#quickModForm[action*="quickmod;board"] {
    /* border: 1px solid #090; */
}
/**/
#quickModForm[action*="quickmod;board"] .titlebg{display:none}

#quickModForm[action*="quickmod;board"]:before {content: 'Topics';display: block;line-height: inherit;margin-top: 0;background-color: hsla(0,0%,26%,1);padding: 10px 15px;overflow: hidden;border-radius: 2px;position: relative;font-size: inherit;z-index: 1;color: #cecece;margin-top: 5px;}

div#wrap {
    background-color: #292929!important;}

#quickModForm[action*="quickmod;board"] td[valign="middle"]{padding: 12px 0!important;border-bottom: 1px solid #404040!important;}
#quickModForm[action*="quickmod;board"] td[valign="middle"]:first-child{width: 50px;}
#quickModForm[action*="quickmod;board"] td[valign="middle"]:nth-child(2){display:none}

#quickModForm[action*="quickmod;board"] td[valign="middle"]:nth-child(3){
    display: block;
    padding-bottom: 1px!important;
    border: 0!important;
}
#quickModForm[action*="quickmod;board"] td[valign="middle"]:nth-child(4){
    display: block;
    padding-top: 0!important;
    font-size: 11px;
    width: 100%;
}
#quickModForm[action*="quickmod;board"] td[valign="middle"]:nth-child(4):before{content:'Posted by '}


img[id*="lockicon"] {
    float: left;
    background: url(http://i.imgur.com/K8SCMBH.png);
    width: 16px;
    height: 16px;
    background-size: 16px 15px;
    background-position-y: 1px;
    background-repeat: no-repeat;
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    padding-left: 16px;
    opacity: 0.6;
    margin-right: 3px!important;
}

img[src*="post.gif"] {
    background: url(http://i.imgur.com/3UkrhoJ.png);
    width: 20px;
    height: 20px;
    background-size: 20px 20px;
    background-repeat: no-repeat;
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    padding-left: 20px;
    opacity: 0.6;
    /* margin-right: 3px!important; */
}

img[src*="sticky"] {
    background: url(http://i.imgur.com/Mgftjah.png);
    width: 20px;
    height: 20px;
    background-size: 20px 20px;
    background-repeat: no-repeat;
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    padding-left: 20px;
    opacity: 0.6;
    /* margin-right: 3px!important; */
}
}
