css:
@-moz-document regexp("http://www.zhihu.com/question/.*/answer/.*"), regexp("https://www.zhihu.com/question/.*/answer/.*") {
/* 时间上方分割线颜色不一致修改这里 */
.goog-scrollfloater{
    border-top: 1px solid #eee;
    margin-top:20px;
}

.down,.zm-list-avatar,.zu-top,.zm-side-section-inner,.toggle-comment,.js-thank,.js-collect,.js-report,.feed-item,.zm-tag-editor-labels,.zh-answers-title,.more-awesome,.content,.btn-backtotop,._CommentBox_root_G_1m,.goog-menu-button-caption,.zg-bull,.report{
    display:none!important;
}
.zu-main-content{
    Position:absolute;
    Top:20px;
    left:10px;
    width:120%;
}
.meta-item{
    display:none!important;
}
.answer-date-link{
    display:inline!important;
}
#zh-question-title>.zm-item-title {
    font-size: 2em;
}
body {
    font-family: 'Helvetica Neue',Helvetica,Arial,sans-serif;
    font-size: 1em;
}

.zm-votebar{
    border-top: 0px solid purple;
}
.count{
	font-size:0.8em;
}





a.QuestionMainAction,div.QuestionHeader-footer-inner,div.AppHeader-inner,div.CornerButtons,div.MoreAnswers,div.AnswerInfo-favlists,div.Card[data-za-module='RelatedQuestions'],div.Card[data-za-module='RelatedLives'],footer.Footer{
	display:none;
}


.RichText{
    white-space:normal!important;
}

.App-main .Question-main .Question-sideColumn {
  opacity: 1!important; }
  .App-main .Question-main .Question-sideColumn:hover {
    opacity: 1!important; }
  .App-main .Question-main .Question-sideColumn .Card.Banner {
    opacity: 1!important; }
    .App-main .Question-main .Question-sideColumn .Card.Banner:hover {
      opacity: 1!important; }


.RichText p {
    margin-bottom: .1em!important;
}

div.ContentItem-actions{
    display:none;
}


}
