css:
@-moz-document url-prefix("http://www.yourworldoftext.com/") {
    #yourworld {
    color: #ffffff;
}
div.world-container.writability-ADMIN .tilecont {
    background-color: #616161;
}
div.world-container.writability-MEMBERS .tilecont {
    background-color: #424242;
}
div.world-container.writability-PUBLIC .tilecont,
div.world-container .tilecont.protected-public {
    background-color: #212121;
}
div.world-container .tilecont.protected-admin {
    background-color: #616161;
}
div.world-container .tilecont.protected-members {
    background-color: #424242;
}
div.world-container .active-cursor,
div.world-container .active-guest-cursor {
    background-color: #ffffff !important;
}
a.urlLink,
.coordLink,
.simplemodal-closelink {
    color: #ffffff;
}
.menu {
    color: #ffffff;
    background-color: #424242;
}
#menu.hover {
    color: #ffffff;
    background: #424242;
    border-left: 0px solid #424242;
}
#nav {
    background: #424242;
    color: #ffffff;
    border-left: 1px solid #424242;
    border-bottom: 1px solid #424242;
}
#nav li {
    border-top: 1px solid #424242;
}
#nav li.hover {
    background-color: #424242;
}
#simplemodal-container {
    color: #ffffff;
    background-color: #424242;
    border: 6px solid #424242;
    padding: 12px;
}
#coords {
    color: #ffffff;
    background: #424242;
}
td:hover {
    background-color: #ffffff;
}
input[type="text"] {
}
input:focus {
    outline: none;
}
#simplemodal-overlay {
    background-color: #212121;
    opacity: 1 !important;
}
#loading {
    color: #ffffff;
    text-align: center;
}
body {
    background: #212121;
}
}
